func runScrape(cmd *cobra.Command, _ []string) error {
	var cfg scrapeConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return errors.Wrap(err, "failed to unmarshal config")
	}

	logger := log.New(
		log.WithService(viper.GetString("app.name")),
		log.WithEncoding(viper.GetString("logging.encoding")),
		log.WithDebugEnabled(viper.GetBool("logging.debug")),
	)
	log.SetLogger(logger)

	ctx := cmd.Context()

	// Make connections
	resolver.Register(dnssrvresolver.New())

	// Tracing
	tp, err := xotel.NewTracerProvider(cmd.Context(), xotel.TracerProviderConfig{
		ServiceName: cfg.App.Name,
		Endpoint:    viper.GetString("otel.tracer.endpoint"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create tracer provider")
	}

	otel.SetTracerProvider(tp)
	otel.SetTextMapPropagator(propagation.NewCompositeTextMapPropagator(propagation.TraceContext{}, propagation.Baggage{}))
	xotel.SetGlobalTracer(tp.Tracer("go.impalastudios.com/otel"))

	mp, err := xotel.NewMeterProvider(cmd.Context(), xotel.MeterProviderConfig{
		ServiceName: cfg.App.Name,
		Endpoint:    viper.GetString("otel.meter.endpoint"),
		Interval:    viper.GetDuration("otel.meter.interval"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create meter provider")
	}

	otel.SetMeterProvider(mp)
	xotel.SetGlobalMeter(mp.Meter("go.impalastudios.com/otel"))

	// Database
	db, err := xmongodb.Connect(ctx, cfg.Mongo)
	if err != nil {
		return errors.Wrap(err, "failed to connect to mongodb")
	}

	if err := xmongodb.Migrate(ctx, cfg.Mongo.DB, db.Client); err != nil {
		return errors.Wrap(err, "failed to migrate mongodb")
	}

	database := db.Database(cfg.Mongo.DB)
	flightsUpdateRepo := mongoadapter.NewFlightsUpdateRepository(database)

	// External provider
	client := radarboxtcp.New(cfg.RadarBox)
	if err := client.Connect(ctx); err != nil {
		return err
	}
	defer func() { _ = client.Close() }()

	// Service
	svc := core.NewFlightsIngestService(
		flightsUpdateRepo,
		client,
		cfg.Services.Scraper.BatchStoreInterval,
	)

	quit := make(chan os.Signal, 1)

	signal.Notify(quit, os.Interrupt)

	log.Info(ctx, "Starting to consume flight updates")

	ctx, cancel := context.WithCancel(ctx)
	defer cancel()

	go func() {
		if err := svc.ConsumeFlightUpdates(ctx); err != nil {
			log.Error(ctx, "Failed to consume flight updates", zap.Error(err))
			cancel()
		}
	}()

	select {
	case <-ctx.Done():
		log.Info(ctx, "Context done, terminating the realtime live flights service")
	case <-quit:
		log.Info(ctx, "Received interrupt signal, terminating the realtime live flights service")
	}

	return nil
}

