func runDataProcessor(cmd *cobra.Command, _ []string) error {
	ctx := cmd.Context()

	// Config
	var cfg dataProcessorConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return errors.Wrap(err, "failed to unmarshal config")
	}

	awsCfg, err := xaws.LoadAWSConfig(ctx, &cfg.AWS)
	if err != nil {
		log.Error(ctx, "Failed to load AWS config", zap.Error(err))
	}

	quit := make(chan os.Signal, 1)
	defer close(quit)

	signal.Notify(quit, os.Interrupt)

	// Logging
	logger := log.New(
		log.WithService(cfg.App.Name),
		log.WithEncoding(cfg.Logging.Encoding),
		log.WithDebugEnabled(cfg.Logging.Debug),
	)
	log.SetLogger(logger)

	// DNS resolver
	resolver.Register(dnssrvresolver.New())

	// Tracing
	tp, err := xotel.NewTracerProvider(cmd.Context(), xotel.TracerProviderConfig{
		ServiceName: cfg.App.Name,
		Endpoint:    viper.GetString("otel.tracer.endpoint"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create tracer provider")
	}

	otel.SetTracerProvider(tp)
	otel.SetTextMapPropagator(propagation.NewCompositeTextMapPropagator(propagation.TraceContext{}, propagation.Baggage{}))
	xotel.SetGlobalTracer(tp.Tracer("go.impalastudios.com/otel"))

	mp, err := xotel.NewMeterProvider(cmd.Context(), xotel.MeterProviderConfig{
		ServiceName: cfg.App.Name,
		Endpoint:    viper.GetString("otel.meter.endpoint"),
		Interval:    viper.GetDuration("otel.meter.interval"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create meter provider")
	}

	otel.SetMeterProvider(mp)
	xotel.SetGlobalMeter(mp.Meter("go.impalastudios.com/otel"))

	// Database
	db, err := xmongodb.Connect(ctx, cfg.Mongo)
	if err != nil {
		return errors.Wrap(err, "failed to connect to mongodb")
	}

	if err := xmongodb.Migrate(ctx, cfg.Mongo.DB, db.Client); err != nil {
		return errors.Wrap(err, "failed to migrate mongodb")
	}

	database := db.Database(cfg.Mongo.DB)

	// Cache
	cacheClient := cache.Connect(cmd.Context(), cfg.Cache)

	airlinesCache := adapterscache.NewAirlinesCache(cacheClient, cfg.Services.Airlines.Cache.TTL)
	airportsCache := adapterscache.NewAirportsCache(cacheClient, cfg.Services.Airports.Cache.TTL)

	// Services
	s3Client := xaws.NewS3ClientFromConfig(awsCfg, func(o *s3.Options) {
		o.UsePathStyle = cfg.AWS.S3.UsePathStyle
	})

	archiveFilesystem := filesystem.NewS3Filesystem(cfg.Stores.Archive.BucketName, s3Client)

	flightsArchiveRepo := mongoadapter.NewFlightsArchiveRepository(database)

	archiveSvc := core.NewFlightArchiveService(
		flightsArchiveRepo,
		archiveFilesystem,
		1000,
		10,
	)

	flightsEnrichRepo := mongoadapter.NewFlightsEnrichRepository(database)

	airlines := adapters.NewAirlinesGrpcClient(
		airlinesv0.NewAirlinesServiceClient(
			xgrpc.MustConnect(cfg.Services.Airlines.Endpoint),
		),
		airlinesCache,
	)

	airports := adapters.NewAirportsGrpcClient(
		airportsv0.NewAirportsServiceClient(
			xgrpc.MustConnect(cfg.Services.Airports.Endpoint),
		),
		airportsCache,
		25,
	)

	enrichSvc := core.NewFlightsEnrichService(
		flightsEnrichRepo,
		airlines,
		airports,
		1000,
		10,
	)

	log.Info(ctx, "Starting to process data")

	ctx, cancel := context.WithCancel(ctx)
	defer cancel()

	maxStepDuration := viper.GetDuration("max_duration")
	if maxStepDuration == 0 {
		maxStepDuration = 150 * time.Second
	}
	sleepDuration := viper.GetDuration("sleep_duration")
	if sleepDuration == 0 {
		sleepDuration = time.Minute
	}

	for {
		select {
		case <-ctx.Done():
			log.Info(ctx, "Context done, exiting processor loop")
			return nil
		default:
		}

		startArchive := time.Now()
		err := archiveSvc.Archive(ctx)
		if err != nil {
			log.Error(ctx, "Failed to archive flights", zap.Error(err))
		}

		archiveDur := time.Since(startArchive)
		if archiveDur > maxStepDuration {
			log.Warn(ctx, "Clean/archive step took longer than expected",
				zap.Duration("duration", archiveDur),
				zap.Duration("max_duration", maxStepDuration),
				zap.Error(err),
			)
		}

		startEnrich := time.Now()
		err = enrichSvc.Enrich(ctx)
		if err != nil {
			log.Error(ctx, "Failed to enrich live flights data", zap.Error(err))
		}

		enrichDur := time.Since(startEnrich)
		if enrichDur > maxStepDuration {
			log.Warn(ctx, "Enrich step took longer than expected",
				zap.Duration("duration", enrichDur),
				zap.Duration("max_duration", maxStepDuration),
				zap.Error(err),
			)
		}

		select {
		case <-ctx.Done():
			log.Info(ctx, "Context done, exiting processor loop")
			return nil
		case <-time.After(sleepDuration):
		}
	}
}

