	ctx := cmd.Context()

	// Config
	var cfg serveHttpConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return errors.Wrap(err, "failed to unmarshal db config")
	}

	awsCfg, err := xaws.LoadAWSConfig(ctx, &cfg.AWS)
	if err != nil {
		log.Error(ctx, "Failed to load AWS config", zap.Error(err))
	}

	quit := make(chan os.Signal, 1)
	defer close(quit)

	signal.Notify(quit, os.Interrupt)

	// Logging
	logger := log.New(
		log.WithService(cfg.App.Name),
		log.WithEncoding(cfg.Logging.Encoding),
		log.WithDebugEnabled(cfg.Logging.Debug),
	)
	log.SetLogger(logger)

	// DNS resolver
	resolver.Register(dnssrvresolver.New())

	// i18n
	if v, ok := binding.Validator.Engine().(*validator.Validate); ok {
		i18n.Register(v)
		customrules.Register(v)
	}

	// Tracing
	tp, err := xotel.NewTracerProvider(cmd.Context(), xotel.TracerProviderConfig{
		ServiceName: cfg.App.Name,
		Endpoint:    viper.GetString("otel.tracer.endpoint"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create tracer provider")
	}

	otel.SetTracerProvider(tp)
	otel.SetTextMapPropagator(propagation.NewCompositeTextMapPropagator(propagation.TraceContext{}, propagation.Baggage{}))
	xotel.SetGlobalTracer(tp.Tracer("go.impalastudios.com/otel"))

	mp, err := xotel.NewMeterProvider(cmd.Context(), xotel.MeterProviderConfig{
		ServiceName: cfg.App.Name,
		Endpoint:    viper.GetString("otel.meter.endpoint"),
		Interval:    viper.GetDuration("otel.meter.interval"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create meter provider")
	}

	otel.SetMeterProvider(mp)
	xotel.SetGlobalMeter(mp.Meter("go.impalastudios.com/otel"))

	// Database
	db, err := xmongodb.Connect(ctx, cfg.Mongo)
	if err != nil {
		return errors.Wrap(err, "failed to connect to mongodb")
	}

	database := db.Database(cfg.Mongo.DB)

	// Services
	flightsUpdateRepo := mongodb.NewFlightsUpdateRepository(database)

	s3Client := xaws.NewS3ClientFromConfig(awsCfg, func(o *s3.Options) {
		o.UsePathStyle = cfg.AWS.S3.UsePathStyle
	})

	svc := core.NewService(flightsUpdateRepo, filesystem.NewS3Filesystem(cfg.Stores.Archive.BucketName, s3Client))

	// Routing
	router := gin.New()
	router.Use(otelginmetric.Middleware(
		otelginmetric.WithRecordFilter(func(route string, _ *http.Request) bool {
			return route != "/__gw/health"
		}),
	))
	router.Use(requestid.New()...)
	router.Use(
		ginzap.GinzapWithConfig(logger, &ginzap.Config{
			TimeFormat: time.RFC3339,
			UTC:        true,
			SkipPaths:  []string{"/__gw/health"},
			Context: func(c *gin.Context) []zapcore.Field {
				return []zapcore.Field{log.RequestIDExtractor(c), zap.String("type", "access_log")}
			},
		}),
		ginzap.RecoveryWithZap(logger, true),
	)
	router.Use(otelgin.Middleware(
		cfg.App.Name,
		otelgin.WithTracerProvider(tp),
		otelgin.WithFilter(func(req *http.Request) bool {
			return slices.Contains([]string{"/__gw/health"}, req.URL.Path)
		}),
	))

	gw := router.Group("/__gw")

	handlersV0 := v0.New(svc)
	handlersV0.Register(gw)

	healthHandler := httphandlers.NewHealthHandler()
	healthHandler.Register(gw)

	srv := http.Server{
		Addr:    fmt.Sprintf(":%d", viper.GetInt("app.http.port")),
		Handler: router,
	}

	go func() {
		logger.Info("Server started. Ready to accept incoming requests...")
		if err := srv.ListenAndServe(); err != nil && !errors.Is(err, http.ErrServerClosed) {
			log.Error(context.TODO(), "Error occurred while listening.", zap.Error(err))
		}
	}()

	// Shutting down
	<-quit

	log.Info(context.TODO(), "Shutting down the REST server...")

	ctx, cancel := context.WithTimeout(context.Background(), 5*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		log.Error(cmd.Context(), "Error occurred while shutting down.", zap.Error(err))
	}

	if err := tp.Shutdown(ctx); err != nil {
		log.Error(cmd.Context(), "Error occurred while shutting down the tracer.", zap.Error(err))
	}

	if err := mp.Shutdown(ctx); err != nil {
		log.Error(cmd.Context(), "Error occurred while shutting down the meter.", zap.Error(err))
	}

	select {
	case <-ctx.Done():
		log.Info(context.TODO(), "REST server shutdown timed out.")
	default:
		log.Info(context.TODO(), "REST server shutdown gracefully.")
	}

	return nil
}

doc_head:
func runServeHttp(cmd *cobra.Command, _ []string) error {
	ctx := cmd.Context()
	// Config
	var cfg serveHttpConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return errors.Wrap(err, "failed to unmarshal db config")
	}
	awsCfg, err := xaws.LoadAWSConfig(ctx, &cfg.AWS)
	if err != nil {
		log.Error(ctx, "Failed to load AWS config", zap.Error(err))
	}
	quit := make(chan os.Signal, 1)
	defer close(quit)
	signal.Notify(quit, os.Interrupt)
	// Logging
	logger := log.New(
		log.WithService(cfg.App.Name),
		log.WithEncoding(cfg.Logging.Encoding),
		log.WithDebugEnabled(cfg.Logging.Debug),
	)
	log.SetLogger(logger)
	// DNS resolver
	resolver.Register(dnssrvresolver.New())
	// i18n
	if v, ok := binding.Validator.Engine().(*validator.Validate); ok {
		i18n.Register(v)
		customrules.Register(v)
	}
	// Tracing
	tp, err := xotel.NewTracerProvider(cmd.Context(), xotel.TracerProviderConfig{
		ServiceName: cfg.App.Name,
		Endpoint:    viper.GetString("otel.tracer.endpoint"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create tracer provider")
	}
	otel.SetTracerProvider(tp)

