func (s *FlightArchiveService) Archive(ctx context.Context) error {
	s.metrics.totalCalls++

	var totalArchived int

	for i := 0; i < s.maxArchiveCycles; i++ {
		flights, err := s.archives.GetArchivableFlights(ctx, s.archivePageSize)
		if err != nil {
			log.Error(ctx, "Failed to fetch archivable flights", zap.Error(err))
			return err
		}

		var archived, skipped, failed int

		for _, flight := range flights {
			if flight.FlightID == nil {
				skipped++
				continue
			}

			if err := s.filesystem.SaveLiveFlight(ctx, *flight.FlightID, flight); err != nil {
				failed++
				continue
			}

			archived++
		}

		if err := s.archives.DeleteFlights(ctx, flights); err != nil {
			log.Error(ctx, "Failed to delete archived flights", zap.Error(err))
			return err
		}

		s.metrics.totalArchived += archived
		s.metrics.totalSkipped += skipped
		s.metrics.totalFailed += failed
		s.metrics.totalDeleted += len(flights)

		totalArchived += archived

		if archived == 0 {
			break
		}
	}

	s.logMetricsIfNeeded(ctx)
	return nil
}

