// ConsumeFlightUpdates consumes flight updates from the realtime flight updates client
func (s *FlightsIngestService) ConsumeFlightUpdates(ctx context.Context) error {
	subscriber := make(chan LiveFlightUpdate)

	realtimeFlightDataStreamErr := make(chan error)

	go func() {
		if err := s.realtimeFlightUpdatesClient.Read(ctx, subscriber); err != nil {
			realtimeFlightDataStreamErr <- err
		}
	}()

	uniqueFlightUpdates := make(map[string]LiveFlightUpdate)
	ticker := time.NewTicker(s.batchStoreInterval)

	for {
		select {
		case flightupdate := <-subscriber:
			uniqueFlightUpdates[flightupdate.ID] = flightupdate
		case <-ticker.C:
			s.insertBatchFlightUpdates(ctx, uniqueFlightUpdates)
			clear(uniqueFlightUpdates)
		case <-ctx.Done():
			log.Info(ctx, "Shutting down the flight updates service...")
			return nil
		case err := <-realtimeFlightDataStreamErr:
			log.Error(ctx, "Realtime data stream raised an error unexpectedly", zap.Error(err))
			return err
		}
	}
}

