text:
func (s *FlightsIngestService) insertBatchFlightUpdates(ctx context.Context, uniqueUpdates map[string]LiveFlightUpdate) {
	if len(uniqueUpdates) == 0 {
		log.Debug(ctx, "There are no flight updates to store")
		return
	}

	flightUpdatesBatch := make([]*LiveFlightUpdate, 0, len(uniqueUpdates))

	for _, update := range uniqueUpdates {
		flightUpdatesBatch = append(flightUpdatesBatch, &update)
	}

	start := time.Now()
	err := s.flightUpdatesRepo.UpsertMany(ctx, flightUpdatesBatch)
	dur := time.Since(start)

	if err != nil {
		log.Error(ctx, "Failed to insert batch records of realtime flight updates",
			zap.Error(err),
			zap.Int("count", len(flightUpdatesBatch)),
		)

		return
	}

	s.metrics.totalBatches++
	s.metrics.totalUpdates += len(flightUpdatesBatch)
	s.metrics.totalDuration += dur

	s.logMetricsIfNeeded(ctx)
}

