func (s *FlightsIngestService) logMetricsIfNeeded(ctx context.Context) {
	now := time.Now()
	if now.Sub(s.metrics.lastReport) < 5*time.Minute {
		return
	}

	var avgBatchDur time.Duration
	var avgUpdatesPerBatch float64

	if s.metrics.totalBatches > 0 {
		avgBatchDur = s.metrics.totalDuration / time.Duration(s.metrics.totalBatches)
		avgUpdatesPerBatch = float64(s.metrics.totalUpdates) / float64(s.metrics.totalBatches)
	}

	log.Debug(ctx, "Ingest metrics report",
		zap.Int("batches", s.metrics.totalBatches),
		zap.Int("total_updates", s.metrics.totalUpdates),
		zap.Duration("avg_batch_duration", avgBatchDur),
		zap.Float64("avg_updates_per_batch", avgUpdatesPerBatch),
	)

	s.metrics = ingestMetrics{lastReport: now}
}

