func (s *FlightsEnrichService) Enrich(ctx context.Context) error {
	s.metrics.totalCalls++

	for i := 0; i < s.maxEnrichCycles; i++ {
		flights, err := s.enrichRepo.GetUnenrichedFlights(ctx, s.enrichPageSize)
		if err != nil {
			log.Error(ctx, "Failed to retrieve unenriched flights", zap.Error(err))
			s.metrics.totalFailed++
			s.logMetricsIfNeeded(ctx)
			return err
		}
		if len(flights) == 0 {
			break
		}

		var enriched []*EnrichedFlightData
		for _, flight := range flights {
			data := s.buildEnrichedFlightData(ctx, flight)
			if data != nil {
				enriched = append(enriched, data)
				s.metrics.totalEnriched++

				if data.FlightID == nil {
					s.metrics.missingFlightID++
				}
			} else {
				s.metrics.totalSkipped++
			}
		}

		if len(enriched) > 0 {
			if err := s.enrichRepo.EnrichFlights(ctx, enriched); err != nil {
				log.Error(ctx, "Failed to bulk enrich flights", zap.Error(err))
				s.metrics.totalFailed++
				s.logMetricsIfNeeded(ctx)
				return err
			}
		}
	}

	s.logMetricsIfNeeded(ctx)
	return nil
}

