func (s *FlightsEnrichService) buildEnrichedFlightData(ctx context.Context, flight *LiveFlight) *EnrichedFlightData {
	const defaultClassification = 5

	data := &EnrichedFlightData{
		LiveFlightID:   flight.ID,
		Classification: defaultClassification,
	}

	if flight.AirlineDesignator != "" {
		airline, err := s.airlines.GetByDesignator(ctx, flight.AirlineDesignator)
		if err != nil {
			s.metrics.airlineNotFound[flight.AirlineDesignator]++
		} else if airline != nil {
			data.Classification = int64(airline.Classification)
		}
	}

	var departureCode string
	if flight.DepartureAirportICAO != "" || flight.DepartureAirportIATA != "" {
		departure, err := s.airports.GetByICAOOrIATA(
			ctx,
			optionalString(flight.DepartureAirportICAO),
			optionalString(flight.DepartureAirportIATA),
		)
		if err != nil {
			key := flight.DepartureAirportICAO + "/" + flight.DepartureAirportIATA
			s.metrics.departureAirportNotFound[key]++
		} else if departure != nil {
			departureCode = departure.Designator
		}
	}

	var arrivalCode string
	if flight.ArrivalAirportICAO != "" || flight.ArrivalAirportIATA != "" {
		arrival, err := s.airports.GetByICAOOrIATA(
			ctx,
			optionalString(flight.ArrivalAirportICAO),
			optionalString(flight.ArrivalAirportIATA),
		)

		if err != nil {
			key := flight.ArrivalAirportICAO + "/" + flight.ArrivalAirportIATA
			s.metrics.arrivalAirportNotFound[key]++
		} else if arrival != nil {
			arrivalCode = arrival.Designator
		}
	}

	if !flight.DepartureTime.IsZero() && flight.FlightNumber != "" &&
		flight.AirlineDesignator != "" && departureCode != "" && arrivalCode != "" {

		//TODO: FlightID logic
		flightID := fmt.Sprintf(
			"%s_%s_%s_%s_%s",
			flight.DepartureTime.Format("20060102"),
			flight.AirlineDesignator,
			flight.FlightNumber,
			departureCode,
			arrivalCode,
		)
		data.FlightID = &flightID
	}

	return data
}

