func (s *FlightsEnrichService) logMetricsIfNeeded(ctx context.Context) {
	now := time.Now()
	if now.Sub(s.metrics.lastReport) < 5*time.Minute {
		return
	}

	log.Debug(ctx, "Flights enrich metrics (last 5 min)",
		zap.Int("calls", s.metrics.totalCalls),
		zap.Int("enriched", s.metrics.totalEnriched),
		zap.Int("failed", s.metrics.totalFailed),
		zap.Int("skipped", s.metrics.totalSkipped),
		zap.Int("missing_flight_id", s.metrics.missingFlightID),
	)

	type pair struct {
		key   string
		count int
	}
	explain := func(data map[string]int) (distinct int, top10 []string) {
		distinct = len(data)

		pairs := make([]pair, 0, distinct)
		for k, v := range data {
			pairs = append(pairs, pair{k, v})
		}

		sort.Slice(pairs, func(i, j int) bool {
			return pairs[i].count > pairs[j].count
		})

		n := 10
		if len(pairs) < n {
			n = len(pairs)
		}
		top10 = make([]string, 0, n)
		for i := 0; i < n; i++ {
			top10 = append(top10, fmt.Sprintf("%s(%d)", pairs[i].key, pairs[i].count))
		}
		return
	}

	aDistinct, aTop := explain(s.metrics.airlineNotFound)
	if aDistinct > 0 {
		log.Debug(ctx, "Airlines not found summary",
			zap.Int("distinct_airlines_not_found_count", aDistinct),
			zap.Strings("top_airlines_not_found", aTop),
		)
	}

	dDistinct, dTop := explain(s.metrics.departureAirportNotFound)
	if dDistinct > 0 {
		log.Debug(ctx, "Departure airports not found summary",
			zap.Int("distinct_departures_not_found_count", dDistinct),
			zap.Strings("top_departures_not_found", dTop),
		)
	}

	rDistinct, rTop := explain(s.metrics.arrivalAirportNotFound)
	if rDistinct > 0 {
		log.Debug(ctx, "Arrival airports not found summary",
			zap.Int("distinct_arrivals_not_found_count", rDistinct),
			zap.Strings("top_arrivals_not_found", rTop),
		)
	}

	s.metrics = enrichMetrics{
		airlineNotFound:          make(map[string]int),
		departureAirportNotFound: make(map[string]int),
		arrivalAirportNotFound:   make(map[string]int),
		lastReport:               now,
	}
}

