func (c *AirportsGrpcClient) GetByICAOOrIATA(ctx context.Context, icao, iata *string) (*core.Airport, error) {
	ctx, span := xotel.Tracer.Start(ctx, "AirportsGrpcClient:GetByICAOOrIATA", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	span.SetAttributes(
		attribute.String("icao", str(icao)),
		attribute.String("iata", str(iata)),
	)

	var icaoCode, iataCode string
	if icao != nil {
		icaoCode = strings.ToUpper(strings.TrimSpace(*icao))
	}
	if iata != nil {
		iataCode = strings.ToUpper(strings.TrimSpace(*iata))
	}
	compositeKey := fmt.Sprintf("%s:%s", icaoCode, iataCode)

	if a, err := c.cache.Get(ctx, compositeKey); err == nil {
		return &a, nil
	} else if !errors.Is(err, core.ErrCacheMiss) {
		span.RecordError(err)
		span.SetStatus(codes.Error, err.Error())

		log.Warn(ctx, "Cache failure", zap.Error(err))
	}

	var airportsList []*v0.Airport
	if icaoCode != "" {
		resp, err := c.client.Search(ctx, &v0.SearchAirportsRequest{Q: icaoCode, Limit: uint32(c.searchLimit)})
		if err != nil {
			span.RecordError(err)
			span.SetStatus(codes.Error, err.Error())

			log.Debug(ctx, "ICAO search failed", zap.Error(err))
		}
		airportsList = append(airportsList, resp.Result...)
	}
	if iataCode != "" {
		resp, err := c.client.Search(ctx, &v0.SearchAirportsRequest{Q: iataCode, Limit: uint32(c.searchLimit)})
		if err != nil {
			span.RecordError(err)
			span.SetStatus(codes.Error, err.Error())

			log.Debug(ctx, "ICAO search failed", zap.Error(err))
		}
		airportsList = append(airportsList, resp.Result...)
	}

	var bestBoth, bestIATA, bestICAO *core.Airport
	seen := make(map[string]struct{})

	for _, airportResult := range airportsList {
		if airportResult.Designator == nil {
			continue
		}
		a := &core.Airport{
			ID:             airportResult.Id,
			Designator:     *airportResult.Designator,
			IATA:           airportResult.Iata,
			ICAO:           airportResult.Icao,
			Classification: airportResult.Classification,
		}
		var dbICAO, dbIATA string
		if a.ICAO != nil {
			dbICAO = strings.ToUpper(strings.TrimSpace(*a.ICAO))
		}
		if a.IATA != nil {
			dbIATA = strings.ToUpper(strings.TrimSpace(*a.IATA))
		}
		key := fmt.Sprintf("%s:%s", dbICAO, dbIATA)
		if _, exists := seen[key]; exists {
			continue
		}
		seen[key] = struct{}{}
		_ = c.cache.Set(ctx, key, *a)

		bothMatch := icaoCode != "" && iataCode != "" && dbICAO == icaoCode && dbIATA == iataCode
		iataMatch := iataCode != "" && dbIATA == iataCode
		icaoMatch := icaoCode != "" && dbICAO == icaoCode

		if bothMatch {
			bestBoth = a
			break
		}
		if bestIATA == nil && iataMatch {
			bestIATA = a
		}
		if bestICAO == nil && icaoMatch {
			bestICAO = a
		}
	}

	if bestBoth != nil {
		return bestBoth, nil
	}
	if bestIATA != nil {
		return bestIATA, nil
	}
	if bestICAO != nil {
		return bestICAO, nil
	}
	return nil, nil
}

