func (c *AirlinesCache) Get(ctx context.Context, designator string) (core.Airline, error) {
	ctx, span := xotel.Tracer.Start(ctx, "AirlinesCache:Get", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	span.SetAttributes(attribute.String("designator", designator))

	key := fmt.Sprintf("airlines:%s", designator)

	var item airline
	err := c.client.Get(ctx, key, &item)
	if err != nil {
		span.RecordError(err)
		span.SetStatus(codes.Error, err.Error())

		if errors.Is(err, cache.ErrCacheMiss) {
			return core.Airline{}, core.ErrCacheMiss
		}
		return core.Airline{}, errors.Wrap(err, "failed to get airline from cache")
	}

	return item.toDomain(), nil
}

