func (c *AirlinesCache) Set(ctx context.Context, designator string, airline core.Airline) error {
	ctx, span := xotel.Tracer.Start(ctx, "AirlinesCache:Set", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	span.SetAttributes(
		attribute.String("designator", designator),
		attribute.String("cache_ttl", c.ttl.String()),
	)

	key := fmt.Sprintf("airlines:%s", designator)

	return c.client.Set(&cache.Item{
		Ctx:   ctx,
		Key:   key,
		Value: airlineFromDomain(airline),
		TTL:   c.ttl,
	})
}

