package main

import "go.impalastudios.com/aviation/schedules/cmd"

func main() {
	if err := cmd.RunRootCmd(); err != nil {
		panic(err)
	}
}

func consume(ctx context.Context, cfg *consumeConfig) error {
	log.SetLogger(
		log.New(
			log.WithService(viper.GetString("app.name")),
			log.WithEncoding(viper.GetString("logging.encoding")),
			log.WithDebugEnabled(viper.GetBool("logging.debug")),
		),
	)

	if err := pgsql.MigrateUp(&cfg.DB); err != nil {
		return errors.Wrap(err, "failed to migrate up")
	}

	db, err := pgsql.Connect(ctx, &cfg.DB)
	if err != nil {
		return errors.Wrap(err, "failed to connect to database")
	}

	awsCfg, err := xaws.GetConfig(ctx, &cfg.AWS)
	if err != nil {
		log.Error(ctx, "Failed to load AWS config", zap.Error(err))
	}

	queue := sqs.NewFromConfig(awsCfg)

	log.Debug(ctx, "Starting to consume messages", zap.String("queue_url", cfg.QueueURL))

	for {
		out, err := queue.ReceiveMessage(ctx, &sqs.ReceiveMessageInput{
			QueueUrl:            &cfg.QueueURL,
			MaxNumberOfMessages: 10,
			VisibilityTimeout:   60,
			WaitTimeSeconds:     20,
		})
		if err != nil {
			log.Error(ctx, "Failed to receive messages", zap.Error(err))
			continue
		}

		if len(out.Messages) == 0 {
			continue
		}

		log.Debug(ctx, "Received messages, starting to process", zap.Int("messages", len(out.Messages)))

		for _, msg := range out.Messages {
			if msg.Body == nil {
				log.Error(ctx, "Message body is nil", zap.Any("msg", msg))
				continue
			}

			var body flightStatusUpdateEventMsgBody
			if err := json.Unmarshal([]byte(*msg.Body), &body); err != nil {
				log.Error(ctx, "Failed to unmarshal message body", zap.Error(err))
				continue
			}
			event := body.Detail
			departureInfo := mergeDepartureInformation(event.Status.Old.Departure, event.Status.New.Departure)
			arrivalInfo := mergeArrivalInformation(event.Status.Old.Arrival, event.Status.New.Arrival)

			var (
				departureStatus     *string
				arrivalStatus       *string
				flightStatus        *string
				expectedDepartureAt *time.Time
				actualDepartureAt   *time.Time
				expectedArrivalAt   *time.Time
				actualArrivalAt     *time.Time
			)

			if slices.Contains(schedules.FlightOperationalStatusValues(), event.Status.New.StatusCode) {
				flightStatus = &event.Status.New.StatusCode
			}

			if slices.Contains(schedules.FlightTimingStatusValues(), departureInfo.Delay.Status) {
				departureStatus = &departureInfo.Delay.Status
			}

			if slices.Contains(schedules.FlightTimingStatusValues(), arrivalInfo.Delay.Status) {
				arrivalStatus = &arrivalInfo.Delay.Status
			}

			log.Debug(ctx, "Consuming update.", zap.Any("update", event))

			expectedDepartureAt = getBestDepartureAt(departureInfo.Expected)
			actualDepartureAt = getBestDepartureAt(departureInfo.Actual)

			expectedArrivalAt = getBestArrivalAt(arrivalInfo.Expected)
			actualArrivalAt = getBestArrivalAt(arrivalInfo.Actual)

			upd := &ssimdb.FlightStatusUpdate{
				ExpectedDepartureAt:   expectedDepartureAt,
				ActualDepartureAt:     actualDepartureAt,
				ExpectedArrivalAt:     expectedArrivalAt,
				ActualArrivalAt:       actualArrivalAt,
				FlightStatus:          flightStatus,
				DepartureFlightStatus: departureStatus,
				ArrivalFlightStatus:   arrivalStatus,
			}

			log.Debug(ctx, "Updating expected and actual times",
				zap.String("flight_id", event.FlightID),
				zap.Int("transaction_id", event.Status.New.TransactionId),
				zap.Any("update", upd),
			)

			if event.FlightID == "" {
				log.Warn(ctx, "Flight ID is empty", zap.Any("event", event))
				continue
			}

			if err := ssimdb.UpdateFlightStatus(ctx, db, event.FlightID, upd); err != nil {
				log.Error(ctx, "Failed to update expected and actual times", zap.Error(err))
			}

			if _, err := queue.DeleteMessage(ctx, &sqs.DeleteMessageInput{
				QueueUrl:      &cfg.QueueURL,
				ReceiptHandle: msg.ReceiptHandle,
			}); err != nil {
				log.Error(ctx, "Failed to delete message from queue", zap.Error(err))
			}
		}
	}
}

func mergeDepartureDetails(o, n flightStatusDepartureDetails) flightStatusDepartureDetails {
	m := flightStatusDepartureDetails{
		Timestamp: o.Timestamp,
		OffBlock:  o.OffBlock,
		Airborne:  o.Airborne,
	}

	if n.Timestamp != nil {
		m.Timestamp = n.Timestamp
	}

	if n.OffBlock != nil {
		m.OffBlock = n.OffBlock
	}

	if n.Airborne != nil {
		m.Airborne = n.Airborne
	}

	return m
}

func mergeArrivalDetails(o, n flightStatusArrivalDetails) flightStatusArrivalDetails {
	m := flightStatusArrivalDetails{
		Timestamp: o.Timestamp,
		Touchdown: o.Touchdown,
		OnBlock:   o.OnBlock,
	}

	if n.Timestamp != nil {
		m.Timestamp = n.Timestamp
	}

	if n.Touchdown != nil {
		m.Touchdown = n.Touchdown
	}

	if n.OnBlock != nil {
		m.OnBlock = n.OnBlock
	}

	return m
}

func mergeDelay(o, n flightStatusUpdateDelay) flightStatusUpdateDelay {
	m := flightStatusUpdateDelay{
		Detail:   o.Detail,
		Category: o.Category,
		Status:   o.Status,
	}

	if n.Detail != "" {
		m.Detail = n.Detail
	}
	if n.Category != "" {
		m.Category = n.Category
	}
	if n.Status != "" {
		m.Status = n.Status
	}

	return m
}

func mergeDepartureInformation(o, n flightStatusDepartureInformation) flightStatusDepartureInformation {
	m := flightStatusDepartureInformation{
		Scheduled: mergeDepartureDetails(o.Scheduled, n.Scheduled),
		Expected:  mergeDepartureDetails(o.Expected, n.Expected),
		Actual:    mergeDepartureDetails(o.Actual, n.Actual),
		Delay:     mergeDelay(o.Delay, n.Delay),
	}

	return m
}

func mergeArrivalInformation(o, n flightStatusArrivalInformation) flightStatusArrivalInformation {
	m := flightStatusArrivalInformation{
		Scheduled: mergeArrivalDetails(o.Scheduled, n.Scheduled),
		Expected:  mergeArrivalDetails(o.Expected, n.Expected),
		Actual:    mergeArrivalDetails(o.Actual, n.Actual),
		Delay:     mergeDelay(o.Delay, n.Delay),
	}

	return m
}

func getBestDepartureAt(dept flightStatusDepartureDetails) *time.Time {
	var t *time.Time

	if dept.Airborne != nil && !dept.Airborne.IsZero() {
		t = dept.Airborne
	}

	if dept.OffBlock != nil && !dept.OffBlock.IsZero() {
		t = dept.OffBlock
	}

	if dept.Timestamp != nil && !dept.Timestamp.IsZero() {
		t = dept.Timestamp
	}

	return t
}

func getBestArrivalAt(arr flightStatusArrivalDetails) *time.Time {
	var t *time.Time

	if arr.Touchdown != nil && !arr.Touchdown.IsZero() {
		t = arr.Touchdown
	}

	if arr.OnBlock != nil && !arr.OnBlock.IsZero() {
		t = arr.OnBlock
	}

	if arr.Timestamp != nil && !arr.Timestamp.IsZero() {
		t = arr.Timestamp
	}

	return t
}

type (
	flightStatusUpdateEventMsgBody struct {
		Detail flightStatusUpdateEvent `json:"detail"`
	}

	flightStatusUpdateEvent struct {
		FlightID string `json:"id"`
		Status   struct {
			Old flightStatus `json:"old"`
			New flightStatus `json:"new"`
		} `json:"status"`
	}

	flightStatusDepartureInformation struct {
		Scheduled flightStatusDepartureDetails `json:"scheduled"`
		Expected  flightStatusDepartureDetails `json:"expected"`
		Actual    flightStatusDepartureDetails `json:"actual"`
		Delay     flightStatusUpdateDelay      `json:"delay"`
	}

	flightStatusArrivalInformation struct {
		Scheduled flightStatusArrivalDetails `json:"scheduled"`
		Expected  flightStatusArrivalDetails `json:"expected"`
		Actual    flightStatusArrivalDetails `json:"actual"`
		Delay     flightStatusUpdateDelay    `json:"delay"`
	}

	flightStatus struct {
		StatusCode    string                           `json:"status_code"`
		TransactionId int                              `json:"transaction_id"`
		Departure     flightStatusDepartureInformation `json:"departure"`
		Arrival       flightStatusArrivalInformation   `json:"arrival"`
	}

	flightStatusDepartureDetails struct {
		Timestamp *time.Time `json:"timestamp"`
		OffBlock  *time.Time `json:"off_block"`
		Airborne  *time.Time `json:"airborne"`
		Gate      *string    `json:"gate"`
		CheckIn   *string    `json:"check_in"`
	}

	flightStatusArrivalDetails struct {
		Timestamp    *time.Time `json:"timestamp"`
		Touchdown    *time.Time `json:"touchdown"`
		OnBlock      *time.Time `json:"on_block"`
		BaggageClaim *string    `json:"baggage_claim"`
	}

	flightStatusUpdateDelay struct {
		Detail   string `json:"detail"`
		Category string `json:"category"`
		Status   string `json:"status"`
	}
)

package ssim

import (
	"context"
	"os"
	"os/signal"

	"github.com/cockroachdb/errors"
	"github.com/spf13/cobra"
	"github.com/spf13/viper"
	"go.impalastudios.com/aviation/schedules/pkg/pgsql"
	ssimdb "go.impalastudios.com/aviation/schedules/pkg/ssim/db"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
	"go.impalastudios.com/log"
)

func NewIngest() *cobra.Command {
	cmd := &cobra.Command{
		Use:   "ingest",
		Short: "Ingest SSIM data",
		Long:  "Ingest parsed carrier trailer blocks",
		PreRunE: func(cmd *cobra.Command, _ []string) error {
			return viper.BindPFlags(cmd.Flags())
		},
		RunE: runIngest,
		Args: cobra.ExactArgs(0),
	}

	cmd.PersistentFlags().StringP("link", "l", "carrier_trailers", "Folder that contains the carrier trailer blocks")

	cmd.PersistentFlags().StringP("db.host", "H", "localhost", "database host")
	cmd.PersistentFlags().IntP("db.port", "P", 5433, "database port")
	cmd.PersistentFlags().StringP("db.dbname", "d", "schedules", "database name")
	cmd.PersistentFlags().StringP("db.user", "U", "postgres", "database user")
	cmd.PersistentFlags().String("db.password", "toor", "database password")

	return cmd
}

type ingestConfig struct {
	Link string
	DB   *pgsql.Config
}

func runIngest(cmd *cobra.Command, _ []string) error {
	var cfg ingestConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return err
	}

	quit := make(chan os.Signal, 1)

	signal.Notify(quit, os.Interrupt)

	ctx, cancel := context.WithCancel(cmd.Context())
	defer cancel()

	errChan := make(chan error, 1)
	go func() {
		errChan <- ingest(ctx, &cfg)
	}()

	select {
	case <-quit:
		return errors.New("context canceled")
	case err := <-errChan:
		return err
	}
}

func ingest(ctx context.Context, cfg *ingestConfig) error {
	log.SetLogger(
		log.New(
			log.WithService(viper.GetString("app.name")),
			log.WithEncoding(viper.GetString("logging.encoding")),
			log.WithDebugEnabled(viper.GetBool("logging.debug")),
		),
	)

	db, err := pgsql.Connect(ctx, cfg.DB)
	if err != nil {
		return errors.Wrap(err, "failed to connect to database")
	}

	dirs, err := os.ReadDir(cfg.Link)
	if err != nil {
		return errors.Wrap(err, "failed to read directory")
	}

	links := make([]*parser.CarrierTrailerBlockLink, 0)
	for _, dir := range dirs {
		if dir.IsDir() {
			continue
		}

		links = append(links, &parser.CarrierTrailerBlockLink{
			Filepath:    cfg.Link + "/" + dir.Name(),
			PeriodBegin: dir.Name()[3:],
		})
	}

	ingester := ssimdb.NewIngester(db, cfg.DB)
	return errors.Wrap(ingester.Ingest(ctx, links), "failed to ingest")
}

