func NewImport() *cobra.Command {
	cmd := &cobra.Command{
		Use:   "import",
		Short: "Import SSIM data",
		Long:  "Parse SSIM data, find the diff with the previous state and ingest the diff",
		PreRunE: func(cmd *cobra.Command, _ []string) error {
			return viper.BindPFlags(cmd.Flags())
		},
		RunE: runImport,
		Args: cobra.ExactArgs(0),
	}

	cmd.PersistentFlags().StringP("bucket", "b", "", "S3 bucket")
	cmd.PersistentFlags().StringP("key", "k", "", "S3 key")

	cmd.PersistentFlags().StringP("aws.access-key-id", "i", "", "AWS access key ID")
	cmd.PersistentFlags().StringP("aws.secret-access-key", "s", "", "AWS secret access key")
	cmd.PersistentFlags().StringP("aws.session-token", "t", "", "AWS session token")
	cmd.PersistentFlags().StringP("aws.region", "r", "us-east-1", "AWS region")
	cmd.PersistentFlags().StringP("aws.endpoint-url", "e", "", "AWS endpoint URL")
	cmd.PersistentFlags().Bool("aws.host-name-immutable", false, "AWS host name immutable")
	cmd.PersistentFlags().Bool("aws.s3.use-path-style", false, "AWS S3 use path style")

	cmd.MarkFlagsRequiredTogether("aws.access-key-id", "aws.secret-access-key", "aws.session-token", "aws.region", "aws.endpoint-url")

	cmd.PersistentFlags().StringP("db.host", "H", "localhost", "database host")
	cmd.PersistentFlags().IntP("db.port", "P", 5432, "database port")
	cmd.PersistentFlags().StringP("db.dbname", "d", "schedules", "database name")
	cmd.PersistentFlags().StringP("db.user", "U", "postgres", "database user")
	cmd.PersistentFlags().String("db.password", "toor", "database password")

	return cmd
}

type importConfig struct {
	Bucket string        `mapstructure:"bucket"`
	Key    string        `mapstructure:"key"`
	AWS    xaws.Config   `mapstructure:"aws"`
	DB     *pgsql.Config `mapstructure:"db"`
}

func runImport(cmd *cobra.Command, _ []string) error {
	var cfg importConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return err
	}

	quit := make(chan os.Signal, 1)

	signal.Notify(quit, os.Interrupt)

	ctx, cancel := context.WithCancel(cmd.Context())
	defer cancel()

	errChan := make(chan error, 1)
	go func() {
		errChan <- importssim(ctx, &cfg)
	}()

	select {
	case <-quit:
		return errors.New("context canceled")
	case err := <-errChan:
		return err
	}
}

func importssim(ctx context.Context, cfg *importConfig) error {
	log.SetLogger(
		log.New(
			log.WithService(viper.GetString("app.name")),
			log.WithEncoding(viper.GetString("logging.encoding")),
			log.WithDebugEnabled(viper.GetBool("logging.debug")),
		),
	)

	if err := pgsql.MigrateUp(cfg.DB); err != nil {
		return errors.Wrap(err, "failed to migrate up")
	}

	log.Debug(ctx, "SSIM data processing started", zap.Any("config", cfg))

	db, err := pgsql.Connect(ctx, cfg.DB)
	if err != nil {
		return errors.Wrap(err, "failed to connect to database")
	}
	defer db.Close()

	awsCfg, err := xaws.GetConfig(ctx, &cfg.AWS)
	if err != nil {
		log.Error(ctx, "Failed to load AWS config", zap.Error(err))
	}

	airlinesMapper, err := ssimdb.LoadOagAirlinesMapper(ctx, db)
	if err != nil {
		return errors.Wrap(err, "failed to fill airlines mapper")
	}

	storage := s3.NewFromConfig(awsCfg, func(o *s3.Options) {
		o.UsePathStyle = cfg.AWS.S3.UsePathStyle
	})
	link, err := parseSsimZipFromS3(ctx, cfg, storage, airlinesMapper)
	if err != nil {
		return err
	}

	log.Debug(ctx, "Starting export statefiles")

	if err := exportStatefiles(ctx, cfg, link); err != nil {
		return err
	}

	ingester := ssimdb.NewIngester(db, cfg.DB)

	return ingestStateDiff(ctx, db, ingester, link)
}

func ingestStateDiff(
	ctx context.Context,
	db *pgsql.DB,
	i *ssimdb.Ingester,
	link *parser.SSIM,
) error {
	w := writer.NewBlockWriter(writer.DefaultDir)

	log.Debug(ctx, "SSIM started partial ingestion", zap.Int("link", len(link.Links)))

	var (
		totalIngestedFlights    = concurrent.NewAtomicInt64(0)
		totalRemovedFlights     = concurrent.NewAtomicInt64(0)
		totalIngestedCF         = concurrent.NewAtomicInt64(0)
		totalRemovedCF          = concurrent.NewAtomicInt64(0)
		totalSameFlights        = concurrent.NewAtomicInt64(0)
		totalSameConnectFlights = concurrent.NewAtomicInt64(0)
		totalZombieFlights      = concurrent.NewAtomicInt64(0)
		totalZombieCF           = concurrent.NewAtomicInt64(0)
	)

	fn := func(ctx context.Context, l *parser.CarrierTrailerBlockLink) error {
		oldfilepath := strings.ReplaceAll(l.Filepath, "carrier_trailers", "statefiles")
		old, err := readCarrierTrailerBlock(oldfilepath)
		if errors.Is(err, os.ErrNotExist) {
			return i.IngestLink(ctx, l)
		} else if err != nil {
			return err
		}

		new, err := readCarrierTrailerBlock(l.Filepath)
		if err != nil {
			return errors.Wrap(err, "failed to read ct file")
		}

		df, err := diff.Compare(ctx, old, new)
		if err != nil {
			return errors.Wrap(err, "failed to compare")
		}

		if err := ssimdb.IngestPartially(ctx, db, i, df); err != nil {
			return errors.Wrap(err, "failed to ingest partially")
		}

		totalSameFlights.Add(int64(len(df.Flights.NoChanges)))
		totalSameConnectFlights.Add(int64(len(df.ConnectingFlights.NoChanges)))
		totalRemovedFlights.Add(int64(len(df.Flights.Removed)))
		totalRemovedCF.Add(int64(len(df.ConnectingFlights.Removed)))
		totalIngestedFlights.Add(int64(len(df.MergedCT.Flights)))
		totalIngestedCF.Add(int64(len(df.MergedCT.ConnectingFlights)))
		totalZombieFlights.Add(int64(len(df.Flights.Zombie)))
		totalZombieCF.Add(int64(len(df.ConnectingFlights.Zombie)))

		// log 1 random zombie flight (if any)
		if len(df.Flights.Zombie) > 0 {
			keys := make([]string, 0, len(df.Flights.Zombie))
			for k := range df.Flights.Zombie {
				keys = append(keys, k)
			}
			pick := keys[rand.Intn(len(keys))]
			log.Debug(ctx, "Zombie flight sample", zap.String("flight_id", pick))
		}

		// log 1 random zombie connecting flight (if any)
		if len(df.ConnectingFlights.Zombie) > 0 {
			keys := make([]string, 0, len(df.ConnectingFlights.Zombie))
			for k := range df.ConnectingFlights.Zombie {
				keys = append(keys, k)
			}
			pick := keys[rand.Intn(len(keys))]
			log.Debug(ctx, "Zombie connected flight sample", zap.String("connected_flight_id", pick))
		}

		finalCT := buildFinalCarrierTrailer(old, new, df)
		_, err = w.Write(finalCT)
		return errors.Wrap(err, "failed to write ct")
	}

	errs := concurrent.ExecuteInPool(ctx, link.Links, fn)

	totalIngestedFlights.Add(i.TotalFlightsIngested.Get())
	totalIngestedCF.Add(i.TotalConnectingFlightsIngested.Get())

	log.Debug(ctx, "SSIM finished partial ingestion",
		zap.Int64("same_flights", totalSameFlights.Get()),
		zap.Int64("same_cf", totalSameConnectFlights.Get()),
		zap.Int64("removed_flights", totalRemovedFlights.Get()),
		zap.Int64("removed_cf", totalRemovedCF.Get()),
		zap.Int64("ingested_flights", totalIngestedFlights.Get()),
		zap.Int64("ingested_cf", totalIngestedCF.Get()),
		zap.Int64("zombie_flights", totalZombieFlights.Get()),
		zap.Int64("zombie_cf", totalZombieCF.Get()),
		zap.Errors("errors", errs),
	)

	return nil
}

// buildFinalCarrierTrailer merges the new carrier trailer block with the unchanged old block
// some flights in the old carrier trailer block gets updated so we need to find the unchanged ones
// to merge with the new carrier trailer block
func buildFinalCarrierTrailer(old, curr *parser.CarrierTrailerBlock, df *diff.Diff) *parser.CarrierTrailerBlock {
	// Direct flights
	currentFlights := make(map[string]*parser.Flight)
	for _, f := range curr.Flights {
		currentFlights[f.FlightID] = f
	}

	for _, f := range old.Flights {
		if _, ok := currentFlights[f.FlightID]; ok {
			continue
		}

		if _, ok := df.Flights.Removed[f.FlightID]; !ok {
			curr.Flights = append(curr.Flights, f)
			currentFlights[f.FlightID] = f
		}
	}

	// Connecting flights
	currentCFs := make(map[string]*parser.ConnectingFlight)
	for _, cf := range curr.ConnectingFlights {
		currentCFs[cf.ConnectingFlightID] = cf
	}

	for _, cf := range old.ConnectingFlights {
		if _, ok := currentCFs[cf.ConnectingFlightID]; ok {
			continue
		}

		if _, ok := df.ConnectingFlights.Removed[cf.ConnectingFlightID]; !ok {
			curr.ConnectingFlights = append(curr.ConnectingFlights, cf)
			currentCFs[cf.ConnectingFlightID] = cf
		}
	}

	return curr
}

func exportStatefiles(
	ctx context.Context,
	cfg *importConfig,
	link *parser.SSIM,
) error {
	var (
		airlines []string
		schema   []string
	)

	log.Debug(ctx, "Preparing airline and schema slices", zap.Int("link", len(link.Links)))

	for _, l := range link.Links {
		excludedFilepath := strings.Split(l.Filepath, "/")[1]

		parts := strings.Split(strings.TrimRight(excludedFilepath, ".json"), "_")

		airlines = append(airlines, parts[0])
		schema = append(schema, fmt.Sprintf("period_%s_%s", parts[1], parts[2]))
	}

	exportCfg := &exportCfg{
		Airline: airlines,
		Schema:  schema,
		DB:      cfg.DB,
	}
	return export(ctx, exportCfg)
}

func readCarrierTrailerBlock(filepath string) (*parser.CarrierTrailerBlock, error) {
	f, err := os.Open(filepath)
	if err != nil {
		return nil, errors.Wrap(err, "open ct file")
	}
	defer f.Close()

	var ct parser.CarrierTrailerBlock
	if err := json.NewDecoder(f).Decode(&ct); err != nil {
		return nil, errors.Wrap(err, "decode ct")
	}

	return &ct, nil
}

type mapper interface {
	Get(ctx context.Context, key string) string
}

func parseSsimZipFromS3(
	ctx context.Context,
	cfg *importConfig,
	storage *s3.Client,
	airlinesMapper mapper,
) (*parser.SSIM, error) {
	obj, err := storage.GetObject(ctx, &s3.GetObjectInput{
		Bucket: &cfg.Bucket,
		Key:    &cfg.Key,
	})
	if err != nil {
		return nil, errors.Wrap(err, "failed to get object")
	}
	defer obj.Body.Close()

	readerat := NewBufferingReaderAt(obj.Body)
	reader, err := zip.NewReader(readerat, aws.ToInt64(obj.ContentLength))
	if err != nil {
		return nil, errors.Wrap(err, "failed to create zip reader")
	}

	// We expect a single file in the zip archive
	// That's how the SSIM data is delivered to us
	if len(reader.File) != 1 {
		return nil, errors.New("expected a single file in the zip archive")
	}

	ssimFile := reader.File[0]
	rc, err := ssimFile.Open()
	if err != nil {
		return nil, errors.Wrap(err, "failed to open file")
	}
	defer rc.Close()

	w := writer.NewBlockWriter(writer.DefaultDir)
	p := parser.NewParser(w, airlinesMapper)

	link, err := p.Parse(ctx, rc)
	if err != nil {
		return nil, errors.Wrap(err, "failed to parse file")
	}

	log.Debug(ctx, "SSIM data parsing finished", zap.Int("link", len(link.Links)))

	return link, nil
}

type bufferingReaderAt struct {
	r   io.Reader
	buf []byte
}

func NewBufferingReaderAt(r io.Reader) io.ReaderAt {
	return &bufferingReaderAt{
		r: r,
	}
}

func (br *bufferingReaderAt) ReadAt(p []byte, off int64) (n int, err error) {
	endOff := off + int64(len(p))
	need := endOff - int64(len(br.buf))
	if need > 0 {
		buf := make([]byte, need)
		var rn int
		rn, err = io.ReadFull(br.r, buf)
		br.buf = append(br.buf, buf[:rn]...)
	}

	if int64(len(br.buf)) >= off {
		n = copy(p, br.buf[off:])
	}

	if n == len(p) {
		err = nil
	}

	return
}

