type controlledDuplicatesConfig struct {
	Bucket string        `mapstructure:"bucket"`
	Key    string        `mapstructure:"key"`
	AWS    xaws.Config   `mapstructure:"aws"`
	DB     *pgsql.Config `mapstructure:"db"`
}

func runControlledDuplicates(cmd *cobra.Command, _ []string) error {
	var cfg controlledDuplicatesConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return err
	}

	log.SetLogger(
		log.New(
			log.WithService(viper.GetString("app.name")),
			log.WithEncoding(viper.GetString("logging.encoding")),
			log.WithDebugEnabled(viper.GetBool("logging.debug")),
		),
	)

	if err := pgsql.MigrateUp(cfg.DB); err != nil {
		return errors.Wrap(err, "failed to migrate up")
	}

	ctx := cmd.Context()

	db, err := pgsql.Connect(ctx, cfg.DB)
	if err != nil {
		return errors.Wrap(err, "failed to connect to database")
	}
	defer db.Close()

	awsCfg, err := xaws.GetConfig(ctx, &cfg.AWS)
	if err != nil {
		log.Error(ctx, "Failed to load AWS config", zap.Error(err))
	}

	storage := s3.NewFromConfig(awsCfg, func(o *s3.Options) {
		o.UsePathStyle = cfg.AWS.S3.UsePathStyle
	})
	obj, err := storage.GetObject(ctx, &s3.GetObjectInput{
		Bucket: &cfg.Bucket,
		Key:    &cfg.Key,
	})
	if err != nil {
		return errors.Wrap(err, "failed to get object")
	}
	defer obj.Body.Close()

	var controlledDuplicates []controlledDuplicate
	if err := gocsv.Unmarshal(obj.Body, &controlledDuplicates); err != nil {
		return errors.Wrap(err, "failed to unmarshal CSV")
	}

	var oagMappings []ssimdb.OagMapping
	for _, controlledDuplicate := range controlledDuplicates {
		oagMappings = append(oagMappings, ssimdb.OagMapping{
			OagID: controlledDuplicate.OAGCode,
			ID:    controlledDuplicate.IATACode,
		})
	}

	return ssimdb.UpsertAirlineMappings(ctx, db, oagMappings)
}

