	IATACode             string `csv:"IATACode"`
	PrimaryCarrierName   string `csv:"PrimaryCarrierName"`
	DupeCode             string `csv:"DupeCode"`
	OAGCode              string `csv:"OAGCode"`
	SecondaryCarrierName string `csv:"SecondaryCarrierName"`
}

