func NewExport() *cobra.Command {
	cmd := &cobra.Command{
		Use:   "export",
		Short: "Export flights data",
		Long:  "Exports flight data from the database to a CSV file",
		PreRunE: func(cmd *cobra.Command, _ []string) error {
			return viper.BindPFlags(cmd.Flags())
		},
		RunE: runExport,
		Args: cobra.ExactArgs(0),
	}

	cmd.PersistentFlags().String("airline", "AA", "Airline code")
	cmd.PersistentFlags().String("schema", "period_2024_1", "Schema name")

	cmd.PersistentFlags().StringP("db.host", "H", "localhost", "database host")
	cmd.PersistentFlags().IntP("db.port", "P", 5432, "database port")
	cmd.PersistentFlags().StringP("db.dbname", "d", "schedules", "database name")
	cmd.PersistentFlags().StringP("db.user", "U", "postgres", "database user")
	cmd.PersistentFlags().String("db.password", "toor", "database password")

	return cmd
}

type exportCfg struct {
	Airline []string `mapstructure:"airline"`
	Schema  []string `mapstructure:"schema"`

	DB *pgsql.Config `mapstructure:"db"`
}

func runExport(cmd *cobra.Command, _ []string) error {
	var cfg exportCfg
	if err := viper.Unmarshal(&cfg); err != nil {
		return err
	}

	quit := make(chan os.Signal, 1)

	signal.Notify(quit, os.Interrupt)

	ctx, cancel := context.WithCancel(cmd.Context())
	defer cancel()

	errChan := make(chan error, 1)
	go func() {
		errChan <- export(ctx, &cfg)
	}()

	select {
	case <-quit:
		return errors.New("context canceled")
	case err := <-errChan:
		return err
	}
}

func export(ctx context.Context, cfg *exportCfg) error {
	log.Debug(ctx, "Starting export process")

	db, err := pgsql.Connect(ctx, cfg.DB)
	if err != nil {
		return errors.Wrap(err, "failed to connect to database")
	}
	defer db.Close()

	schemas, err := listSchemas(ctx, db)
	if err != nil {
		return err
	}

	type exportSpec struct {
		Airline string
		Schema  string
	}
	var specs []exportSpec
	for i := range cfg.Airline {
		if _, ok := schemas[cfg.Schema[i]]; !ok {
			continue
		}

		specs = append(specs, exportSpec{
			cfg.Airline[i],
			cfg.Schema[i],
		})
	}

	log.Debug(ctx, "Exporting the data", zap.Int("specs", len(specs)))

	w := &writerCounter{
		blockWriter:            writer.NewBlockWriter("statefiles"),
		TotalFlights:           concurrent.NewAtomicInt64(0),
		TotalConnectingFilghts: concurrent.NewAtomicInt64(0),
	}

	errs := concurrent.ExecuteInPool(ctx, specs, func(ctx context.Context, es exportSpec) error {
		log.Debug(ctx, "exporting data", zap.String("airline", es.Airline), zap.String("schema", es.Schema))
		return ssim_db.Export(ctx, db, w, es.Schema, es.Airline)
	})
	if len(errs) > 0 {
		log.Error(ctx, "failed to export data", zap.Errors("errs", errs))
	}

	log.Debug(ctx, "exported data", zap.Int64("flights", w.TotalFlights.Get()), zap.Int64("connecting_flights", w.TotalConnectingFilghts.Get()))
	return nil
}

func listSchemas(ctx context.Context, db *pgsql.DB) (map[string]bool, error) {
	log.Debug(ctx, "Querying all the schemas")

	rows, err := db.Query(ctx, "SELECT schema_name FROM information_schema.schemata WHERE schema_name LIKE 'period_%'")
	if err != nil {
		return nil, errors.Wrap(err, "failed to list schemas")
	}
	defer rows.Close()

	schemas := make(map[string]bool)
	for rows.Next() {
		var schema string
		if err := rows.Scan(&schema); err != nil {
			return nil, errors.Wrap(err, "failed to scan schema")
		}
		schemas[schema] = true
	}
	if err := rows.Err(); err != nil {
		return nil, errors.Wrap(err, "failed to iterate over schemas")
	}

	log.Debug(ctx, "Found schemas.", zap.Int("schemas", len(schemas)))

	return schemas, nil
}

type writerCounter struct {
	blockWriter *writer.BlockWriter

	TotalFlights           *concurrent.AtomicInt64
	TotalConnectingFilghts *concurrent.AtomicInt64
}

func (w *writerCounter) Write(block *parser.CarrierTrailerBlock) (*parser.CarrierTrailerBlockLink, error) {
	w.TotalConnectingFilghts.Add(int64(len(block.ConnectingFlights)))
	w.TotalFlights.Add(int64(len(block.Flights)))

	return w.blockWriter.Write(block)
}

