)

func NewDiff() *cobra.Command {
	cmd := &cobra.Command{
		Use:   "diff",
		Short: "Diff SSIM data",
		Long:  "Finds the differences between two parsed SSIM data",
		PreRunE: func(cmd *cobra.Command, _ []string) error {
			return viper.BindPFlags(cmd.Flags())
		},
		RunE: runDiff,
		Args: cobra.ExactArgs(0),
	}

	cmd.PersistentFlags().StringP("old", "o", "SIMSCD.DAT", "Old SSIM file path")
	cmd.PersistentFlags().StringP("new", "n", "SIMSCD.DAT", "New SSIM file path")

	return cmd
}

type diffConfig struct {
	Old string `mapstructure:"old"`
	New string `mapstructure:"new"`
}

func runDiff(cmd *cobra.Command, _ []string) error {
	var cfg diffConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return err
	}

	quit := make(chan os.Signal, 1)

	signal.Notify(quit, os.Interrupt)

	ctx, cancel := context.WithCancel(cmd.Context())
	defer cancel()

	errChan := make(chan error, 1)
	go func() {
		errChan <- findDiff(ctx, &cfg)
	}()

	select {
	case <-quit:
		return errors.New("context canceled")
	case err := <-errChan:
		return err
	}
}

func findDiff(ctx context.Context, cfg *diffConfig) error {
	oldBytes, err := os.ReadFile(cfg.Old)
	if err != nil {
		return errors.Wrap(err, "failed to read old file")
	}

	newBytes, err := os.ReadFile(cfg.New)
	if err != nil {
		return errors.Wrap(err, "failed to read new file")
	}

	var (
		oldCtblock parser.CarrierTrailerBlock
		newCtblock parser.CarrierTrailerBlock
	)

	if err := json.Unmarshal(oldBytes, &oldCtblock); err != nil {
		return errors.Wrap(err, "failed to unmarshal old file")
	}

	if err := json.Unmarshal(newBytes, &newCtblock); err != nil {
		return errors.Wrap(err, "failed to unmarshal new file")
	}

	df, err := diff.Compare(ctx, &oldCtblock, &newCtblock)
	if err != nil {
		return errors.Wrap(err, "failed to compare")
	}

	log.Debug(ctx, "diffing ssim data",
		zap.Int("flights_same", len(df.Flights.NoChanges)),
		zap.Int("connecting_flights_same", len(df.ConnectingFlights.NoChanges)),
		zap.Int("flights_removed_updated", len(df.Flights.Removed)),
		zap.Int("connecting_flights_removed_updated", len(df.ConnectingFlights.Removed)),
	)

	return nil
}

