// defaultMapper is a mapper that returns the key itself
type defaultMapper struct{}

// Get returns the key
func (d *defaultMapper) Get(ctx context.Context, key string) string {
	return key
}

func parse(ctx context.Context, cfg *parseConfig) error {
	log.SetLogger(
