type grpcHealthCheckDbClient interface {
	Ping(ctx context.Context) error
}

func newServeGrpcCommand() *cobra.Command {
	cmd := &cobra.Command{
		Use:   "grpc",
		Short: "Serve gRPC server",
		Long:  "Serve gRPC server",
		PreRunE: func(cmd *cobra.Command, _ []string) error {
			return viper.BindPFlags(cmd.Flags())
		},
		RunE: runServeGRPC,
		Args: cobra.ExactArgs(0),
	}

	cmd.PersistentFlags().Int("app.grpc.port", 50051, "gRPC service port")

	return cmd
}

func runServeGRPC(cmd *cobra.Command, _ []string) error {
	ctx := cmd.Context()

	logger := log.New(
		log.WithService(viper.GetString("app.name")),
		log.WithEncoding(viper.GetString("logging.encoding")),
		log.WithDebugEnabled(viper.GetBool("logging.debug")),
		log.AddCallerSkip(1),
	)
	log.SetLogger(logger)

	var cfg serveConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return errors.Wrap(err, "failed to unmarshal config")
	}

	quit := make(chan os.Signal, 1)
	defer close(quit)

	signal.Notify(quit, os.Interrupt)

	resolver.Register(dnssrvresolver.New())

	db, err := pgsql.Connect(ctx, cfg.DB)
	if err != nil {
		return errors.Wrap(err, "failed to connect to database")
	}

	airportsGrpcAdapter := schedules_adapter.NewAirportGrpcClient(
		grpc_airports.NewAirportsServiceClient(xgrpc.MustConnect(cfg.Services.Airports)),
	)
	airlinesGrpcAdapter := schedules_adapter.NewAirlineGrpcClient(
		grpc_airlines.NewAirlinesServiceClient(xgrpc.MustConnect(cfg.Services.Airlines)),
	)
	aircraftGrpcAdapter := schedules_adapter.NewAircraftGrpcClient(
		grpc_aircraft.NewAircraftServiceClient(xgrpc.MustConnect(cfg.Services.Aircraft)),
	)

	cipher := enc.NewCBCCipher(viper.GetString("app.key"))
	repo := schedules_adapter.NewFlightsPostgresRepository(db, cipher)
	svc := schedules.NewService(repo, airlinesGrpcAdapter, airportsGrpcAdapter, aircraftGrpcAdapter)

	server := grpc.NewServer(
		grpc.UnaryInterceptor(grpc_requestid.NewServerUnaryInterceptor()),
		grpc.StreamInterceptor(grpc_requestid.NewServerStreamInterceptor()),
	)
	schedulesServiceV0 := grpcv0.NewServer(svc)
	pbv0.RegisterSchedulesServiceServer(server, schedulesServiceV0)

	healthcheck := health.NewServer()
	grpc_health_v1.RegisterHealthServer(server, healthcheck)
	go func(hc *health.Server, db grpcHealthCheckDbClient) {
		for {
			st := grpc_health_v1.HealthCheckResponse_SERVING
			if err := db.Ping(context.TODO()); err != nil {
				st = grpc_health_v1.HealthCheckResponse_NOT_SERVING
			}

			hc.SetServingStatus("", st)

			time.Sleep(time.Minute)
		}
	}(healthcheck, db)

	reflection.Register(server)

	listener, err := net.Listen("tcp", fmt.Sprintf(":%d", viper.GetInt("app.grpc.port")))
	if err != nil {
		return err
	}

	go func(s *grpc.Server, lis *net.Listener) {
		log.Info(ctx, "GRPC server started. Ready to accept incoming requests...")
		if err := s.Serve(*lis); err != nil {
			log.Fatal(ctx, fmt.Sprintf("Failed to serve gRPC: %v", err))
		}
	}(server, &listener)

	<-quit

	log.Info(ctx, "Shutting down the REST server...")

	ctx, cancel := context.WithTimeout(ctx, 5*time.Second)
	defer cancel()

	server.GracefulStop()

	select {
	case <-ctx.Done():
		log.Info(ctx, "REST server shutdown timed out.")
	default:
		log.Info(ctx, "REST server shutdown gracefully.")
	}

	return nil
}

func runServeHTTP(cmd *cobra.Command, _ []string) error {
	ctx := cmd.Context()

	logger := log.New(
		log.WithService(viper.GetString("app.name")),
		log.WithEncoding(viper.GetString("logging.encoding")),
		log.WithDebugEnabled(viper.GetBool("logging.debug")),
		log.AddCallerSkip(1),
	)
	log.SetLogger(logger)

	var cfg serveConfig
	if err := viper.Unmarshal(&cfg); err != nil {
		return errors.Wrap(err, "failed to unmarshal config")
	}

	quit := make(chan os.Signal, 1)
	defer close(quit)

	signal.Notify(quit, os.Interrupt)

	router := gin.New()
	router.Use(requestid.New()...)
	router.Use(
		ginzap.GinzapWithConfig(logger, &ginzap.Config{
			TimeFormat: time.RFC3339,
			UTC:        true,
			SkipPaths:  []string{"/__gw/health"},
			Context: func(c *gin.Context) []zapcore.Field {
				return []zapcore.Field{log.RequestIDExtractor(c), zap.String("type", "access_log")}
			},
		}),
		ginzap.RecoveryWithZap(logger, true),
	)

	resolver.Register(dnssrvresolver.New())

	db, err := pgsql.Connect(ctx, cfg.DB)
	if err != nil {
		return errors.Wrap(err, "failed to connect to database")
	}

	airportsGrpcAdapter := schedules_adapter.NewAirportGrpcClient(
		grpc_airports.NewAirportsServiceClient(xgrpc.MustConnect(cfg.Services.Airports)),
	)
	airlinesGrpcAdapter := schedules_adapter.NewAirlineGrpcClient(
		grpc_airlines.NewAirlinesServiceClient(xgrpc.MustConnect(cfg.Services.Airlines)),
	)
	aircraftsGrpcAdapter := schedules_adapter.NewAircraftGrpcClient(
		grpc_aircraft.NewAircraftServiceClient(xgrpc.MustConnect(cfg.Services.Aircraft)),
	)

	cipher := enc.NewCBCCipher(viper.GetString("app.key"))
	repo := schedules_adapter.NewFlightsPostgresRepository(db, cipher)
	svc := schedules.NewService(repo, airlinesGrpcAdapter, airportsGrpcAdapter, aircraftsGrpcAdapter)

	gw := router.Group("/__gw")

	handlers := v0.NewHandler(svc)
	handlers.Register(gw)

	health := httphandlers.NewHealthHandler(db)
	health.Register(gw)

	if v, ok := binding.Validator.Engine().(*validator.Validate); ok {
		i18n.Register(v)
	}

	srv := http.Server{
		Addr:    fmt.Sprintf(":%d", viper.GetInt("app.http.port")),
		Handler: router,
	}

	go func() {
		if err := srv.ListenAndServe(); err != nil && errors.Is(err, http.ErrServerClosed) {
			log.Error(ctx, "Error occurred while listening.", zap.Error(err))
		}
	}()

	<-quit

	log.Info(ctx, "Shutting down the REST server...")

	ctx, cancel := context.WithTimeout(ctx, 5*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		log.Error(ctx, "Error occurred while shutting down.", zap.Error(err))
	}

	select {
	case <-ctx.Done():
		log.Info(ctx, "REST server shutdown timed out.")
	default:
		log.Info(ctx, "REST server shutdown gracefully.")
	}

	return nil
}

