// NewService creates a new schedules service
func NewService(repo repository, airlines airlinesClient, airports airportsClient, aircraft aircraftClient) *Service {
	return &Service{
		repo:     repo,
		airlines: airlines,
		airports: airports,
		aircraft: aircraft,
	}
}

// FlightSearchQuery search for flights
type FlightSearchQuery struct {
	DepartureAirports []string
	ArrivalAirports   []string
	DepartureCity     int64
	ArrivalCity       int64
	DepartureDate     time.Time
	Airline           *string
	Cursor            *string
	Limit             uint
}

// FlightDesignatorQuery search for flights
type FlightDesignatorQuery struct {
	Airline       string
	Number        int
	DepartureDate time.Time
	Cursor        *string
	Limit         uint
}

// AirportFlightBoardQuery search for flights
type AirportFlightBoardQuery struct {
	Airport string
	Type    FlightBoardType
	Date    time.Time
	Cursor  *string
	Limit   uint
}

// AirlineFlightBoardQuery search for flights
type AirlineFlightBoardQuery struct {
	Airline string
	Date    time.Time
	Cursor  *string
	Limit   uint
}

func (s *Service) Search(ctx context.Context, q *FlightSearchQuery) (*Page[*Schedule], error) {
	var err error
	if q.DepartureCity > 0 {
		apts, err := s.getAirportDesignatorsForCity(ctx, q.DepartureCity, q.DepartureDate)
		if err != nil {
			log.Error(ctx, "Cannot retrieve  airports for departure city.", zap.Int64("departure_city", q.DepartureCity), zap.Error(err))
			return nil, err
		}
		log.Debug(ctx, "Airport designators for departure city.", zap.Int64("departure_city", q.DepartureCity), zap.Strings("designators", apts))
		q.DepartureAirports = apts
	}

	if q.ArrivalCity > 0 {
		apts, err := s.getAirportDesignatorsForCity(ctx, q.ArrivalCity, q.DepartureDate)
		if err != nil {
			log.Error(ctx, "Cannot retrieve  airports for arrival city.", zap.Int64("arrival_city", q.ArrivalCity), zap.Error(err))
			return nil, err
		}

		log.Debug(ctx, "Airport designators for arrival city.", zap.Int64("arrival_city", q.ArrivalCity), zap.Strings("designators", apts))
		q.ArrivalAirports = apts
	}

	log.Debug(ctx, "Search query.", zap.Any("q", q))

	page, err := s.repo.Search(ctx, q)
	if err != nil {
		return nil, err
	}
	var flights []*Flight

	for sc := range page.Items {
		flights = append(flights, page.Items[sc].Flights...)
	}

	if len(flights) > 0 {
		s.enrichFlights(ctx, flights)
	}

	for sc := range page.Items {
		page.Items[sc].MainCarrier = page.Items[sc].Flights[0].Airline
		page.Items[sc].DepartureAirport = page.Items[sc].Flights[0].DepartureAirport
		page.Items[sc].ArrivalAirport = page.Items[sc].Flights[len(page.Items[sc].Flights)-1].ArrivalAirport
	}

	return page, nil
}

func (s *Service) Get(ctx context.Context, id string) (*Flight, error) {
	f, err := s.repo.Get(ctx, id)
	if err != nil {
		log.Error(ctx, "Cannot get flight.", zap.String("id", id), zap.Error(err))
		return nil, err
	}

	s.enrichFlights(ctx, []*Flight{f})

	return f, nil
}

func (s *Service) GetBatch(ctx context.Context, ids []string) ([]*Flight, error) {
