func (s *Service) getAirportDesignatorsForCity(ctx context.Context, id int64, scheduledDepartureAt time.Time) ([]string, error) {
	airports, err := s.airports.GetByCity(ctx, id, scheduledDepartureAt)
	if err != nil {
		return nil, err
	}

	designators := make([]string, 0, len(airports))
	for _, a := range airports {
		if a.Designator != "" {
			designators = append(designators, a.Designator)
		}
	}

	return designators, nil
}

func (s *Service) getUTCDate(t time.Time) time.Time {
	tUTC := t.In(time.UTC)
	year, month, day := tUTC.Date()
	return time.Date(year, month, day, 0, 0, 0, 0, time.UTC)
}

func (s *Service) enrichFlights(ctx context.Context, fs []*Flight) {
	airlineDesignatorsByDate := make(map[string]map[string]bool)
	airportDesignatorsByDate := make(map[string]map[string]bool)
	aircraftDesignators := make(map[string]bool)

	// Collect all related entities' designators
	for _, f := range fs {
		sch := s.getUTCDate(f.ScheduledDepartureAt).Format(time.DateOnly)
		if _, found := airlineDesignatorsByDate[sch]; !found {
			airlineDesignatorsByDate[sch] = make(map[string]bool)
		}

		if _, found := airportDesignatorsByDate[sch]; !found {
			airportDesignatorsByDate[sch] = make(map[string]bool)
		}

		airlineDesignatorsByDate[sch][f.Airline.Designator] = true
		airportDesignatorsByDate[sch][f.DepartureAirport.Designator] = true
		airportDesignatorsByDate[sch][f.ArrivalAirport.Designator] = true
		if f.Aircraft != nil {
			aircraftDesignators[f.Aircraft.ID] = true
		}

		for _, c := range f.Codeshare {
			if c.Airline.Designator != "" {
				airlineDesignatorsByDate[sch][c.Airline.Designator] = true
			}
		}
	}

	log.Debug(ctx, "Collected designators and IDs.", zap.Any("airlines", airlineDesignatorsByDate), zap.Any("airports", airportDesignatorsByDate), zap.Any("aircraft", aircraftDesignators))

	// Retrieve related entities from respective services.
	airportsByDateDesignators := make(map[string]map[string]*Airport)
	airlinesByDateDesignators := make(map[string]map[string]*Airline)
	aircraftByDesignators := make(map[string]*Aircraft)

	for sch, ds := range airportDesignatorsByDate {
		if _, found := airportsByDateDesignators[sch]; !found {
			airportsByDateDesignators[sch] = make(map[string]*Airport)
		}

		scheduledAt, _ := time.Parse(time.DateOnly, sch)
		var designators []string
		for k := range ds {
			designators = append(designators, k)
		}

		log.Debug(ctx, "Getting airports batch.", zap.Strings("designators", designators), zap.Time("for_date", scheduledAt))
		if airports, err := s.airports.BatchGet(ctx, designators, scheduledAt); err == nil {
			for _, a := range airports {
				airportsByDateDesignators[sch][a.Designator] = a
			}
		} else {
			log.Warn(ctx, "Cannot retrieve airports.", zap.Strings("designators", designators), zap.Time("for_date", scheduledAt), zap.Error(err))
		}
	}

	for sch, ds := range airlineDesignatorsByDate {
		if _, found := airlinesByDateDesignators[sch]; !found {
			airlinesByDateDesignators[sch] = make(map[string]*Airline)
		}
		scheduledAt, _ := time.Parse(time.DateOnly, sch)
		var designators []string
		for k := range ds {
			designators = append(designators, k)
		}
		log.Debug(ctx, "Getting airlines batch.", zap.Strings("designators", designators), zap.Time("for_date", scheduledAt))
		if airlines, err := s.airlines.BatchGet(ctx, designators, scheduledAt); err == nil {
			for _, a := range airlines {
				airlinesByDateDesignators[sch][a.Designator] = a
			}
		} else {
			log.Warn(ctx, "Cannot retrieve airlines.", zap.Strings("designators", designators), zap.Time("for_date", scheduledAt), zap.Error(err))
		}
	}

	var acrtDesignators []string
	for k := range aircraftDesignators {
		acrtDesignators = append(acrtDesignators, k)
	}
	log.Debug(ctx, "Getting aircraft batch.", zap.Strings("designators", acrtDesignators))
	if aircraft, err := s.aircraft.BatchGet(ctx, acrtDesignators); err == nil {
		for _, a := range aircraft {
			aircraftByDesignators[a.ID] = a
		}
	} else {
		log.Warn(ctx, "Cannot retrieve aircraft.", zap.Strings("designators", acrtDesignators), zap.Error(err))
	}

	// Fill in related entity.
	for _, f := range fs {
		sch := s.getUTCDate(f.ScheduledDepartureAt).Format(time.DateOnly)

		if _, found := airlinesByDateDesignators[sch]; found {
			if arl, found := airlinesByDateDesignators[sch][f.Airline.Designator]; found {
				f.Airline = arl
			} else {
				log.Warn(ctx, "Cannot find main carrier airline.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airline", f.Airline))
			}
		} else {
			log.Warn(ctx, "Cannot find main carrier airline.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airline", f.Airline))
		}

		if _, found := airportsByDateDesignators[sch]; found {
			if apt, found := airportsByDateDesignators[sch][f.DepartureAirport.Designator]; found {
				f.DepartureAirport = apt
			} else {
				log.Warn(ctx, "Cannot find departure airport.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airport", f.DepartureAirport))
			}
		} else {
			log.Warn(ctx, "Cannot find departure airport.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airport", f.DepartureAirport))
		}

		if _, found := airportsByDateDesignators[sch]; found {
			if apt, found := airportsByDateDesignators[sch][f.ArrivalAirport.Designator]; found {
				f.ArrivalAirport = apt
			} else {
				log.Warn(ctx, "Cannot find arrival airport.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airport", f.ArrivalAirport))
			}
		} else {
			log.Warn(ctx, "Cannot find arrival airport.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airport", f.ArrivalAirport))
		}

		for _, c := range f.Codeshare {
			if c.Airline.Designator != "" {
				if _, found := airlinesByDateDesignators[sch]; found {
					if arl, found := airlinesByDateDesignators[sch][c.Airline.Designator]; found {
						c.Airline = arl
					} else {
						log.Warn(ctx, "Cannot find codeshare carrier airline.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airline", c.Airline))
					}
				} else {
					log.Warn(ctx, "Cannot find codeshare carrier airline.", zap.String("flight_id", f.FlightID), zap.String("scheduled_departure_at", sch), zap.Any("airline", c.Airline))
				}
			}
		}

		if f.Aircraft != nil {
			if aircraft, found := aircraftByDesignators[f.Aircraft.ID]; found {
				f.Aircraft = aircraft
			} else {
				log.Warn(ctx, "Cannot find aircraft.", zap.String("flight_id", f.FlightID), zap.Any("aircraft", f.Aircraft))
			}
		}
	}
}

func (s *Service) enrichRoutes(ctx context.Context, routes *UniqueRoutes, departureDate time.Time) {
	if routes.Airline.Designator != "" {
		if airline, err := s.airlines.Get(ctx, routes.Airline.Designator, departureDate); err == nil {
			routes.Airline = airline
		} else {
			log.Warn(ctx, "Cannot retrieve airline for routes.", zap.String("airline", routes.Airline.Designator), zap.Error(err))
		}
	}

	if len(routes.Routes) > 0 {
		airportDesignators := set.New()
		for _, route := range routes.Routes {
			airportDesignators.Insert(route.A.Designator)
			airportDesignators.Insert(route.B.Designator)
		}

		var designators []string
		airportDesignators.Do(func(k interface{}) {
			designators = append(designators, k.(string))
		})

		if airports, err := s.airports.BatchGet(ctx, designators, departureDate); err == nil {
			airportsHash := make(map[string]*Airport)
			for _, airport := range airports {
				airportsHash[airport.Designator] = airport
			}

			for _, route := range routes.Routes {
				if a, found := airportsHash[route.A.Designator]; found {
					route.A = a
				}

				if b, found := airportsHash[route.B.Designator]; found {
					route.B = b
				}
			}
		} else {
			log.Warn(ctx, "Cannot retrieve airports for routes.", zap.Strings("designators", designators), zap.Error(err))
		}
	}
}

