)

func TestSchedulesPostgresSQLAdapter(t *testing.T) {
	if testing.Short() {
		t.Skip("skipping schedules postgresql adapters test suite")
	}

	suite.Run(t, &schedulesPostgresSQLRepositoryTestSuite{
		PostgresSQLSuite: postgrestesting.NewPostgresSQLSuite(
			postgrestesting.WithUserPassword("ro", "toor"),
			postgrestesting.WithDB("schedules"),
			postgrestesting.Migrate(),
		),
	})
}

type schedulesPostgresSQLRepositoryTestSuite struct {
	*postgrestesting.PostgresSQLSuite
}

func (s *schedulesPostgresSQLRepositoryTestSuite) TestSearch_TFT9691_ConnectingFlights() {
	ctx := context.Background()
	cipher := mocks.NewMockcipher(gomock.NewController(s.T()))
	repo := adapter.NewFlightsPostgresRepository(s.DB(ctx), cipher)
	var connectingFlightID int64 = 1
	expectedFlights := []*schedules.Flight{
		{
			ID:                   1,
			ConnectingFlightID:   connectingFlightID,
			FlightID:             "20230815_KL_425_AMS_RUH",
			Airline:              &schedules.Airline{Designator: "KL"},
			FlightNumber:         425,
			Sequence:             1,
			DepartureAirport:     &schedules.Airport{Designator: "AMS"},
			ArrivalAirport:       &schedules.Airport{Designator: "RUH"},
			ScheduledDepartureAt: time.Date(2023, 8, 15, 8, 0, 0, 0, time.Local),
			ExpectedDepartureAt:  time.Date(2023, 8, 15, 8, 0, 0, 0, time.Local),
			ActualDepartureAt:    time.Date(2023, 8, 15, 8, 0, 0, 0, time.Local),
			ScheduledArrivalAt:   time.Date(2023, 8, 15, 11, 0, 0, 0, time.Local),
			ExpectedArrivalAt:    time.Date(2023, 8, 15, 11, 0, 0, 0, time.Local),
			ActualArrivalAt:      time.Date(2023, 8, 15, 11, 0, 0, 0, time.Local),
			Aircraft:             &schedules.Aircraft{ID: "C30"},
			Status:               schedules.FlightOperationalStatusScheduled,
			DepartureStatus:      schedules.FlightTimingStatusNoStatus,
			ArrivalStatus:        schedules.FlightTimingStatusNoStatus,
			Codeshare: []*schedules.Codeshare{
				{
					Airline:      &schedules.Airline{Designator: "AF"},
					FlightNumber: 3123,
				},
				{
					Airline:      &schedules.Airline{Designator: "SV"},
					FlightNumber: 6301,
				},
			},
		},
		{
			ID:                   2,
			ConnectingFlightID:   connectingFlightID,
			FlightID:             "20230816_KL_425_RUH_DMM",
			Airline:              &schedules.Airline{Designator: "KL"},
			FlightNumber:         425,
			Sequence:             2,
			DepartureAirport:     &schedules.Airport{Designator: "RUH"},
			ArrivalAirport:       &schedules.Airport{Designator: "DMM"},
			ScheduledDepartureAt: time.Date(2023, 8, 16, 14, 0, 0, 0, time.Local),
			ExpectedDepartureAt:  time.Date(2023, 8, 16, 14, 0, 0, 0, time.Local),
			ActualDepartureAt:    time.Date(2023, 8, 16, 14, 0, 0, 0, time.Local),
			ScheduledArrivalAt:   time.Date(2023, 8, 16, 17, 0, 0, 0, time.Local),
			ExpectedArrivalAt:    time.Date(2023, 8, 16, 17, 0, 0, 0, time.Local),
			ActualArrivalAt:      time.Date(2023, 8, 16, 17, 0, 0, 0, time.Local),
			Aircraft:             &schedules.Aircraft{ID: "A10"},
			Status:               schedules.FlightOperationalStatusScheduled,
			DepartureStatus:      schedules.FlightTimingStatusNoStatus,
			ArrivalStatus:        schedules.FlightTimingStatusNoStatus,
			Codeshare: []*schedules.Codeshare{
				{
					Airline:      &schedules.Airline{Designator: "AF"},
					FlightNumber: 3123,
				},
				{
					Airline:      &schedules.Airline{Designator: "SV"},
					FlightNumber: 6301,
				},
			},
		},
	}

	s.insertFlights(ctx, expectedFlights, &connectingFlightID)

	// Replicate SSIM import inconsistency by deleting the first flight of a connected flight
	// without updating the information of the connected flight row.
	s.deleteFlights(ctx, []int64{1})

	page, err := repo.Search(ctx, &schedules.FlightSearchQuery{
		DepartureAirports: []string{"AMS"},
		DepartureDate:     time.Date(2023, 8, 16, 0, 0, 0, 0, time.UTC),
		Limit:             10,
	})

	s.NoError(err)
	s.Len(page.Items, 0)
}

func (s *schedulesPostgresSQLRepositoryTestSuite) deleteFlights(ctx context.Context, ids []int64) {
	_, err := s.DB(ctx).Exec(
		ctx,
		`DELETE FROM period_2023_8.flights WHERE id = ANY($1::int[])`,
		pgsql.Array(ids),
	)
	s.Require().NoError(err)
}

func (s *schedulesPostgresSQLRepositoryTestSuite) TearDownTest() {
	ctx := context.Background()

	_, err := s.DB(ctx).Exec(ctx, "DROP SCHEMA IF EXISTS period_2023_8 CASCADE")
	s.Require().NoError(err)
}

package adapter

import (
	"context"
	"fmt"
	"github.com/cockroachdb/errors"
	lru "github.com/hashicorp/golang-lru/v2"
	"go.impalastudios.com/aviation/schedules/internal/schedules"
	"go.impalastudios.com/requestid"
	grpcrid "go.impalastudios.com/requestid/grpc"
	"go.uber.org/zap"
	"google.golang.org/grpc/metadata"
	"time"

	grpc_clients "go.impalastudios.com/aviation/schedules/internal/schedules/adapter/grpc_clients/airlines"
	"go.impalastudios.com/log"
)

// AirlineGrpcClient is a wrapper around the grpc client for the airline service.
// It contains a cache to avoid unnecessary calls to the service.
type AirlineGrpcClient struct {
	cache  *lru.Cache[string, *schedules.Airline]
	client grpc_clients.AirlinesServiceClient
}

// NewAirlineGrpcClient creates a new AirlineGrpcClient.
func NewAirlineGrpcClient(client grpc_clients.AirlinesServiceClient) *AirlineGrpcClient {
	cache, _ := lru.New[string, *schedules.Airline](lruCacheSize)

	return &AirlineGrpcClient{
		cache:  cache,
		client: client,
	}
}

// Get returns an airline by its ID.
func (c *AirlineGrpcClient) Get(ctx context.Context, designator string, forDate time.Time) (*schedules.Airline, error) {
	log.Debug(ctx, "Getting airline details.", zap.String("designator", designator), zap.Time("for_date", forDate))

	cacheKey := c.getCacheKey(designator, forDate)
	if airline, ok := c.cache.Get(cacheKey); ok {
		log.Debug(ctx, "Airline cache hit.", zap.Any("airline", airline))
		return airline, nil
	}

	d := forDate.Format(time.DateOnly)
	req := &grpc_clients.GetByDesignatorRequest{Designator: designator, ForDate: &d}

	ctx = metadata.AppendToOutgoingContext(ctx, grpcrid.MetadataKey, requestid.GetRequestID(ctx))
	resp, err := c.client.GetByDesignator(ctx, req)
	if err != nil {
		return nil, errors.Wrap(err, "failed to get airline details")
	}

	log.Debug(ctx, "fetched airline through gRPC", zap.Any("airline", resp.Airline))

	arl := c.toDomain(resp.Airline)
	c.cache.Add(cacheKey, arl)
	return arl, nil
}

// BatchGet returns airlines by their designators.
func (c *AirlineGrpcClient) BatchGet(ctx context.Context, designators []string, forDate time.Time) ([]*schedules.Airline, error) {
	log.Debug(ctx, "Getting batch of airports details.", zap.Strings("designators", designators), zap.Time("for_date", forDate))

	var result []*schedules.Airline
	var cacheMisses []string

	for _, designator := range designators {
		cacheKey := c.getCacheKey(designator, forDate)

		if airline, found := c.cache.Get(cacheKey); found {
			log.Debug(ctx, "Airline cache hit.", zap.String("designator", designator), zap.Time("for_date", forDate), zap.Any("airline", airline))
			result = append(result, airline)
			continue
		}

		cacheMisses = append(cacheMisses, designator)
	}

	if len(cacheMisses) > 0 {
		ctx = metadata.AppendToOutgoingContext(ctx, grpcrid.MetadataKey, requestid.GetRequestID(ctx))
		for _, batch := range chunkEvery(cacheMisses, 25) {
			log.Debug(ctx, "Retrieving airports batch.", zap.Strings("designators", batch), zap.Time("for_date", forDate))

			fd := forDate.Format(time.DateOnly)
			req := &grpc_clients.BatchGetByDesignatorRequest{Designators: batch, ForDate: &fd}
			resp, err := c.client.BatchGetByDesignator(ctx, req)
			if err != nil {
				log.Error(ctx, "Cannot retrieve airlines batch.", zap.Strings("designators", designators), zap.Time("for_date", forDate), zap.Error(err))
				continue
			}

			for _, airline := range resp.Airlines {
				arl := c.toDomain(airline)
				cacheKey := c.getCacheKey(arl.Designator, forDate)
				c.cache.Add(cacheKey, arl)
				result = append(result, arl)
			}
		}
	}

	return result, nil
}

func (c *AirlineGrpcClient) toDomain(a *grpc_clients.Airline) *schedules.Airline {
	ad := &schedules.Airline{}

	ad.ID = &a.Id
	if a.Designator != nil {
		ad.Designator = *a.Designator
	}
	ad.IATA = a.Iata
	ad.ICAO = a.Icao
	ad.Classification = uint(a.Classification)
	ad.Name = a.Name
	ad.Description = a.GetDescription()
	ad.WebsiteURL = a.WebsiteUrl
	ad.WikipediaURL = a.GetWikipediaUrl()
	ad.FacebookURL = a.FacebookUrl
	ad.TwitterURL = a.TwitterUrl
	ad.ReservationTelephoneNumber = a.ReservationTelephoneNumber

	if a.HubAirport != nil {
		ad.HubAirport = &schedules.Airport{
			ID:         &a.GetHubAirport().Id,
			Designator: a.GetHubAirport().GetDesignator(),
			Name:       a.GetHubAirport().Name,
		}
		if a.GetHubAirport().City != nil {
			ad.HubAirport.City = &schedules.City{
				ID:       int(a.GetHubAirport().GetCity().GetId()),
				Name:     a.GetHubAirport().GetCity().GetName(),
				State:    a.GetHubAirport().GetCity().State,
				Timezone: a.GetHubAirport().GetCity().GetTimezone(),
				Country: &schedules.Country{
					ID:   a.GetHubAirport().GetCity().GetCountry().GetId(),
					Name: a.GetHubAirport().GetCity().GetCountry().GetName(),
				},
			}
		}
	}
	ad.Images = make(map[string]string)
	if a.GetAssets().Tail != nil {
		ad.Images["tail"] = a.GetAssets().GetTail()
	}
	if a.GetAssets().Square != nil {
		ad.Images["square"] = a.GetAssets().GetSquare()
	}
	if a.GetAssets().Rect != nil {
		ad.Images["rect"] = a.GetAssets().GetRect()
	}
	ad.CreatedAt = a.CreatedAt.AsTime()
	ad.UpdatedAt = a.UpdatedAt.AsTime()

	return ad
}

func (c *AirlineGrpcClient) getCacheKey(designator string, d time.Time) string {
	return fmt.Sprintf("%s:%s", d.Format(time.DateOnly), designator)
}

