// NewAircraftGrpcClient creates a new AircraftGrpcClient.
func NewAircraftGrpcClient(client grpc_clients.AircraftServiceClient) *AircraftGrpcClient {
	cache, _ := lru.New[string, *schedules.Aircraft](lruCacheSize)

	return &AircraftGrpcClient{
		cache:  cache,
		client: client,
	}
}

// Get returns an aircraft by its IATA code.
func (c *AircraftGrpcClient) Get(ctx context.Context, id string) (*schedules.Aircraft, error) {
	log.Debug(ctx, "getting aircraft details", zap.String("id", id))

	if aircraft, ok := c.cache.Get(id); ok {
		log.Debug(ctx, "found aircraft in cache", zap.Any("aircraft", aircraft))
		return aircraft, nil
	}

	ctx = metadata.AppendToOutgoingContext(ctx, grpcrid.MetadataKey, requestid.GetRequestID(ctx))
	resp, err := c.client.Get(ctx, &grpc_clients.AircraftDetailsRequest{Id: id})
	if err != nil {
		return nil, errors.Wrap(err, "failed to get aircraft details")
	}

	log.Debug(ctx, "Fetched aircraft through gRPC.", zap.Any("aircraft", resp.Aircraft))

	arct := c.toDomain(resp.Aircraft)

	c.cache.Add(id, arct)
	return arct, nil
}

// BatchGet returns airlines by their designators.
func (c *AircraftGrpcClient) BatchGet(ctx context.Context, designators []string) ([]*schedules.Aircraft, error) {
	log.Debug(ctx, "Getting batch of aircraft details.", zap.Strings("designators", designators))

	var result []*schedules.Aircraft
	var cacheMisses []string

	for _, designator := range designators {
		if aircraft, found := c.cache.Get(designator); found {
			log.Debug(ctx, "Airline cache hit.", zap.String("designator", designator), zap.Any("aircraft", aircraft))
			result = append(result, aircraft)
			continue
		}

		cacheMisses = append(cacheMisses, designator)
	}

	if len(cacheMisses) > 0 {
		ctx = metadata.AppendToOutgoingContext(ctx, grpcrid.MetadataKey, requestid.GetRequestID(ctx))
		for _, batch := range chunkEvery(cacheMisses, 25) {
			log.Debug(ctx, "Retrieving airports batch.", zap.Strings("designators", batch))

			req := &grpc_clients.BatchGetRequest{Ids: batch}
			resp, err := c.client.BatchGet(ctx, req)
			if err != nil {
				log.Error(ctx, "Cannot retrieve airlines batch.", zap.Strings("designators", designators), zap.Error(err))
				continue
			}

			for _, aircraft := range resp.Aircraft {
				arc := c.toDomain(aircraft)
				c.cache.Add(arc.ID, arc)
				result = append(result, arc)
			}
		}
	}

	return result, nil
}

func (c *AircraftGrpcClient) toDomain(a *grpc_clients.Aircraft) *schedules.Aircraft {
	ad := &schedules.Aircraft{}

	ad.ID = a.Id
	ad.Type = a.Type
	ad.Name = a.Name
	ad.Description = a.GetDescription()
	ad.WikipediaURL = a.GetWikipediaUrl()

	ad.Images = make(map[string]string)
	for _, image := range a.GetImages() {
		k := strings.ToLower(image.Type.String())
		ad.Images[k] = image.Url
	}

	ad.Specification = &schedules.Specification{
		Engine:       a.GetSpecs().Engine,
		AverageSpeed: a.GetSpecs().AverageSpeed,
		WideBody:     a.GetSpecs().WideBody,
		Wingspan:     a.GetSpecs().Wingspan,
		Length:       a.GetSpecs().Length,
		Height:       a.GetSpecs().Height,
		FlightRange:  a.GetSpecs().FlightRange,
	}

	if a.GetSpecs().SeatingCapacity != nil {
		sc := int(a.GetSpecs().GetSeatingCapacity())
		ad.Specification.SeatingCapacity = &sc
	}

	if a.GetSpecs().CrewSize != nil {
		cs := int(a.GetSpecs().GetCrewSize())
		ad.Specification.CrewSize = &cs
	}

	ad.CreatedAt = a.CreatedAt.AsTime()
	ad.UpdatedAt = a.UpdatedAt.AsTime()

	return ad
}

package adapter

func chunkEvery[T any](items []T, size int) (chunks [][]T) {
	for size < len(items) {
		items, chunks = items[size:], append(chunks, items[0:size:size])
	}
	return append(chunks, items)
}

