)

// AirportGrpcClient is a wrapper around the grpc client for the airport service.
// It contains an airport and cityCache to avoid unnecessary calls to the service.
type AirportGrpcClient struct {
	airportCache *lru.Cache[string, *schedules.Airport]
	cityCache    *lru.Cache[string, []string]
	client       grpc_clients.AirportsServiceClient
}

// NewAirportGrpcClient creates a new AirportGrpcClient.
func NewAirportGrpcClient(client grpc_clients.AirportsServiceClient) *AirportGrpcClient {
	airportCache, _ := lru.New[string, *schedules.Airport](lruCacheSize)
	cityCache, _ := lru.New[string, []string](lruCacheSize)

	return &AirportGrpcClient{
		airportCache: airportCache,
		cityCache:    cityCache,
		client:       client,
	}
}

// Get returns an airport by its designator.
func (c *AirportGrpcClient) Get(ctx context.Context, designator string, forDate time.Time) (*schedules.Airport, error) {
	log.Debug(ctx, "Getting airport details.", zap.String("designator", designator), zap.Time("for_date", forDate))

	cacheKey := designator
	if !forDate.IsZero() {
		d := forDate.Format(time.DateOnly)
		cacheKey = fmt.Sprintf("%s:%s", d, cacheKey)
	}

	if airport, ok := c.airportCache.Get(cacheKey); ok {
		log.Debug(ctx, "found airport in cache", zap.Any("airport", airport))
		return airport, nil
	}

	ctx = metadata.AppendToOutgoingContext(ctx, grpcrid.MetadataKey, requestid.GetRequestID(ctx))
	req := &grpc_clients.GetByDesignatorRequest{Designator: designator}
	if !forDate.IsZero() {
		d := forDate.Format(time.DateOnly)
		req.ForDate = &d
	}
	resp, err := c.client.GetByDesignator(ctx, req)
	if err != nil {
		return nil, errors.Wrap(err, "failed to get airport details")
	}

	log.Debug(ctx, "fetched airport through gRPC", zap.Any("airport", resp.Airport))

	apt := c.toDomain(resp.Airport)
	c.airportCache.Add(cacheKey, apt)
	return apt, nil
}

// BatchGet returns airports by their designators.
func (c *AirportGrpcClient) BatchGet(ctx context.Context, designators []string, forDate time.Time) ([]*schedules.Airport, error) {
	log.Debug(ctx, "Getting batch of airports details.", zap.Strings("designators", designators), zap.Time("for_date", forDate))

	var result []*schedules.Airport
	var cacheMisses []string

	for _, designator := range designators {
		cacheKey := c.getCacheKey(designator, forDate)

		if airport, found := c.airportCache.Get(cacheKey); found {
			log.Debug(ctx, "Airport cache hit.", zap.Any("airport", airport))
			result = append(result, airport)
			continue
		}

		cacheMisses = append(cacheMisses, designator)
	}

	if len(cacheMisses) > 0 {
		ctx = metadata.AppendToOutgoingContext(ctx, grpcrid.MetadataKey, requestid.GetRequestID(ctx))
		for _, batch := range chunkEvery(cacheMisses, 25) {
			log.Debug(ctx, "Retrieving airports batch.", zap.Strings("designators", batch), zap.Time("for_date", forDate))

			fd := forDate.Format(time.DateOnly)
			req := &grpc_clients.BatchGetByDesignatorRequest{Designators: batch, ForDate: &fd}
			resp, err := c.client.BatchGetByDesignator(ctx, req)
			if err != nil {
				log.Error(ctx, "Cannot retrieve airports batch.", zap.Strings("designators", designators), zap.Time("for_date", forDate), zap.Error(err))
				continue
			}

			for _, airport := range resp.Airports {
				apt := c.toDomain(airport)
				cacheKey := c.getCacheKey(apt.Designator, forDate)
				c.airportCache.Add(cacheKey, apt)
				result = append(result, apt)
			}
		}
	}

	return result, nil
}

// GetByCity returns all airport by their city id.
func (c *AirportGrpcClient) GetByCity(ctx context.Context, id int64, forDate time.Time) ([]*schedules.Airport, error) {
	log.Debug(ctx, "Getting airport by city.", zap.Int64("city_id", id), zap.Time("for_date", forDate))

	cityCacheKey := fmt.Sprintf("%d", id)
	if !forDate.IsZero() {
		d := forDate.Format(time.DateOnly)
		cityCacheKey = fmt.Sprintf("%s:%s", d, cityCacheKey)
	}

	if designators, found := c.cityCache.Get(cityCacheKey); found {
		log.Debug(ctx, "Found airport designators in cityCache.", zap.Strings("designators", designators), zap.Time("for_date", forDate))

		airports := make([]*schedules.Airport, 0, len(designators))

		for _, des := range designators {
			aptCacheKey := des
			if !forDate.IsZero() {
				d := forDate.Format(time.DateOnly)
				aptCacheKey = fmt.Sprintf("%s:%s", d, aptCacheKey)
			}

			if airport, found := c.airportCache.Get(aptCacheKey); found {
				airports = append(airports, airport)
			}
		}

		log.Debug(ctx, "Retrieved airports for city.", zap.Int64("city_id", id), zap.Time("for_date", forDate), zap.Any("airports", airports))

		if len(designators) == len(airports) {
			return airports, nil
		}
	}

	ctx = metadata.AppendToOutgoingContext(ctx, grpcrid.MetadataKey, requestid.GetRequestID(ctx))
	resp, err := c.client.GetByCity(ctx, &grpc_clients.AirportByCityRequest{Id: id})
	if err != nil {
		return nil, errors.Wrap(err, "failed to get airport by city")
	}

	log.Debug(ctx, "Fetched airports through gRPC.", zap.Any("airports", resp.Airports))

	designators := make([]string, 0, len(resp.Airports))
	airports := make([]*schedules.Airport, 0, len(resp.Airports))

	for _, airport := range resp.Airports {
		if airport.Designator != nil {
			a := c.toDomain(airport)
			designators = append(designators, *airport.Designator)

			aptCacheKey := *airport.Designator
			if !forDate.IsZero() {
				d := forDate.Format(time.DateOnly)
				aptCacheKey = fmt.Sprintf("%s:%s", d, aptCacheKey)
			}

			log.Debug(ctx, "Caching airport.", zap.Any("airport", a), zap.String("cache_key", aptCacheKey))

			airports = append(airports, a)
			c.airportCache.Add(aptCacheKey, a)
		}
	}

	c.cityCache.Add(cityCacheKey, designators)

	log.Debug(ctx, "Retrieved airports for city.", zap.Int64("city_id", id), zap.Any("airports", airports))

	return airports, nil
}

func (c *AirportGrpcClient) toDomain(a *grpc_clients.Airport) *schedules.Airport {
	ad := &schedules.Airport{}

	ad.ID = &a.Id
	if a.Designator != nil {
		ad.Designator = *a.Designator
	}
	ad.IATA = a.Iata
	ad.ICAO = a.Icao
	ad.FAA = a.Faa
	ad.Classification = uint(a.Classification)
	ad.Name = a.Name
	ad.ShortName = a.ShortName
	ad.Description = a.Description
	ad.City = &schedules.City{
		ID:       int(a.GetCity().GetId()),
		Name:     a.GetCity().GetName(),
		State:    a.GetCity().State,
		Timezone: a.GetCity().GetTimezone(),
		Country: &schedules.Country{
			ID:   a.GetCity().GetCountry().GetId(),
			Name: a.GetCity().GetCountry().GetName(),
		},
	}
	ad.Latitude = float64(a.Latitude)
	ad.Longitude = float64(a.Longitude)
	ad.Elevation = int(a.Elevation)
	ad.NumberOfRunways = uint(a.NumberOfRunways)
	ad.WebsiteURL = a.WebsiteUrl
	ad.WikipediaURL = a.GetWikipediaUrl()
	ad.FacebookURL = a.FacebookUrl
	ad.TwitterURL = a.TwitterUrl
	ad.CreatedAt = a.CreatedAt.AsTime()
	ad.UpdatedAt = a.UpdatedAt.AsTime()

	return ad
}

func (c *AirportGrpcClient) getCacheKey(designator string, d time.Time) string {
	return fmt.Sprintf("%s:%s", d.Format(time.DateOnly), designator)
}

func (f *flightPg) toDomain() (*schedules.Flight, error) {
	fd := &schedules.Flight{}

	fd.ID = f.ID
	fd.FlightID = f.FlightID
	fd.ConnectingFlightID = f.ConnectingFlightID.Int64
	fd.Airline = &schedules.Airline{Designator: f.MainCarrierDesignator}
	fd.FlightNumber = f.FlightNumber
	fd.Sequence = f.Sequence
	fd.DepartureAirport = &schedules.Airport{Designator: f.DepartureAirportDesignator}
	fd.ArrivalAirport = &schedules.Airport{Designator: f.ArrivalAirportDesignator}
	fd.Status = schedules.FlightOperationalStatus(f.Status)
	fd.DepartureStatus = schedules.FlightTimingStatus(f.DepartureStatus)
	fd.ArrivalStatus = schedules.FlightTimingStatus(f.ArrivalStatus)

	if f.AircraftID.Valid {
		fd.Aircraft = &schedules.Aircraft{ID: f.AircraftID.String}
	}

	var err error
	if fd.ScheduledDepartureAt, err = fromDateAndTimeWithTZ(f.ScheduledDepartureDate, f.ScheduledDepartureTimeTz); err != nil {
		return nil, err
	}

	if fd.ExpectedDepartureAt, err = fromDateAndTimeWithTZ(f.ExpectedDepartureDate, f.ExpectedDepartureTimeTz); err != nil {
		return nil, err
	}

	if fd.ActualDepartureAt, err = fromDateAndTimeWithTZ(f.ActualDepartureDate, f.ActualDepartureTimeTz); err != nil {
		return nil, err
	}

	if fd.ScheduledArrivalAt, err = fromDateAndTimeWithTZ(f.ScheduledArrivalDate, f.ScheduledArrivalTimeTz); err != nil {
		return nil, err
	}

	if fd.ExpectedArrivalAt, err = fromDateAndTimeWithTZ(f.ExpectedArrivalDate, f.ExpectedArrivalTimeTz); err != nil {
		return nil, err
	}

	if fd.ActualArrivalAt, err = fromDateAndTimeWithTZ(f.ActualArrivalDate, f.ActualArrivalTimeTz); err != nil {
		return nil, err
	}

	fd.Codeshare = make([]*schedules.Codeshare, 0, len(f.Codeshares))
	for i := range f.Codeshares {
		fn, err := strconv.Atoi(f.Codeshares[i][1])
		if err != nil {
			continue
		}
		fd.Codeshare = append(fd.Codeshare, &schedules.Codeshare{
			Airline:      &schedules.Airline{Designator: f.Codeshares[i][0]},
			FlightNumber: fn,
		})
	}

	return fd, nil
}

