// FlightsPostgresRepository is the adapter for the flight postgres database
type FlightsPostgresRepository struct {
	db        *pgsql.DB
	paginator *cursor.Paginator[goqu.Expression, []exp.OrderedExpression]
}

// NewFlightsPostgresRepository creates a new flight postgres adapter
func NewFlightsPostgresRepository(db *pgsql.DB, cipher cipher) *FlightsPostgresRepository {
	return &FlightsPostgresRepository{
		db: db,
		paginator: cursor.NewPaginator[goqu.Expression, []exp.OrderedExpression](
			cursor.WithEncryption[goqu.Expression, []exp.OrderedExpression](cipher),
			cursor.WithBuilder(cursor.NewGoquBuilder()),
		),
	}
}

// Search returns a list paginated list of schedules matching the given criteria.
// The result set consists of two types of items: directed flights and connected ones.
// Direct flights are flights that take you from A to B in a single operation. For example, AMS (Amsterdam) to Venice (VCE).
// Connected flights are flights that take you from A to B with more than one operation. For example,
// AMS (Amsterdam) to NRT (Tokyo Narita) via FRA (Frankfurt). This results in two operations: AMS -> FRA, and FRA -> NRT.
// To achieve this behaviour, we merge the result of two queries: the first one returns all the direct flights operations, while the
// second one returns all connected operations. The result set is then ordered by scheduled departure timestamp ascending order.
// To paginate the dataset, we leverage the DENSE_RANK function to assign a row number to each schedule. In the case of a
// connected flight, the row number will be the same for all the operations in the very same connected flight.
func (r *FlightsPostgresRepository) Search(ctx context.Context, in *schedules.FlightSearchQuery) (*schedules.Page[*schedules.Schedule], error) {
	var c *cursor.Cursor
	var err error

	schema := r.getSchemaFromTime(in.DepartureDate)
	pg := goqu.Dialect("postgres")

	if in.Cursor != nil {
		if c, err = r.paginator.Decode(*in.Cursor); err != nil {
			return nil, err
		}
	} else {
		c = cursor.NewForwardCursor(
			in.Limit,
			cursor.Field{Name: "row_number", Direction: cursor.DirectionAsc},
		)
	}

	var upperBoundRowNumberClause goqu.Expression
	if c.Fields[0].Value != nil {
		upperBoundRowNumberClause = goqu.I(c.Fields[0].Name).Lte(int(c.Fields[0].Value.(float64)) + int(in.Limit) + 1)
	} else {
		upperBoundRowNumberClause = goqu.I(c.Fields[0].Name).Lte(int(in.Limit) + 1)
	}

	aggregatedConnectedFlightsCTE := pg.Select(
		goqu.I("connecting_flights.id"),
		goqu.I("connecting_flights.departure_airports"),
		goqu.I("connecting_flights.arrival_airports"),
		goqu.I("connecting_flights.scheduled_departure_ats"),
	).
		From(goqu.S(schema).Table("connecting_flights")).
		Where(goqu.L("? <@ connecting_flights.scheduled_departure_ats", pq.Array([]time.Time{in.DepartureDate})))

	var connectedFlightsSearchDepartureAirportsCTE, connectedFlightsSearchArrivalAirportsCTE goqu.Expression
	if len(in.DepartureAirports) > 0 {
		connectedFlightsSearchDepartureAirportsCTE = pg.Select(
			goqu.L("UNNEST(?::varchar[])", pq.Array(in.DepartureAirports)).As("departure_airport"),
		)
	}

	if len(in.ArrivalAirports) > 0 {
		connectedFlightsSearchArrivalAirportsCTE = pg.Select(
			goqu.L("UNNEST(?::varchar[])", pq.Array(in.ArrivalAirports)).As("arrival_airport"),
		)
	}

	var eligibleConnectedMinSequenceColumn, eligibleConnectedMaxSequenceColumn exp.LiteralExpression
	var eligibleConnectedFlightsWhereConditions []exp.Expression

	connectedFlightsJoinedCTE := pg.Select(goqu.Star()).From("aggregated_connected_flights")

	if connectedFlightsSearchDepartureAirportsCTE != nil {
		connectedFlightsJoinedCTE = connectedFlightsJoinedCTE.CrossJoin(goqu.T("connected_flights_search_departure_airports"))
		eligibleConnectedFlightsWhereConditions = append(eligibleConnectedFlightsWhereConditions,
			goqu.L("ARRAY[connected_flights_joined.departure_airport]::varchar[] <@ connected_flights_joined.departure_airports"),
		)
		eligibleConnectedMinSequenceColumn = goqu.L("MIN(array_position(connected_flights_joined.departure_airports, connected_flights_joined.departure_airport))")
	} else {
		eligibleConnectedFlightsWhereConditions = append(eligibleConnectedFlightsWhereConditions, goqu.L("?::date[] <@ connected_flights_joined.scheduled_departure_ats", pq.Array([]time.Time{in.DepartureDate})))
		eligibleConnectedMinSequenceColumn = goqu.L("MIN(array_position(connected_flights_joined.scheduled_departure_ats, ?))", in.DepartureDate)
	}

	if connectedFlightsSearchArrivalAirportsCTE != nil {
		connectedFlightsJoinedCTE = connectedFlightsJoinedCTE.CrossJoin(goqu.T("connected_flights_search_arrival_airports"))
		eligibleConnectedFlightsWhereConditions = append(eligibleConnectedFlightsWhereConditions, goqu.L("ARRAY[connected_flights_joined.arrival_airport]::varchar[] <@ connected_flights_joined.arrival_airports"))
		eligibleConnectedMaxSequenceColumn = goqu.L("MAX(array_position(connected_flights_joined.arrival_airports, connected_flights_joined.arrival_airport))")
	} else {
		eligibleConnectedMaxSequenceColumn = goqu.L("9999::int")
	}

	eligibleConnectedFlightsCTE := pg.Select(
		goqu.I("connected_flights_joined.id"),
		goqu.I("connected_flights_joined.scheduled_departure_ats"),
		eligibleConnectedMinSequenceColumn.As("min_sequence"),
		eligibleConnectedMaxSequenceColumn.As("max_sequence"),
	).
		GroupBy(goqu.I("connected_flights_joined.id"), goqu.I("connected_flights_joined.scheduled_departure_ats")).
		From("connected_flights_joined").
		Where(eligibleConnectedFlightsWhereConditions...)

	connectedFlightsCTE := pg.
		Select(
			goqu.I("flights.connecting_flight_id"),
			goqu.I("flights.id"),
			goqu.I("flights.flight_id"),
			goqu.I("flights.airline"),
			goqu.I("flights.flight_number"),
			goqu.I("flights.sequence"),
			goqu.I("flights.departure_airport"),
			goqu.I("flights.status"),
			goqu.I("flights.departure_status"),
			goqu.I("flights.arrival_status"),
			goqu.I("flights.scheduled_departure_at"),
			goqu.I("flights.scheduled_departure_time_tz"),
			goqu.L(`eligible_connected_flights.scheduled_departure_ats[eligible_connected_flights.min_sequence]::date`).As("first_scheduled_departure_at"),
			goqu.L(`FIRST_VALUE((CONCAT("flights"."scheduled_departure_at", ' ', REGEXP_REPLACE("flights"."scheduled_departure_time_tz", '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\1:\2\3\4:\5')))::timestamptz) OVER (PARTITION BY "flights"."connecting_flight_id" ORDER BY ("flights"."sequence"))`).As("connected_scheduled_departure_time_utc"),
			goqu.L(`(concat("flights"."scheduled_departure_at", ' ', regexp_replace("flights"."scheduled_departure_time_tz", '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\1:\2\3\4:\5')))::timestamptz`).As("scheduled_departure_time_utc"),
			goqu.I("flights.expected_departure_at"),
			goqu.I("flights.expected_departure_time_tz"),
			goqu.I("flights.actual_departure_at"),
			goqu.I("flights.actual_departure_time_tz"),
			goqu.I("flights.arrival_airport"),
			goqu.I("flights.scheduled_arrival_at"),
			goqu.I("flights.scheduled_arrival_time_tz"),
			goqu.I("flights.expected_arrival_at"),
			goqu.I("flights.expected_arrival_time_tz"),
			goqu.I("flights.actual_arrival_at"),
			goqu.I("flights.actual_arrival_time_tz"),
			goqu.I("flights.aircraft"),
			goqu.I("flights.codeshares"),
		).
		From(goqu.S(schema).Table("flights")).
		InnerJoin(goqu.T("eligible_connected_flights"), goqu.On(goqu.And(
			goqu.I("flights.connecting_flight_id").Eq(goqu.I("eligible_connected_flights.id")),
			goqu.I("flights.sequence").Gte(goqu.I("eligible_connected_flights.min_sequence")),
			goqu.I("flights.sequence").Lte(goqu.I("eligible_connected_flights.max_sequence")),
		)))

	if in.Airline != nil {
		connectedFlightsCTE = connectedFlightsCTE.Where(
			goqu.Or(
				goqu.I("flights.airline").Eq(*in.Airline),
				goqu.L("? <@ flights.codeshare_airlines", pq.Array([]string{*in.Airline})),
			),
		)
	}

	directFlightsCTE := pg.
		Select(
			goqu.I("flights.connecting_flight_id"),
			goqu.I("flights.id"),
			goqu.I("flights.flight_id"),
			goqu.I("flights.airline"),
			goqu.I("flights.flight_number"),
			goqu.I("flights.sequence"),
			goqu.I("flights.departure_airport"),
			goqu.I("flights.status"),
			goqu.I("flights.departure_status"),
			goqu.I("flights.arrival_status"),
			goqu.I("flights.scheduled_departure_at"),
			goqu.I("flights.scheduled_departure_time_tz"),
			goqu.I("flights.scheduled_departure_at").As("first_scheduled_departure_at"),
			goqu.L("(CONCAT(flights.scheduled_departure_at, ' ', REGEXP_REPLACE(scheduled_departure_time_tz, '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\\1:\\2\\3\\4:\\5')))::timestamptz").As("connected_scheduled_departure_time_utc"),
			goqu.L("(CONCAT(flights.scheduled_departure_at, ' ', REGEXP_REPLACE(scheduled_departure_time_tz, '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\\1:\\2\\3\\4:\\5')))::timestamptz").As("scheduled_departure_time_utc"),
			goqu.I("flights.expected_departure_at"),
			goqu.I("flights.expected_departure_time_tz"),
			goqu.I("flights.actual_departure_at"),
			goqu.I("flights.actual_departure_time_tz"),
			goqu.I("flights.arrival_airport"),
			goqu.I("flights.scheduled_arrival_at"),
			goqu.I("flights.scheduled_arrival_time_tz"),
			goqu.I("flights.expected_arrival_at"),
			goqu.I("flights.expected_arrival_time_tz"),
			goqu.I("flights.actual_arrival_at"),
			goqu.I("flights.actual_arrival_time_tz"),
			goqu.I("flights.aircraft"),
			goqu.I("flights.codeshares"),
		).
		From(goqu.S(schema).Table("flights")).
		Where(goqu.L("flights.scheduled_departure_at::date = ?", in.DepartureDate), goqu.I("flights.connecting_flight_id").IsNull())

	if len(in.ArrivalAirports) > 0 {
		directFlightsCTE = directFlightsCTE.Where(goqu.I("flights.arrival_airport").In(in.ArrivalAirports))
	}

	if len(in.DepartureAirports) > 0 {
		directFlightsCTE = directFlightsCTE.Where(goqu.I("flights.departure_airport").In(in.DepartureAirports))
	}

	if in.Airline != nil {
		directFlightsCTE = directFlightsCTE.Where(
			goqu.Or(
				goqu.I("flights.airline").Eq(*in.Airline),
				goqu.L("? <@ flights.codeshare_airlines", pq.Array([]string{*in.Airline})),
			),
		)
	}

	mergedCTE := pg.From("direct_flights").Select(goqu.Star()).
		Union(pg.From("connected_flights").Select(goqu.Star()))

	rankedCTE := pg.
		Select(
			goqu.L(`DENSE_RANK() OVER (ORDER BY "merged"."connected_scheduled_departure_time_utc", "merged"."airline", "merged"."flight_number")`).As("row_number"),
			goqu.Star(),
		).
		From("merged").
		Where(goqu.L(`"merged"."first_scheduled_departure_at"::date = ?`, in.DepartureDate)).
		Order(
			goqu.I("merged.connected_scheduled_departure_time_utc").Asc(),
			goqu.I("merged.scheduled_departure_time_utc").Asc(),
			goqu.I("merged.flight_id").Asc(),
		)

	w := []goqu.Expression{r.paginator.GetWhere(c)}
	if upperBoundRowNumberClause != nil {
		w = append(w, upperBoundRowNumberClause)
	}

	q := pg.
		From("ranked").
		With("direct_flights", directFlightsCTE).
		With("aggregated_connected_flights", aggregatedConnectedFlightsCTE)

	if len(in.DepartureAirports) > 0 {
		q = q.With("connected_flights_search_departure_airports", connectedFlightsSearchDepartureAirportsCTE)
	}

	if len(in.ArrivalAirports) > 0 {
		q = q.With("connected_flights_search_arrival_airports", connectedFlightsSearchArrivalAirportsCTE)
	}

	stmt, bindings, err := q.
		With("connected_flights_joined", connectedFlightsJoinedCTE).
		With("eligible_connected_flights", eligibleConnectedFlightsCTE).
		With("connected_flights", connectedFlightsCTE).
		With("merged", mergedCTE).
		With("ranked", rankedCTE).
		Select(
			goqu.I("ranked.connecting_flight_id").As("connecting_flight_id"),
			goqu.I("ranked.id").As("id"),
			goqu.I("ranked.flight_id").As("flight_id"),
			goqu.I("ranked.airline").As("airline"),
			goqu.I("ranked.flight_number").As("flight_number"),
			goqu.I("ranked.sequence").As("sequence"),
			goqu.I("ranked.departure_airport").As("departure_airport"),
			goqu.I("ranked.status").As("status"),
			goqu.I("ranked.departure_status").As("departure_status"),
			goqu.I("ranked.arrival_status").As("arrival_status"),
			goqu.I("ranked.scheduled_departure_at").As("scheduled_departure_at"),
			goqu.I("ranked.scheduled_departure_time_tz").As("scheduled_departure_time_tz"),
			goqu.I("ranked.expected_departure_at").As("expected_departure_at"),
			goqu.I("ranked.expected_departure_time_tz").As("expected_departure_time_tz"),
			goqu.I("ranked.actual_departure_at").As("actual_departure_at"),
			goqu.I("ranked.actual_departure_time_tz").As("actual_departure_time_tz"),
			goqu.I("ranked.arrival_airport").As("arrival_airport"),
			goqu.I("ranked.scheduled_arrival_at").As("scheduled_arrival_at"),
			goqu.I("ranked.scheduled_arrival_time_tz").As("scheduled_arrival_time_tz"),
			goqu.I("ranked.expected_arrival_at").As("expected_arrival_at"),
			goqu.I("ranked.expected_arrival_time_tz").As("expected_arrival_time_tz"),
			goqu.I("ranked.actual_arrival_at").As("actual_arrival_at"),
			goqu.I("ranked.actual_arrival_time_tz").As("actual_arrival_time_tz"),
			goqu.I("ranked.aircraft").As("aircraft"),
			goqu.I("ranked.codeshares").As("codeshares"),
			goqu.I("ranked.connected_scheduled_departure_time_utc").As("connected_scheduled_departure_time_utc"),
			goqu.I("ranked.scheduled_departure_time_utc").As("scheduled_departure_time_utc"),
			goqu.I("ranked.row_number").As("row_number"),
		).
		Where(w...).
		Prepared(true).
		ToSQL()

	if err != nil {
		return nil, errors.Wrap(err, "postgres repository: failed to prepare sql statement")
	}

	log.Debug(ctx, "Prepared SQL statement.", zap.String("statement", stmt), zap.Any("bindings", bindings))

	rows, err := r.db.Query(ctx, stmt, bindings...)
	if err != nil {
		if e, ok := err.(*pgconn.PgError); ok && e.Code == "42P01" { // Missing Schema
			log.Error(ctx, "Schema not found.", zap.Any("request", in), zap.String("schema", schema), zap.Error(err))
			return &schedules.Page[*schedules.Schedule]{Items: make([]*schedules.Schedule, 0)}, nil
		}

		log.Error(ctx, "Cannot execute query.",
			zap.Any("request", in),
			zap.String("statement", stmt),
			zap.Any("bindings", bindings),
			zap.Error(err),
		)
		return nil, errors.Wrap(err, "postgres repository: failed to query database")
	}

	var result []flightPg
	var distinctSchedules = make(map[int]bool)
	var rowNumbers []int

	for rows.Next() {
		var fpg flightPg
		if err := r.scan(rows, &fpg); err != nil {
			return nil, errors.Wrap(err, "postgres repository: cannot scan row")
		}
		result = append(result, fpg)
		if _, found := distinctSchedules[fpg.RowNumber]; !found {
			distinctSchedules[fpg.RowNumber] = true
			rowNumbers = append(rowNumbers, fpg.RowNumber)
		}
	}

	sort.Ints(rowNumbers)
	log.Debug(ctx, "Retrieved row numbers.", zap.Ints("list", rowNumbers))
	hasNextPage := len(rowNumbers) > int(in.Limit)

	var upperBoundRowNumber int
	if hasNextPage {
		upperBoundRowNumber = rowNumbers[len(rowNumbers)-1] - 1
	} else {
		if len(rowNumbers) > 0 {
			upperBoundRowNumber = rowNumbers[len(rowNumbers)-1]
		}
	}

	var nextCursor *string
	if hasNextPage {
		c = cursor.NewForwardCursor(
			c.Limit,
			cursor.Field{Name: "row_number", Value: upperBoundRowNumber, Direction: cursor.DirectionAsc},
		)

		if next, err := r.paginator.Encode(c); err == nil {
			nextCursor = &next
			log.Debug(ctx, "Next page available.", zap.Uint("limit", in.Limit), zap.Int("count", len(result)), zap.String("next", next))
		} else {
			log.Error(ctx, "Cannot create next cursor.", zap.Uint("limit", in.Limit), zap.Int("count", len(result)), zap.Error(err))
		}

		result = result[:len(result)-1]
	}

	var sch []*schedules.Schedule
	var connectedFlightsMap = make(map[int64]*schedules.Schedule)

	for _, fpg := range result {
		if fpg.RowNumber > upperBoundRowNumber {
			continue
		}

		log.Debug(ctx, "converting flight db model.", zap.Any("flight_db_model", fpg))

		f, err := fpg.toDomain()

		log.Debug(ctx, "converted flight model.", zap.Any("flight_domain_model", f))

		if err != nil {
			return nil, errors.Wrap(err, "postgres repository: cannot adapt flight to domain model")
		}

		if f.ConnectingFlightID > 0 {
			if sc, found := connectedFlightsMap[f.ConnectingFlightID]; found {
				sc.ArrivalAirport = f.ArrivalAirport
				sc.Flights = append(sc.Flights, f)
			} else {
				sc = &schedules.Schedule{
					DepartureAirport: f.DepartureAirport,
					ArrivalAirport:   f.ArrivalAirport,
					MainCarrier:      f.Airline,
					FlightNumber:     f.FlightNumber,
					Flights:          []*schedules.Flight{f},
				}
				sch = append(sch, sc)
				connectedFlightsMap[f.ConnectingFlightID] = sc
			}
		} else {
			sc := &schedules.Schedule{
				DepartureAirport: f.DepartureAirport,
				ArrivalAirport:   f.ArrivalAirport,
				MainCarrier:      f.Airline,
				FlightNumber:     f.FlightNumber,
				Flights:          []*schedules.Flight{f},
			}
			sch = append(sch, sc)
		}
	}

	log.Debug(ctx, "Schedules.", zap.Int("count", len(sch)))

	return &schedules.Page[*schedules.Schedule]{
		Items: sch,
		Next:  nextCursor,
	}, nil
}

func (r *FlightsPostgresRepository) Get(ctx context.Context, id string) (*schedules.Flight, error) {
	flights, err := r.GetBatch(ctx, []string{id})
	if err != nil {
		return nil, err
	}
	if len(flights) == 0 {
		return nil, schedules.ErrFlightNotFound
	}
	return flights[0], nil
}

func (r *FlightsPostgresRepository) GetBatch(ctx context.Context, ids []string) ([]*schedules.Flight, error) {
	uniqueIDs := make(map[string]struct{})
	for _, id := range ids {
		uniqueIDs[id] = struct{}{}
	}

	uniqueIDSlice := make([]string, 0, len(uniqueIDs))
	for id := range uniqueIDs {
		uniqueIDSlice = append(uniqueIDSlice, id)
	}

	idsPerSchema := make(map[string][]string)
	for _, id := range uniqueIDSlice {
		schema := r.getSchemaFromFlightID(id)
		idsPerSchema[schema] = append(idsPerSchema[schema], id)
	}

	fs := make([]*schedules.Flight, 0)
	for schema, ids := range idsPerSchema {
		fsPerSchema, err := r.getBatchForSchema(ctx, schema, ids)
		if err != nil {
			log.Warn(ctx, "failed to get batch for schema",
				zap.String("schema", schema),
				zap.Error(err))
			continue
		}

		fs = append(fs, fsPerSchema...)
	}

	return fs, nil
}

func (r *FlightsPostgresRepository) getBatchForSchema(ctx context.Context, schema string, ids []string) ([]*schedules.Flight, error) {
	pg := goqu.Dialect("postgres")

	stmt, bindings, err := pg.
		Select(
			goqu.I("flights.connecting_flight_id"),
			goqu.I("flights.id"),
			goqu.I("flights.flight_id"),
			goqu.I("flights.airline"),
			goqu.I("flights.flight_number"),
			goqu.I("flights.sequence"),
			goqu.I("flights.departure_airport"),
			goqu.I("flights.status"),
			goqu.I("flights.departure_status"),
			goqu.I("flights.arrival_status"),
			goqu.I("flights.scheduled_departure_at"),
			goqu.I("flights.scheduled_departure_time_tz"),
			goqu.I("flights.expected_departure_at"),
			goqu.I("flights.expected_departure_time_tz"),
			goqu.I("flights.actual_departure_at"),
			goqu.I("flights.actual_departure_time_tz"),
			goqu.I("flights.arrival_airport"),
			goqu.I("flights.scheduled_arrival_at"),
			goqu.I("flights.scheduled_arrival_time_tz"),
			goqu.I("flights.expected_arrival_at"),
			goqu.I("flights.expected_arrival_time_tz"),
			goqu.I("flights.actual_arrival_at"),
			goqu.I("flights.actual_arrival_time_tz"),
			goqu.I("flights.aircraft"),
			goqu.I("flights.codeshares"),
			goqu.L("(concat(flights.scheduled_departure_at, ' ', regexp_replace(scheduled_departure_time_tz, '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\\1:\\2\\3\\4:\\5')))::timestamptz").As("connected_scheduled_departure_time_utc"),
			goqu.L("(concat(flights.scheduled_departure_at, ' ', regexp_replace(scheduled_departure_time_tz, '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\\1:\\2\\3\\4:\\5')))::timestamptz").As("scheduled_departure_time_utc"),
			goqu.V(1).As("row_number"),
		).
		From(goqu.S(schema).Table("flights")).
		Where(goqu.I("flights.flight_id").In(ids)).
		ToSQL()

	if err != nil {
		return nil, errors.Wrap(err, "schedules mysql repository: failed to build sql statement")
	}

	log.Debug(ctx, "Get SQL statement.", zap.String("statement", stmt), zap.Any("bindings", bindings))

	rows, err := r.db.Query(ctx, stmt, bindings...)
	if err != nil {
		return nil, errors.Wrap(err, "failed to query database")
	}
	defer rows.Close()

	fs := make([]*schedules.Flight, 0)

	for rows.Next() {
		var fpg flightPg
		if err := r.scan(rows, &fpg); err != nil {
			return nil, errors.Wrap(err, "cannot scan row")
		}
		f, err := fpg.toDomain()
		if err != nil {
			return nil, errors.Wrap(err, "cannot adapt flight to domain model")
		}
		fs = append(fs, f)
	}

	if err := rows.Err(); err != nil {
		return nil, errors.Wrap(err, "error encountered while iterating through rows")
	}

	return fs, nil
}

func (r *FlightsPostgresRepository) GetByFlightDesignator(ctx context.Context, in *schedules.FlightDesignatorQuery) (*schedules.Page[*schedules.Schedule], error) {
	schema := r.getSchemaFromTime(in.DepartureDate)
	pg := goqu.Dialect("postgres")

	stmt, args, err := pg.
		Select(
			goqu.I("flights.connecting_flight_id"),
			goqu.I("flights.id"),
			goqu.I("flights.flight_id"),
			goqu.I("flights.airline"),
			goqu.I("flights.flight_number"),
			goqu.I("flights.sequence"),
			goqu.I("flights.departure_airport"),
			goqu.I("flights.status"),
			goqu.I("flights.departure_status"),
			goqu.I("flights.arrival_status"),
			goqu.I("flights.scheduled_departure_at"),
			goqu.I("flights.scheduled_departure_time_tz"),
			goqu.I("flights.expected_departure_at"),
			goqu.I("flights.expected_departure_time_tz"),
			goqu.I("flights.actual_departure_at"),
			goqu.I("flights.actual_departure_time_tz"),
			goqu.I("flights.arrival_airport"),
			goqu.I("flights.scheduled_arrival_at"),
			goqu.I("flights.scheduled_arrival_time_tz"),
			goqu.I("flights.expected_arrival_at"),
			goqu.I("flights.expected_arrival_time_tz"),
			goqu.I("flights.actual_arrival_at"),
			goqu.I("flights.actual_arrival_time_tz"),
			goqu.I("flights.aircraft"),
			goqu.I("flights.codeshares"),
			goqu.L("(concat(flights.scheduled_departure_at, ' ', regexp_replace(scheduled_departure_time_tz, '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\\1:\\2\\3\\4:\\5')))::timestamptz").As("connected_scheduled_departure_time_utc"),
			goqu.L("(concat(flights.scheduled_departure_at, ' ', regexp_replace(scheduled_departure_time_tz, '([0-9]{2})([0-9]{2})([+-])([0-9]{2})([0-9]{2})', '\\1:\\2\\3\\4:\\5')))::timestamptz").As("scheduled_departure_time_utc"),
			goqu.L("1::int").As("row_number"),
		).
		From(goqu.S(schema).Table("flights")).
		Where(
			goqu.I("flights.scheduled_departure_at").Eq(in.DepartureDate),
			goqu.Or(
				goqu.And(
					goqu.I("flights.airline").Eq(in.Airline),
					goqu.I("flights.flight_number").Eq(in.Number),
				),
				goqu.L("? <@ flights.codeshare_designators", pgsql.Array([]string{fmt.Sprintf("%s%d", in.Airline, in.Number)})),
			),
		).
		Prepared(true).
		ToSQL()
	if err != nil {
		return nil, errors.Wrap(err, "failed to prepare sql statement")
	}

	log.Debug(ctx, "Get SQL statement.", zap.String("statement", stmt), zap.Any("bindings", args))

	rows, err := r.db.Query(ctx, stmt, args...)
	if err != nil {
		if e, ok := err.(*pgconn.PgError); ok && e.Code == "42P01" { // Missing Schema
			log.Error(ctx, "Schema not found.", zap.Any("request", in), zap.String("schema", schema), zap.Error(err))
			return &schedules.Page[*schedules.Schedule]{Items: make([]*schedules.Schedule, 0)}, nil
		}

		return nil, errors.Wrap(err, "failed to query database")
	}

	result := make([]flightPg, 0)
	distinctSchedules := make(map[int]bool)

	for rows.Next() {
		var fpg flightPg
		if err := r.scan(rows, &fpg); err != nil {
			return nil, errors.Wrap(err, "cannot scan row")
		}
		result = append(result, fpg)
		if _, found := distinctSchedules[fpg.RowNumber]; !found {
			distinctSchedules[fpg.RowNumber] = true
		}
	}

	var sch []*schedules.Schedule
	var connectedFlightsMap = make(map[int64]*schedules.Schedule)

	for _, fpg := range result {
		f, err := fpg.toDomain()
		if err != nil {
			return nil, errors.Wrap(err, "cannot adapt flight to domain model")
		}

		if f.ConnectingFlightID > 0 {
			if sc, found := connectedFlightsMap[f.ConnectingFlightID]; found {
				sc.ArrivalAirport = f.ArrivalAirport
				sc.Flights = append(sc.Flights, f)
			} else {
				sc = &schedules.Schedule{
					DepartureAirport: f.DepartureAirport,
					ArrivalAirport:   f.ArrivalAirport,
					MainCarrier:      f.Airline,
					FlightNumber:     f.FlightNumber,
					Flights:          []*schedules.Flight{f},
				}
				sch = append(sch, sc)
				connectedFlightsMap[f.ConnectingFlightID] = sc
			}
		} else {
			sc := &schedules.Schedule{
				DepartureAirport: f.DepartureAirport,
				ArrivalAirport:   f.ArrivalAirport,
				MainCarrier:      f.Airline,
				FlightNumber:     f.FlightNumber,
				Flights:          []*schedules.Flight{f},
			}
			sch = append(sch, sc)
		}
	}

