func (r *FlightsPostgresRepository) GetAirlineUniqueRoutes(ctx context.Context, airline string, departureDate time.Time) (*schedules.UniqueRoutes, error) {
	schema := r.getSchemaFromTime(departureDate)
	pg := goqu.Dialect("postgres")

	stmt, bindings, err := pg.
		Select(
			goqu.L("public.akeys(routes.points)"),
		).
		With(
			"routes",
			pg.Select(
				goqu.L("DISTINCT public.hstore(ARRAY[departure_airport, arrival_airport]::text[], ARRAY['true', 'true']::text[])").As("points"),
			).
				From(goqu.S(schema).Table("flights")).
				Where(
					goqu.And(
						goqu.L("flights.scheduled_departure_at::date = ?", departureDate),
						goqu.I("flights.airline").Eq(airline),
					),
				),
		).
		From("routes").
		ToSQL()

	if err != nil {
		return nil, errors.Wrap(err, "postgres repository: failed to prepare sql statement")
	}

	log.Debug(ctx, "Prepared SQL statement.", zap.String("statement", stmt), zap.Any("bindings", bindings))

	rows, err := r.db.Query(ctx, stmt, bindings...)
	if err != nil {
		if e, ok := err.(*pgconn.PgError); ok && e.Code == "42P01" { // Missing Schema
			log.Error(ctx, "Schema not found.", zap.Any("airline", airline), zap.Time("departure_date", departureDate), zap.String("schema", schema), zap.Error(err))
			return &schedules.UniqueRoutes{Airline: &schedules.Airline{Designator: airline}}, nil
		}

		return nil, errors.Wrap(err, "postgres repository: failed to query database")
	}

	result := &schedules.UniqueRoutes{
		Airline: &schedules.Airline{Designator: airline},
	}
	for rows.Next() {
		var rpg routePg
		if err := r.scanRoute(rows, &rpg); err != nil {
			return nil, errors.Wrap(err, "postgres repository: cannot scan row")
		}

		if rd, err := rpg.toDomain(); err == nil {
			result.Routes = append(result.Routes, rd)
		} else {
			log.Error(ctx, "Cannot convert row to domain.", zap.Any("pg_route", rpg), zap.Any("airline", airline), zap.Error(err))
			continue
		}
	}

	return result, nil
}

