func fromDateAndTimeWithTZ(date time.Time, timeTZ string) (time.Time, error) {
	return time.Parse("2006-01-02 1504-0700", date.Format("2006-01-02")+" "+timeTZ)
}

func (r *FlightsPostgresRepository) getSchemaFromTime(d time.Time) string {
	return fmt.Sprintf("period_%s", d.Format("2006_1"))
}

func (r *FlightsPostgresRepository) getSchemaFromFlightID(id string) string {
	rx := regexp.MustCompile("^([0-9]{4})([0-9]{2})")
	if !rx.MatchString(id) {
		return ""
	}

	groups := rx.FindAllStringSubmatch(id, -1)
	return fmt.Sprintf("period_%s_%s", groups[0][1], strings.TrimLeft(groups[0][2], "0"))
}

