)

type searchRequest struct {
	DepartureDate    string  `validate:"required,datetime=2006-01-02"`
	DepartureAirport string  `validate:"required"`
	ArrivalAirport   string  `validate:"required"`
	Limit            uint    `validate:"min=2,max=15"`
	NextPage         *string `validate:"omitempty,required,min=1"`
}

func (s *server) Search(ctx context.Context, in *pbv0.SearchRequest) (*pbv0.SearchResponse, error) {
	req := searchRequest{
		DepartureDate:    in.DepartureDate,
		DepartureAirport: in.DepartureAirport,
		ArrivalAirport:   in.ArrivalAirport,
		Limit:            uint(in.Limit),
		NextPage:         in.Cursor,
	}

	if err := s.validator.Struct(req); err != nil {
		log.Error(ctx, "Validation error.", zap.Any("request", req), zap.Error(err))
		message := i18n.GetFirstValidationErrorMessage(err)
		return nil, status.Errorf(codes.InvalidArgument, message)
	}

	start, err := time.Parse(time.DateOnly, in.DepartureDate)
	if err != nil {
		return nil, status.Error(codes.InvalidArgument, "Invalid departure date provided.")
	}

	if req.NextPage != nil && *req.NextPage == "" {
		req.NextPage = nil
	}

	page, err := s.svc.Search(ctx, &schedules.FlightSearchQuery{
		DepartureAirports: []string{req.DepartureAirport},
		ArrivalAirports:   []string{req.ArrivalAirport},
		DepartureDate:     start,
		Limit:             req.Limit,
		Cursor:            req.NextPage,
	})

	if err != nil {
		return nil, status.Error(codes.Internal, "Failed to retrieve schedules")
	}

	rSchs := make([]*pbv0.Schedule, 0, len(page.Items))
	for _, sc := range page.Items {
		rSchs = append(rSchs, s.toScheduleResource(sc))
	}

	return &pbv0.SearchResponse{
		Schedules: rSchs,
		Next:      page.Next,
	}, nil
}

