	}

	var actualDepartureAtPtr *time.Time
	if !f.ActualDepartureAt.IsZero() {
		actualDepartureAtPtr = &f.ActualDepartureAt
	}

	var actualArrivalAtPtr *time.Time
	if !f.ActualArrivalAt.IsZero() {
