		IATA:           a.IATA,
		ICAO:           a.ICAO,
		FAA:            a.FAA,
		Classification: int(a.Classification),
		Name:           i18n.Localise(a.Name, locale),
		ShortName:      i18n.Localise(a.ShortName, locale),
		Latitude:       a.Latitude,
		Longitude:      a.Longitude,
	}

	if a.ID != nil {
		apt.ID = *a.ID
	}

	if a.City != nil {
		apt.City = &City{
			ID:       a.City.ID,
			Name:     i18n.Localise(a.City.Name, locale),
			Timezone: a.City.Timezone,
