		Date     string                 `form:"filter[date]" binding:"required"`
		Limit    uint                   `form:"page[size],default=10" binding:"min=2,max=15"`
		NextPage *string                `form:"page[next]" binding:"omitempty,required,min=1"`
	}
)

func (t airportFlightBoardType) toDomain() schedules.FlightBoardType {
	switch t {
	case "arrivals":
		return schedules.FlightBoardTypeArrivals
	default:
		return schedules.FlightBoardTypeDepartures
	}
}

