		return
	}

	start, err := time.Parse(time.DateOnly, query.Date)
	if err != nil {
		_ = c.Error(ErrParsingDate)
		return
	}

	q := &schedules.AirportFlightBoardQuery{
		Airport: query.Airport,
		Date:    start,
		Type:    query.Type.toDomain(),
		Cursor:  query.NextPage,
		Limit:   query.Limit,
	}

	page, err := h.svc.GetAirportFlightBoard(c, q)
	if err != nil {
		_ = c.Error(err)
		return
	}

	locale := i18n.GetLocaleFromHeader(c.GetHeader("Accept-Language"))
	var items []*Flight
	for _, f := range page.Items {
