func (h *RestHandler) search(c *gin.Context) {
	var query flightSearchQuery
	if err := c.ShouldBindQuery(&query); err != nil {
		log.Error(c, "Cannot bind request.", zap.Error(err))
		_ = c.Error(ErrRequestBindingFailed)
		return
	}

	start, err := time.Parse(time.DateOnly, query.DepartureDate)
	if err != nil {
		_ = c.Error(ErrParsingDate)
		return
	}

	req := &schedules.FlightSearchQuery{
		DepartureDate: start,
		Limit:         query.Limit,
		Cursor:        query.NextPage,
	}

	if query.DepartureCity > 0 {
		req.DepartureCity = query.DepartureCity
	} else if query.DepartureAirport != "" {
		req.DepartureAirports = []string{query.DepartureAirport}
	}

	if query.ArrivalCity > 0 {
		req.ArrivalCity = query.ArrivalCity
	} else if query.ArrivalAirport != "" {
		req.ArrivalAirports = []string{query.ArrivalAirport}
	}

	if query.Airline != "" {
		req.Airline = &query.Airline
	}

	log.Debug(c, "New incoming search request.", zap.Any("request", req))

	page, err := h.svc.Search(c, req)

	if err != nil {
		log.Error(c, "Cannot search schedules.", zap.Any("request", req), zap.Error(err))
		_ = c.Error(err)
		return
	}

	locale := i18n.GetLocaleFromHeader(c.GetHeader("Accept-Language"))
	var items []*Schedule
	for _, s := range page.Items {
		items = append(items, h.toSchedule(s, locale))
	}

	c.PureJSON(
		http.StatusOK,
		xhttp.NewPaginatedResponse(items, "ok", "Here are the schedules matching the given criteria.", req.Limit, page.Next),
	)
}

