type batchFlightIdsRequest struct {
	IDs []string `form:"ids[]" binding:"required,unique,min=1,max=15,dive,gt=0"`
}

func (h *RestHandler) getFlightBatch(c *gin.Context) {
	locale := i18n.GetLocaleFromHeader(c.GetHeader("Accept-Language"))

	log.Debug(c, "Request detected locale.", zap.String("locale", locale.String()))

	request := batchFlightIdsRequest{}

	if err := c.ShouldBind(&request); err != nil {
		log.Error(c, "Cannot bind request.", zap.Error(err))
		_ = c.Error(ErrRequestBindingFailed)
		return
	}

	fs, err := h.svc.GetBatch(c, request.IDs)
	if err != nil {
		log.Error(c, "An error occurred while retrieving the flights.", zap.Strings("ids", request.IDs), zap.Error(err))
		_ = c.Error(err)
		return
	}

	frs := make([]*Flight, 0)
	for _, f := range fs {
		fr := h.toFlight(f, locale, true)
		frs = append(frs, fr)
	}

	c.PureJSON(
		http.StatusOK,
		xhttp.NewResponse(frs, "ok", "Here are the details of the requested batch of flights."),
	)
}

