	"net/http"
	"time"
)

type getAirlineRoutesUriRequest struct {
	ID string `uri:"id" binding:"required"`
}

func (h *RestHandler) getAirlineRoutes(c *gin.Context) {
	var uri getAirlineRoutesUriRequest
	if err := c.ShouldBindUri(&uri); err != nil {
		log.Error(c, "Cannot bind URI parameters.", zap.Error(err))
		message := i18n.GetFirstValidationErrorMessage(err)

		if message == "" {
			message = "The provided request is invalid."
		}

		c.JSON(http.StatusBadRequest, xhttp.NewErrorResponse[*UniqueRoutes]("invalid_parameters", message))
		return
	}

	routes, err := h.svc.GetAirlineUniqueRoutes(c, uri.ID, time.Now().UTC())
	if err != nil {
		_ = c.Error(err)
		return
	}

	locale := i18n.GetLocaleFromHeader(c.GetHeader("Accept-Language"))

	resp := &UniqueRoutes{
		Airline: h.toAirline(routes.Airline, locale),
	}

	for _, route := range routes.Routes {
		resp.Routes = append(resp.Routes, &Route{
			A: h.toAirport(route.A, locale),
			B: h.toAirport(route.B, locale),
		})
	}

	c.PureJSON(http.StatusOK, xhttp.NewResponse[*UniqueRoutes](resp, "ok", "Here are the unique routes for the requested airline."))
}

