package cursor

import (
	"encoding/base64"
	"encoding/hex"
	"encoding/json"
	"github.com/cockroachdb/errors"
)

type (
	cipher interface {
		Encrypt(input []byte) ([]byte, error)
		Decrypt(input []byte) ([]byte, error)
	}
	Builder[W, O any] interface {
		// GetWhereClause generates the where statement needed to implement
		// the cursor-based pagination given the provided cursor.
		// For more information about how a where clause is generated
		// visit https://brunoscheufler.com/blog/2022-01-01-paginating-large-ordered-datasets-with-cursor-based-pagination.
		GetWhereClause(c *Cursor) W

		// GetOrderByClause generates the order by statement needed to implement
		// the cursor-based pagination given the provided cursor.
		// For more information about how a where clause is generated
		// visit https://brunoscheufler.com/blog/2022-01-01-paginating-large-ordered-datasets-with-cursor-based-pagination.
		GetOrderByClause(c *Cursor) O
	}
)

type PaginatorConfig[W, O any] struct {
	cipher  cipher
	builder Builder[W, O]
}

// Option is a function that configures a Paginator.
type Option[W, O any] func(config *PaginatorConfig[W, O])

func WithEncryption[W, O any](cipher cipher) Option[W, O] {
	return func(cfg *PaginatorConfig[W, O]) {
		cfg.cipher = cipher
	}
}

func WithBuilder[W, O any](builder Builder[W, O]) Option[W, O] {
	return func(cfg *PaginatorConfig[W, O]) {
		cfg.builder = builder
	}
}

type Paginator[W, O any] struct {
	config *PaginatorConfig[W, O]
}

func NewPaginator[W, O any](opts ...Option[W, O]) *Paginator[W, O] {
	cfg := &PaginatorConfig[W, O]{}
	for _, opt := range opts {
		opt(cfg)
	}

	return &Paginator[W, O]{config: cfg}
}

func (p *Paginator[W, O]) Encode(cursor *Cursor) (string, error) {
	var b []byte
	var err error

	b, err = json.Marshal(cursor)
	if err != nil {
		return "", errors.Wrap(err, "cannot encode cursor")
	}

	var c string

	if p.config.cipher != nil {
		b, err = p.config.cipher.Encrypt(b)
		if err != nil {
			return "", errors.Wrap(err, "cannot encrypt cursor")
		}

		c = hex.EncodeToString(b)
	} else {
		c = base64.StdEncoding.EncodeToString(b)
	}

	return c, nil
}

func (p *Paginator[W, O]) Decode(cursor string) (*Cursor, error) {
	var (
		b   []byte
		err error
	)

	if p.config.cipher != nil {
		b, err = hex.DecodeString(cursor)
		if err != nil {
			return nil, errors.Wrap(err, "cannot decode cursor")
		}

		b, err = p.config.cipher.Decrypt(b)
		if err != nil {
			return nil, errors.Wrap(err, "cannot decrypt cursor")
		}
	} else {
		b, err = base64.StdEncoding.DecodeString(cursor)
		if err != nil {
			return nil, errors.Wrap(err, "cannot decode cursor")
		}
	}

	var c Cursor
	if err = json.Unmarshal(b, &c); err != nil {
		return nil, errors.Wrap(err, "cannot decode cursor")
	}

	return &c, nil
}

func (p *Paginator[W, O]) GetWhere(c *Cursor) W {
	return p.config.builder.GetWhereClause(c)
}

func (p *Paginator[W, O]) GetOrderBy(c *Cursor) O {
	return p.config.builder.GetOrderByClause(c)
}

