import "encoding/json"

type Direction int

const (
	DirectionAsc  Direction = 1
	DirectionDesc Direction = -1
)

func (d Direction) Reverse() Direction {
	switch d {
	case DirectionAsc:
		return DirectionDesc
	default:
		return DirectionAsc
	}
}

type Field struct {
	Name      string      `json:"n"`
	Value     interface{} `json:"v"`
	Direction Direction   `json:"d"`
}

type Cursor struct {
	Fields  []Field `json:"fs"`
	Limit   uint    `json:"l"`
	Forward bool    `json:"fw"`
}

func (c *Cursor) String() string {
	b, _ := json.Marshal(c)
	return string(b)
}

func NewCursor(forward bool, limit uint, fields ...Field) *Cursor {
	return &Cursor{Fields: fields, Limit: limit, Forward: forward}
}

func NewForwardCursor(limit uint, fields ...Field) *Cursor {
	return NewCursor(true, limit, fields...)
}

func NewBackwardCursor(limit uint, fields ...Field) *Cursor {
	return NewCursor(false, limit, fields...)
}

package cursor

import (
	"github.com/doug-martin/goqu/v9"
	"github.com/doug-martin/goqu/v9/exp"
)

type GoquBuilder string

func NewGoquBuilder() Builder[goqu.Expression, []exp.OrderedExpression] {
	return GoquBuilder("goqu")
}

func (a GoquBuilder) GetWhereClause(c *Cursor) goqu.Expression {
	var clauses []goqu.Expression

	for i, f := range c.Fields {

		if f.Value == nil {
			continue
		}

		var and []goqu.Expression

		for j := 0; j <= i; j++ {
			var e exp.Expression
			id := goqu.I(c.Fields[j].Name)

			if j != i {
				e = id.Eq(c.Fields[j].Value)
			} else {
				var direction Direction
				if c.Forward {
					direction = f.Direction
				} else {
					direction = f.Direction.Reverse()
				}

				if direction == DirectionAsc {
					e = id.Gt(c.Fields[j].Value)
				} else {
					e = id.Lt(c.Fields[j].Value)
				}
			}

			and = append(and, e)
		}

		clauses = append(clauses, goqu.And(and...))
	}

	if len(clauses) == 1 {
		return clauses[0]
	}

	return goqu.Or(clauses...)
}

func (a GoquBuilder) GetOrderByClause(c *Cursor) []exp.OrderedExpression {
	exps := make([]exp.OrderedExpression, len(c.Fields))

	for i, f := range c.Fields {
		var e exp.OrderedExpression

		if f.Direction == DirectionAsc {
			e = goqu.I(f.Name).Asc()
		} else {
			e = goqu.I(f.Name).Desc()
		}
		exps[i] = e
	}

	return exps
}

