package enc

import (
	"bytes"
	"crypto/aes"
	"crypto/cipher"
	"crypto/rand"
	"io"
)

type CBCCipher struct {
	key string
}

func NewCBCCipher(key string) *CBCCipher {
	return &CBCCipher{key: key}
}

func (c *CBCCipher) Encrypt(input []byte) ([]byte, error) {
	block, err := aes.NewCipher([]byte(c.key))
	if err != nil {
		return nil, err
	}

	data := c.pad(input)
	buffer := make([]byte, aes.BlockSize+len(data))
	iv := buffer[:aes.BlockSize]
	if _, err := io.ReadFull(rand.Reader, iv); err != nil {
		return nil, ErrCannotGenerateRandomIV
	}

	encrypter := cipher.NewCBCEncrypter(block, iv)
	encrypter.CryptBlocks(buffer[len(iv):], data)

	return buffer, nil
}

func (c *CBCCipher) Decrypt(input []byte) ([]byte, error) {
	block, err := aes.NewCipher([]byte(c.key))
	if err != nil {
		return nil, err
	}

	iv := input[0:aes.BlockSize]
	buffer := make([]byte, len(input)-len(iv))
	decrypter := cipher.NewCBCDecrypter(block, iv)
	decrypter.CryptBlocks(buffer, input[len(iv):])

	return c.unpad(buffer), nil
}

func (c *CBCCipher) pad(data []byte) []byte {
	padding := aes.BlockSize - len(data)%aes.BlockSize
	padtext := bytes.Repeat([]byte{byte(padding)}, padding)
	return append(data, padtext...)
}

func (c *CBCCipher) unpad(data []byte) []byte {
	length := len(data)
	padding := int(data[length-1])
	return data[:(length - padding)]
}

