// PostgresSQLSuite is a testcontainers manager for MySQL
type PostgresSQLSuite struct {
	suite.Suite

	container testcontainers.Container
	cfg       *containerConfig
	conn      *pgsql.DB
}

// Option is a function that configures a MySQLSuite
type Option func(*containerConfig)

// WithPort configures the port to use for the MySQL container
func WithPort(port int) Option {
	return func(cfg *containerConfig) {
		cfg.Port = &port
	}
}

// WithDB configures the database to use for the MySQL container
func WithDB(db string) Option {
	return func(cfg *containerConfig) {
		cfg.DB = db
	}
}

// WithUserPassword configures the user and password to use for the MySQL container
func WithUserPassword(user, password string) Option {
	return func(cfg *containerConfig) {
		cfg.User = user
		cfg.Password = password
	}
}

// Migrate configures the MySQL container to run migrations
func Migrate() Option {
	return func(cfg *containerConfig) {
		cfg.Migrate = true
	}
}

// NewPostgresSQLSuite creates a new MySQLSuite
func NewPostgresSQLSuite(opts ...Option) *PostgresSQLSuite {
	cfg := &containerConfig{
		Image:    "postgres:latest",
		Host:     "localhost",
		DB:       "test",
		User:     "test",
		Password: "test",
	}

	for _, opt := range opts {
		opt(cfg)
	}

	return &PostgresSQLSuite{
		cfg: cfg,
	}
}

type containerConfig struct {
	Image    string
	Host     string
	Port     *int
	DB       string
	User     string
	Password string
	Migrate  bool
}

// SetupSuite will be called once before the tests are run
// It starts the postgresql container
func (s *PostgresSQLSuite) SetupSuite() {
	ctx := context.Background()

	s.Require().NoError(s.startContainer(ctx))

	if s.cfg.Migrate {
		s.Require().NoError(pgsql.MigrateUp(s.GetDBConfig()))
	}
}

func (s *PostgresSQLSuite) GetDBConfig() *pgsql.Config {
	return &pgsql.Config{
		Host:     s.cfg.Host,
		Port:     *s.cfg.Port,
		DBName:   s.cfg.DB,
		User:     s.cfg.User,
		Password: s.cfg.Password,
	}
}

// TearDownSuite will be called once after the tests are run
func (s *PostgresSQLSuite) TearDownSuite() {
	ctx := context.Background()

	t := time.Second

	s.Require().NoError(s.container.Stop(ctx, &t))
	s.Require().NoError(s.container.Terminate(ctx))
}

// DB returns a db connection to the MySQL container
// creates one if it doesn't exist
func (s *PostgresSQLSuite) DB(ctx context.Context) *pgsql.DB {
	if s.conn == nil {
		conn, err := pgsql.Connect(ctx, s.GetDBConfig())
		s.Require().NoError(err)

		s.conn = conn
	}

	return s.conn
}

func (s *PostgresSQLSuite) startContainer(ctx context.Context) error {
	exposed := "5432"
	if s.cfg.Port != nil {
		exposed = fmt.Sprintf("%d:%s", *s.cfg.Port, exposed)
	}

	req := testcontainers.GenericContainerRequest{
		ContainerRequest: testcontainers.ContainerRequest{
			Image:        s.cfg.Image,
			ExposedPorts: []string{exposed},
			Env: map[string]string{
				"POSTGRES_DB":       s.cfg.DB,
				"POSTGRES_USER":     s.cfg.User,
				"POSTGRES_PASSWORD": s.cfg.Password,
			},
			WaitingFor: wait.ForListeningPort("5432"),
		},
		Started: true,
	}
	container, err := testcontainers.GenericContainer(ctx, req)
	if err != nil {
		return errors.Wrap(err, "failed to start postgres container")
	}

	s.container = container

	if s.cfg.Port == nil {
		port, err := container.MappedPort(ctx, "5432")
		if err != nil {
			return errors.Wrap(err, "cannot get postgres container mapped port")
		}
		p := port.Int()
		s.cfg.Port = &p
	}

	return nil
}

package concurrent

import "sync"

// Map is a concurrent map safe for concurrent use
type Map[T any] struct {
	m map[string]T

	mux *sync.Mutex
}

// NewMap creates a new concurrent map
func NewMap[T any]() *Map[T] {
	return &Map[T]{
		m:   make(map[string]T),
		mux: &sync.Mutex{},
	}
}

// Set sets a value in the map
func (c *Map[T]) Set(key string, value T) {
	defer c.mux.Unlock()
	c.mux.Lock()

	c.m[key] = value
}

// Get gets a value from the map
func (c *Map[T]) Get(key string) (T, bool) {
	defer c.mux.Unlock()
	c.mux.Lock()

	v, ok := c.m[key]
	return v, ok
}

func (c *Map[T]) Size() int {
	defer c.mux.Unlock()
	c.mux.Lock()

	return len(c.m)
}

package concurrent

import (
	"context"
	"sync"

	"go.impalastudios.com/log"
	"go.uber.org/zap"
)

// ExecuteInPool executes a function in a pool of 100 goroutines.
func ExecuteInPool[T any](ctx context.Context, objs []T, fn func(context.Context, T) error) []error {
	pool := make(chan struct{}, 60)
	for i := 0; i < 60; i++ {
		pool <- struct{}{}
	}

	var wg sync.WaitGroup
	wg.Add(len(objs))

	var errors []error

	for _, o := range objs {
		<-pool
		go func(ctx context.Context, a T) {
			defer func() {
				wg.Done()
				pool <- struct{}{}
			}()

			if err := fn(ctx, a); err != nil {
				log.Error(ctx, "Failed to execute fn", zap.Error(err))
				errors = append(errors, err)
			}
		}(context.Background(), o)
	}

	wg.Wait()

	return errors
}

