import "sync"

// AtomicInt64 is a thread-safe int64
type AtomicInt64 struct {
	i int64

	mux *sync.Mutex
}

// NewAtomicInt64 creates a new AtomicInt64
func NewAtomicInt64(i int64) *AtomicInt64 {
	return &AtomicInt64{
		i:   i,
		mux: &sync.Mutex{},
	}
}

// GetAndInc returns the value of the AtomicInt64 and increments it
func (i *AtomicInt64) GetAndInc() int64 {
	defer i.mux.Unlock()
	i.mux.Lock()

	v := i.i
	i.i++

	return v
}

// Add adds n to the AtomicInt64
func (i *AtomicInt64) Add(n int64) {
	defer i.mux.Unlock()
	i.mux.Lock()

	i.i += n
}

// Get returns the value of the AtomicInt64
func (i *AtomicInt64) Get() int64 {
	defer i.mux.Unlock()
	i.mux.Lock()

	return i.i
}

package reader

import "strings"

// Header represents the SSIM header representation
// The record has a standard length of 200 bytes broken into the following fields. The purpose of this
// record is to assure the users that the data set is being correctly read, and defines, where
// applicable, the number of seasons which follow.
type Header struct {
	// Always 1 (bytes 1-1)
	RecordType string
	// Always reads AIRLINE STANDARD SCHEDULE DATA SET (bytes 2-35)
	TitleOfContents string
	// Optional: (bytes 41-41)
	NumberOfSeasons string
	// Mandatory: (bytes 192-194)
	DatasetSerialNumber string
	// Always 000001
	RecordSerialNumber string
}

const (
	headerRecordType         = "1"
	headerTitleOfContents    = "AIRLINE STANDARD SCHEDULE DATA SET"
	headerRecordSerialNumber = "000001"
)

// ReadHeader parses a header row
func ReadHeader(row string) *Header {
	numberOfSeasons := strings.TrimSpace(row[40:41])
	datasetSerialNumber := strings.TrimSpace(row[191:194])

	return &Header{
		RecordType:          headerRecordType,
		TitleOfContents:     headerTitleOfContents,
		NumberOfSeasons:     numberOfSeasons,
		DatasetSerialNumber: datasetSerialNumber,
		RecordSerialNumber:  headerRecordSerialNumber,
	}
}

package reader

import "strings"

// Trailer is a struct that represents the SSIM trailer
// The record defines the end of the data under the preceding Carrier Record. Further Carrier/Trailer
// Record combinations may appear on this data set. The record has a standard length of 200 bytes
// broken into the following fields:
type Trailer struct {
	// Always 5 (bytes 1-1)
	RecordType string
	// Mandatory: (bytes 3-5)
	AirlineDesignator string
	// Optional: Release (Sell) Date (bytes 6-12)
	ReleaseDate string
	// Mandatory: 6-digit numeric Serial Number. Equal to the Record
	// Serial Number of the previous record irrespective of its
	// Record Type and one less than the Record Serial Number of this Trailer
	// Record (bytes 188193).
	SerialNumberCheckReference string
	// Mandatory: C or E (bytes 194-194)
	ContinuationEndCode string
	// Mandatory: Sequential to previous record irrespective of its Record Type 0 filled. Right justified.
	// See Chapter 2 Record Serial Number description if record count exceeds 999999. (bytes 195-200)
	RecordSerialNumber string
}

const trailerRecordType = "5"

// ReadTrailer parses a trailer row
func ReadTrailer(row string) *Trailer {
	airlineDesignator := strings.TrimSpace(row[2:5])
	releaseDate := strings.TrimSpace(row[5:12])
	serialNumberCheckReference := row[187:193]
	continuationEndCode := row[193:194]
	recordSerialNumber := strings.TrimSpace(row[194:200])

	return &Trailer{
		RecordType:                 trailerRecordType,
		AirlineDesignator:          airlineDesignator,
		ReleaseDate:                releaseDate,
		SerialNumberCheckReference: serialNumberCheckReference,
		ContinuationEndCode:        continuationEndCode,
		RecordSerialNumber:         recordSerialNumber,
	}
}

package reader

import "strings"

// Period is a struct to represent from and to dates
type Period struct {
	From string
	To   string
}

// Carrier is a struct that represents the SSIM carrier
// The record gives an indication of the period(s) of applicability of the schedules that follow on
// subsequent records. The record has the standard length of 200 bytes broken into the following fields:
type Carrier struct {
	// Always 2
	RecordType string
	// Mandatory: U = UTC, L = Local time (bytes 2-2)
	TimeMode string
	// Mandatory: 3-letter IATA code (bytes 3-5)
	AirlineDesignator string
	// Optional: (bytes 11-13)
	Season string
	// Mandatory: First and last date of the schedule contained within this Carrier/Trailer
	// Record. Shown as day, month, year in the time mode as specified.
	// (bytes 15-28) ... from schedule (15-21), to schedule (22-28)
	PeriodOfScheduleValidity *Period
	// Mandatory: Day, month, year of data set creation (e.g. 01APR90) (bytes 29-35)
	CreationDate string
	// Optional: Free format, blank fill e.g. SAS IATA DRAFT S90 (bytes 36-64)
	TitleOfData string
	// Optional: Day, month, year or blank fill (bytes 65-71)
	ReleaseDate string
	// Mandatory: P or C (bytes 72-72)
	ScheduleStatus string
	// Optional: (bytes 73-107)
	CreatorReference string
	// Conditional: (bytes 108-108)
	DuplicateAirlineDesignatorMarker string
	// Optional: (bytes 109-169)
	GeneralInformation string
	// Optional: The format is as defined in defaults Chapter 2, except that the DEI (503) is not required. (bytes 170-188)
	InFlightServiceInformation string
	// Optional: EN = default for Carrier is that flight legs are Not Electronic Ticketing Candidates ET = default for Carrier is that
	// flight legs are Electronic Ticketing Candidates. (bytes 189-190)
	ElectronicTicketingInformation string
	// Mandatory: Hours, minutes of data set creation, e.g. 1346. (bytes 191-194)
	CreationTime string
	// Mandatory: Numeric. One greater than the previous record which must have been either a Header Record or a Trailer
	// Record. Zero fill. Right justified. See Chapter 2 Record Serial Number description if record count exceeds 999999.
	// (bytes 195-200)
	RecordSerialNumber string
}

const carrierRecordType = "2"

// ReadCarrier parses a carrier row
func ReadCarrier(row string) *Carrier {
	timeMode := strings.TrimSpace(row[1:2])
	airlineDesignator := strings.TrimSpace(row[2:5])
	season := strings.TrimSpace(row[10:13])
	periodOfScheduleValidity := &Period{
		From: row[14:21],
		To:   row[21:28],
	}
	creationDate := strings.TrimSpace(row[28:35])
	titleOfData := strings.TrimSpace(row[35:64])
	releaseDate := strings.TrimSpace(row[64:71])
	scheduleStatus := strings.TrimSpace(row[71:72])
	creatorReference := strings.TrimSpace(row[72:107])
	duplicateAirlineDesignatorMarker := strings.TrimSpace(row[107:108])
	generalInformation := strings.TrimSpace(row[108:169])
	inFlightServiceInformation := strings.TrimSpace(row[169:188])
	electronicTicketingInformation := strings.TrimSpace(row[188:190])
	creationTime := strings.TrimSpace(row[190:194])
	recordSerialNumber := strings.TrimSpace(row[194:200])

	return &Carrier{
		RecordType:                       carrierRecordType,
		TimeMode:                         timeMode,
		AirlineDesignator:                airlineDesignator,
		Season:                           season,
		PeriodOfScheduleValidity:         periodOfScheduleValidity,
		CreationDate:                     creationDate,
		TitleOfData:                      titleOfData,
		ReleaseDate:                      releaseDate,
		ScheduleStatus:                   scheduleStatus,
		CreatorReference:                 creatorReference,
		DuplicateAirlineDesignatorMarker: duplicateAirlineDesignatorMarker,
		GeneralInformation:               generalInformation,
		InFlightServiceInformation:       inFlightServiceInformation,
		ElectronicTicketingInformation:   electronicTicketingInformation,
		CreationTime:                     creationTime,
		RecordSerialNumber:               recordSerialNumber,
	}
}

package reader

import "strings"

// RecordType returns the record type of a row
// The record type is the first character of a row
// Which can be 1, 2, 3, 4 or 5
func RecordType(row string) string {
	return row[0:1]
}

// IsHeader returns true if the row is a header row
func IsHeader(row string) bool {
	return RecordType(row) == headerRecordType
}

// IsCarrier returns true if the row is a carrier row
func IsCarrier(row string) bool {
	return RecordType(row) == carrierRecordType
}

// IsLeg returns true if the row is a leg row
func IsLeg(row string) bool {
	return RecordType(row) == legRecordType
}

// IsSegment returns true if the row is a segment row
func IsSegment(row string) bool {
	return RecordType(row) == segmentRecordType
}

// IsTrailer returns true if the row is a trailer row
func IsTrailer(row string) bool {
	return RecordType(row) == trailerRecordType
}

const zeroRecordPrefix = "0000000000000"

// IsZero returns true if the row is a zero row
func IsZero(row string) bool {
	return strings.HasPrefix(row, zeroRecordPrefix)
}

package reader

import (
	"strconv"
	"strings"

	"github.com/cockroachdb/errors"
)

// Leg represents the SSIM leg representation
// The record(s) gives schedule details leg by leg for each Flight Designator. The record has a
// standard length of 200 bytes broken into the following fields:
type Leg struct {
	// Always 3 (bytes 1-1)
	RecordType string
	// Conditional: (bytes 2-2)
	OperationalSuffix string
	// Mandatory: (bytes 3-5)
	AirlineDesignator string
	// Mandatory: (bytes 6-9)
	FlightNumber int
	// Mandatory: Number between 01 and 99 (bytes 10-11)
	ItineraryVariationIdentifier int8
	// Mandatory: Number between 01 and 99, sequencing continuous flight legs as they operate within
	// each Itinerary Variation Identifier. (bytes 12-13)
	LegSequenceNumber int8
	// Mandatory: Alpha (bytes 14-14)
	ServiceType string
	// Mandatory: Day, month, year This field (from) bytes 15-21 applies to the aircraft STD (to) bytes 22-28 and must be compatible with
	// the Time Mode in byte 2 of Record Type 2. (bytes 15-28)
	PeriodOfOperation *Period
	// Mandatory: This field applies to the aircraft STD and must be compatible with the Time Mode in byte 2 of Record Type 2. This
	// field is blank filled, for nonoperational days. (bytes 29-35)
	DaysOfOperation string
	// Conditional: (bytes 36-36)
	FrequencyRate string
	// Mandatory: 3-character IATA code (bytes 37-39)
	DepartureStation string
	// Mandatory: This field must be compatible with the Time Mode in byte 2 of Record Type 2. Although this time will nearly always be
	// the same as aircraft STA it must be completed. (bytes 40-43)
	DepartureScheduledTimeOfPassenger string
	// Mandatory: This field must be compatible with the Time Mode in byte 2 of Record Type 2. (bytes 44-47)
	DepartureScheduledTimeOfAircraft string
	// Mandatory: Hours and Minutes variation from UTC (see Appendix F) (bytes 48-52)
	DepartureUTCLocalTimeVariation string
	// Conditional: Alphanumeric (bytes 53-54)
	DeparturePassengerTerminal string
	// Mandatory: 3-character IATA code (bytes 55-57)
	ArrivalStation string
	// Mandatory: This field must be compatible with the Time Mode in byte 2 of Record Type 2. (bytes 58-61)
	ArrivalScheduledTimeOfAircraft string
	// Mandatory: This field must be compatible with the Time Mode in byte 2 of Record Type 2. Although this time will nearly always be
	// the same as aircraft STA it must be completed. (bytes 62-65)
	ArrivalScheduledTimeOfPassenger string
	// Mandatory: Hours and Minutes variation from UTC (see Appendix F) (bytes 66-70)
	ArrivalUTCLocalTimeVariation string
	// Conditional: Alphanumeric (bytes 71-72)
	ArrivalPassengerTerminal string
	// Mandatory: ATA/IATA Aircraft Type. See Appendix A. (bytes 73-75)
	AircraftType string
	// Conditional: Note: Either this field or the Aircraft Configuration/Version (in bytes 173192) is mandatory.
	// (bytes 76-95)
	PassengerReservationsBookingDesignator string
	// Conditional: Blank fill by Passenger Booking Designator class (bytes 96-100)
	PassengerReservationsBookingModifier string
	// Optional: Blank fill by Passenger Reservations Booking Designator class (bytes 101-110)
	MealServiceNote string
	// Conditional: In the case of 2 character Designators Airline Designators bytes 113 and/or 116 and/or 119 must
	// be blank. Left justify and blank fill if fewer than three carriers. (bytes 111-119)
	JointOperationAirlineDesignators string
	// Optional: Two character combination of D and/or I Position 120 is leg departure status Position 121 is leg arrival status
	// (bytes 120-121)
	MinimumConnectingTimeInternationalDomesticStatus string
	// Optional: S if subject to regulations (bytes 122-122)
	SecureFlightIndicator string
	// Conditional: (bytes 128-128)
	ItineraryVariationIdentifierOverflow string
	// Conditional: (bytes 129-131)
	AircraftOwner string
	// Conditional: (bytes 132-134)
	CockpitCrewEmployer string
	// Conditional: (bytes 135-137)
	CabinCrewEmployer string
	// Conditional: (bytes 145-145)
	AircraftRotationLayover string
	// Conditional: (bytes 146-146)
	// OperationalSuffix       string
	// Conditional: (bytes 148-148)
	FlightTransitLayover string
	// Conditional: Code Share (DEI 2) or Operating Airline Disclosure  Shared Airline or Wet Lease Designation (DEI 9)
	// (bytes 149-149)
	OperatingAirlineDisclosure string
	// Conditional: (bytes 150-160)
	TrafficRestrictionCode string
	// Conditional: (bytes 161-161)
	TrafficRestrictionCodeLegOverflowIndicator string
	// Conditional: Note: Either this field or the Passenger Reservations Booking Designator (bytes 7695) is mandatory
	// (bytes 173-192)
	AircraftConfigurationVersion string
	// Optional: (bytes 193-194)
	DateVariation string
	// Mandatory: 0 filled and sequential to previous record irrespective of its Record Type. See Chapter 2 Record
	// Serial Number description if record count exceeds 999999. (bytes 195-200)
	RecordSerialNumber string
}

const legRecordType = "3"

// ContainsPassengers returns true if the leg is carrying passengers
// The Service Type field is a single character which indicates the type of service being provided on the leg. The
// following codes are used for passenger services:
// J, S, G, B, C
func (l *Leg) ContainsPassengers() bool {
	return l.ServiceType == "J" ||
		l.ServiceType == "S" ||
		l.ServiceType == "G" ||
		l.ServiceType == "B" ||
		l.ServiceType == "C"
}

package reader

import (
	"strconv"
	"strings"

	"github.com/cockroachdb/errors"
)

// Segment represents the SSIM segment representation
// The record(s) specifies the information applicable to a unique Flight Leg Record as specified in
// bytes 0214.
// Although no order is prescribed when multiple Data Element Identifiers follow the same Flight Leg
// Record, the following is recommended:
//  when multiple Segment Data Records apply to different Off Points, the Segment Data Records
// should be ordered according to the occurrence of the Off Point in the itinerary;
//  if multiple Segment Data Records apply to the same Off Point, they should appear together
// and be ordered according to the numeric sequence of the Data Element Identifiers starting with
// the lowest number.
// However, systems should be able to process data elements in any order.
// The record has a standard length of 200 bytes broken into the following fields:
type Segment struct {
	// Always 4 (bytes 1-1)
	RecordType string
	// Conditional: (bytes 2-2)
	OperationalSuffix string
	// Mandatory: (bytes 3-5)
	AirlineDesignator string
	// Mandatory: (bytes 6-9)
	FlightNumber int
	// Mandatory: Number between 01 and 99 (bytes 10-11)
	ItineraryVariationIdentifier int8
	// Mandatory: Number between 01 and 99, sequencing continuous flight legs as they operate within
	// each Itinerary Variation Identifier. (bytes 12-13)
	LegSequenceNumber int8
	// Mandatory: Alpha (bytes 14-14)
	ServiceType string
	// Conditional: (bytes 28-28)
	ItineraryVariationIdentifierOverflow string
	// Mandatory: Alpha (bytes 29-29)
	BoardPointIndicator string
	// Mandatory: Alpha (bytes 30-30)
	OffPointIndicator string
	// Mandatory: Right justify, zero fill. (bytes 31-33)
	DataElementIdentifier string
	// Mandatory: 3-character IATA code (bytes 34-36)
	// When board point and off point merged the field called segment
	BoardPoint string
	// Mandatory: 3-character IATA code (bytes 37-39)
	OffPoint string
	// Conditional: Associated with Data Element Identifier. The format for each data element is defined in Chapter 2.
	// Blank fill. (bytes 40-194)
	Data string
	// Mandatory: Sequential to previous record irrespective of its Record Type. 0 filled. Right justified.
	// See Chapter 2 Record Serial Number description if record count exceeds 999999.
	// (bytes 195-200)
	RecordSerialNumber string
}

const segmentRecordType = "4"

// ReadSegment parses a segment row
func ReadSegment(row string) (*Segment, error) {
	operationalSuffix := strings.TrimSpace(row[1:2])
	airlineDesignator := strings.TrimSpace(row[2:5])

	flightNumber, err := strconv.Atoi(strings.TrimSpace(row[5:9]))
	if err != nil {
		return nil, errors.Wrapf(err, "reader: failed to convert segment flight numbers")
	}

	itineraryVariationIdentifier, err := strconv.Atoi(strings.TrimSpace(row[9:11]))
	if err != nil {
		return nil, errors.Wrap(err, "reader: failed to convert segment itinerary variation identifier")
	}

	legSequenceNumber, err := strconv.Atoi(strings.TrimSpace(row[11:13]))
	if err != nil {
		return nil, errors.Wrap(err, "reader: failed to convert segment leg sequence number")
	}

	serviceType := strings.TrimSpace(row[13:14])
	itineraryVariationIdentifierOverflow := strings.TrimSpace(row[27:28])
	boardPointIndicator := strings.TrimSpace(row[28:29])
	offPointIndicator := strings.TrimSpace(row[29:30])
	dataElementIdentifier := strings.TrimSpace(row[30:33])
	boardPoint := strings.TrimSpace(row[33:36])
	offPoint := strings.TrimSpace(row[36:39])
	data := strings.TrimSpace(row[39:194])
	recordSerialNumber := strings.TrimSpace(row[194:200])

	return &Segment{
		RecordType:                           segmentRecordType,
		OperationalSuffix:                    operationalSuffix,
		AirlineDesignator:                    airlineDesignator,
		FlightNumber:                         flightNumber,
		ItineraryVariationIdentifier:         int8(itineraryVariationIdentifier),
		LegSequenceNumber:                    int8(legSequenceNumber),
		ServiceType:                          serviceType,
		ItineraryVariationIdentifierOverflow: itineraryVariationIdentifierOverflow,
		BoardPointIndicator:                  boardPointIndicator,
		OffPointIndicator:                    offPointIndicator,
		DataElementIdentifier:                dataElementIdentifier,
		BoardPoint:                           boardPoint,
		OffPoint:                             offPoint,
		Data:                                 data,
		RecordSerialNumber:                   recordSerialNumber,
	}, nil
}

package diff

import (
	"context"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
)

func (d *Diff) compareFlights(_ context.Context, old, new *parser.CarrierTrailerBlock) {
	oldFlights := mapFlights(old)
	newFlights := mapFlights(new)

	for flightID, newFlightsMap := range newFlights {
		oldFlightsMap, exists := oldFlights[flightID]
		// If the flightID does not exist in the old flights, then all the flights are new
		// So we add all the flights to the added list
		if !exists {
			for _, newF := range newFlightsMap {
				d.Flights.Added[hashDuplicateFlight(newF)] = newF
			}
			continue
		}

		// If the flightID exists in the old flights, then we need to compare the flights
		for hash, newF := range newFlightsMap {
			oldF, exists := oldFlightsMap[hash]
			// If the hash does not exist in the old flights, then that means the flight schedule has been updated
			// So we add the flight to the added list but we also need to remove the old flight
			// The old flight can not be removed here because we don't know it's hash
			if !exists {
				d.Flights.Added[hashDuplicateFlight(newF)] = newF
			} else {
				if newF.Equals(oldF) {
					d.Flights.NoChanges[hashDuplicateFlight(newF)] = oldF
				} else {
					d.Flights.Removed[hashDuplicateFlight(oldF)] = oldF
					d.Flights.Added[hashDuplicateFlight(newF)] = newF
				}
			}
		}

		// We need to check if there are any flights that have been removed
		// We can do this by checking if the hash of the old flight exists in the new flights
		// If it does not exist, then the flight has been removed
		for hash, oldF := range oldFlightsMap {
			// If the hash does not exist in the new flights, then the flight has been removed
			// So we add the flight to the removed list and remove it from the old flights
			_, exists := newFlightsMap[hash]
			if !exists {
				d.Flights.Removed[hashDuplicateFlight(oldF)] = oldF
			}
		}
	}

	// Detect and handle zombie flights; stay within the time window of new flights
	minD, maxD := deriveWindowFromNewFlights(new)

	oldW := filterCTByWindow(old, minD, maxD)
	oldFlightsFiltered := mapFlights(oldW)

	for flightID, oldFlightsMap := range oldFlightsFiltered {
		if _, exists := newFlights[flightID]; !exists {
			for _, of := range oldFlightsMap {
				key := hashDuplicateFlight(of)
				if _, seen := d.Flights.Removed[key]; !seen {
					d.Flights.Zombie[key] = of
					d.Flights.Removed[key] = of
				}
			}
		}
	}
}

func hashDuplicateFlight(f *parser.Flight) string {
	return f.FlightID
}

func mapFlights(ct *parser.CarrierTrailerBlock) map[string]map[string]*parser.Flight {
	m := make(map[string]map[string]*parser.Flight)
	for _, f := range ct.Flights {
		if duplicatedFlights, ok := m[f.FlightID]; ok {
			duplicatedFlights[hashDuplicateFlight(f)] = f
		} else {
			m[f.FlightID] = make(map[string]*parser.Flight)
			m[f.FlightID][hashDuplicateFlight(f)] = f
		}
	}
	return m
}

package diff

import (
	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
	"sort"
	"time"
)

func dayOf(t time.Time) time.Time {
	y, m, d := t.Date()
	return time.Date(y, m, d, 0, 0, 0, 0, time.UTC)
}

func deriveWindowFromNewFlights(new *parser.CarrierTrailerBlock) (time.Time, time.Time) {
	if len(new.Flights) == 0 {
		return dayOf(new.From), dayOf(new.To)
	}

	minD := dayOf(new.Flights[0].ScheduledDepartureAt)
	maxD := minD

	for _, f := range new.Flights[1:] {
		d := dayOf(f.ScheduledDepartureAt)
		if d.Before(minD) {
			minD = d
		}
		if d.After(maxD) {
			maxD = d
		}
	}
	return minD, maxD
}

func inWindowDate(t, from, to time.Time) bool {
	d := dayOf(t)
	return (d.Equal(from) || d.After(from)) && (d.Equal(to) || d.Before(to))
}

func filterCTByWindow(src *parser.CarrierTrailerBlock, from, to time.Time) *parser.CarrierTrailerBlock {
	out := &parser.CarrierTrailerBlock{
		Carrier: src.Carrier,
		Trailer: src.Trailer,
		From:    from,
		To:      to,
	}

	out.Flights = make([]*parser.Flight, 0, len(src.Flights))
	for _, f := range src.Flights {
		if inWindowDate(f.ScheduledDepartureAt, from, to) {
			out.Flights = append(out.Flights, f)
		}
	}

	out.ConnectingFlights = make([]*parser.ConnectingFlight, 0, len(src.ConnectingFlights))
	for _, cf := range src.ConnectingFlights {
		kept := make([]*parser.Flight, 0, len(cf.Flights))
		for _, lf := range cf.Flights {
			if inWindowDate(lf.ScheduledDepartureAt, from, to) {
				kept = append(kept, lf)
			}
		}
		if len(kept) > 0 {
			sort.SliceStable(kept, func(i, j int) bool { return kept[i].Sequence < kept[j].Sequence })
			copyCF := *cf
			copyCF.Flights = kept
			out.ConnectingFlights = append(out.ConnectingFlights, &copyCF)
		}
	}
	return out
}

package diff

import (
	"context"
	"go.impalastudios.com/log"
	"go.uber.org/zap"

	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
)

// Diff contains the result of the diff computation
type Diff struct {
	MergedCT          *parser.CarrierTrailerBlock
	Flights           *Comparison[*parser.Flight]
	ConnectingFlights *Comparison[*parser.ConnectingFlight]
}

// Compare compares two carrier trailer blocks
func Compare(ctx context.Context, old, new *parser.CarrierTrailerBlock) (*Diff, error) {
	mergedCT := &parser.CarrierTrailerBlock{
		Carrier: new.Carrier, // we only care about the new carrier period to compare removed flights
		Trailer: new.Trailer,
		From:    new.From,
		To:      new.To,
	}

	log.Debug(ctx, "Comparing the new carrier trailer block period", zap.Any("carrier", new.Carrier), zap.Any("period", new.Carrier.PeriodOfScheduleValidity))

	diff := &Diff{
		Flights: &Comparison[*parser.Flight]{
			NoChanges: make(map[string]*parser.Flight),
			Removed:   make(map[string]*parser.Flight),
			Added:     make(map[string]*parser.Flight),
			Zombie:    make(map[string]*parser.Flight),
		},
		ConnectingFlights: &Comparison[*parser.ConnectingFlight]{
			NoChanges: make(map[string]*parser.ConnectingFlight),
			Removed:   make(map[string]*parser.ConnectingFlight),
			Added:     make(map[string]*parser.ConnectingFlight),
			Zombie:    make(map[string]*parser.ConnectingFlight),
		},
	}

	diff.compareFlights(ctx, old, new)
	diff.compareConnectingFlights(ctx, old, new)

	mergedCT.Flights = mapValues(diff.Flights.Added)
	mergedCT.ConnectingFlights = mapValues(diff.ConnectingFlights.Added)
	diff.MergedCT = mergedCT
	return diff, nil
}

func mapValues[T any](m map[string]T) []T {
	values := make([]T, 0)
	for _, val := range m {
		values = append(values, val)
	}

	return values
}

// Comparison contains the result of the comparison
type Comparison[T any] struct {
	NoChanges map[string]T
	Removed   map[string]T
	Added     map[string]T
	Zombie    map[string]T
}

package diff

import (
	"context"

	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
	"go.impalastudios.com/log"
	"go.uber.org/zap"
)

func (d *Diff) compareConnectingFlights(ctx context.Context, old, new *parser.CarrierTrailerBlock) {
	oldCF := mapConnectingFlights(old)
	newCF := mapConnectingFlights(new)

	for id, n := range newCF {
		o, exists := oldCF[id]
		if !exists {
			d.resolveFlightDuplicationDueToConnectingFlightUpdate(ctx, n)
			d.ConnectingFlights.Added[id] = n
			continue
		}
		if !o.Equals(n) {
			d.resolveFlightDuplicationDueToConnectingFlightUpdate(ctx, n)
			d.ConnectingFlights.Added[id] = n
			d.ConnectingFlights.Removed[id] = o
		} else {
			d.ConnectingFlights.NoChanges[id] = o
		}
	}

	// Detect and handle zombie connected flights; stay within the time window of new connected flights
	minD, maxD := deriveWindowFromNewFlights(new)

	oldW := filterCTByWindow(old, minD, maxD)
	oldCFFiltered := mapConnectingFlights(oldW)

	for id, o := range oldCFFiltered {
		if _, exists := newCF[id]; !exists {
			if _, seen := d.ConnectingFlights.Removed[id]; !seen {
				d.ConnectingFlights.Zombie[id] = o
				d.ConnectingFlights.Removed[id] = o
			}
		}
	}
}

// Sometimes connecting flights are drastically updated
// For ex. There is an addition of a new flight in the connecting flight
// In such cases, we need to update the connecting flight
// We need to check in the no changes map if the new flights are present
// If they are present then we need to add them to removed map
// Because when we create new connecting flight the flights will be duplicated
func (d *Diff) resolveFlightDuplicationDueToConnectingFlightUpdate(ctx context.Context, cf *parser.ConnectingFlight) {
	for _, f := range cf.Flights {
		flightHash := hashDuplicateFlight(f)
		if oldF, ok := d.Flights.NoChanges[flightHash]; ok {
			log.Warn(ctx, "Resolved flight duplication due to connecting flight update",
				zap.Any("connecting_flight", cf),
				zap.Any("old_flight", oldF),
				zap.Any("new_flight", f),
			)
			d.Flights.Removed[flightHash] = oldF
		}
	}
}

func mapConnectingFlights(ct *parser.CarrierTrailerBlock) map[string]*parser.ConnectingFlight {
	m := make(map[string]*parser.ConnectingFlight)
	for _, cf := range ct.ConnectingFlights {
		m[cf.ConnectingFlightID] = cf
	}

	return m
}

