package parser

import (
	"context"
	"fmt"
	"strconv"
	"strings"
	"time"

	"github.com/golang-collections/collections/set"
	"github.com/pkg/errors"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/reader"
)

// Flight is a flight scheduled for some time
type Flight struct {
	ID                   int64     `json:"id,omitempty"`
	FlightID             string    `json:"flight_id"`
	ConnectingFlightID   int64     `json:"connecting_flight_id,omitempty"`
	Airline              string    `json:"airline"`
	FlightNumber         int       `json:"flight_number"`
	Itinerary            int8      `json:"itinerary,omitempty"`
	Sequence             int8      `json:"sequence,omitempty"`
	DepartureAirport     string    `json:"departure_airport"`
	ArrivalAirport       string    `json:"arrival_airport"`
	Aircraft             string    `json:"aircraft"`
	ScheduledDepartureAt time.Time `json:"scheduled_departure_at"`
	ExpectedDepartureAt  time.Time `json:"expected_departure_at"`
	ActualDepartureAt    time.Time `json:"actual_departure_at"`
	ScheduledArrivalAt   time.Time `json:"scheduled_arrival_at"`
	ExpectedArrivalAt    time.Time `json:"expected_arrival_at"`
	ActualArrivalAt      time.Time `json:"actual_arrival_at"`
	// There is a default value for FlightStatus in DB
	// DepartureStatus, ArrivalStatus values are nullable
	// Values for the FlightStatus SCHEDULED, CANCELLED, DIVERTED, LANDED, AIRBORNE
	// The default value by the DB is SCHEDULED
	Status          string `json:"-"`
	DepartureStatus string `json:"-"`
	ArrivalStatus   string `json:"-"`

	Codeshares []*Codeshare `json:"codeshares,omitempty"`
}

func (f *Flight) Equals(other *Flight) bool {
	if f.Airline != other.Airline {
		return false
	}

	if f.FlightNumber != other.FlightNumber {
		return false
	}

	if f.DepartureAirport != other.DepartureAirport {
		return false
	}

	if f.ArrivalAirport != other.ArrivalAirport {
		return false
	}

	if f.Itinerary != other.Itinerary {
		return false
	}

	if f.Sequence != other.Sequence {
		return false
	}

	if f.Aircraft != other.Aircraft {
		return false
	}

	if !f.ScheduledDepartureAt.Equal(other.ScheduledDepartureAt) {
		return false
	}

	if !f.ScheduledArrivalAt.Equal(other.ScheduledArrivalAt) {
		return false
	}

	cs := set.New()
	otherCs := set.New()

	for _, c := range f.Codeshares {
		cs.Insert(fmt.Sprintf("%s%d", c.Airline, c.FlightNumber))
	}

	for _, c := range other.Codeshares {
		otherCs.Insert(fmt.Sprintf("%s%d", c.Airline, c.FlightNumber))
	}

	if cs.Intersection(otherCs).Len() != otherCs.Len() || otherCs.Intersection(cs).Len() != cs.Len() {
		return false
	}

	return true
}

// NewFlight creates a new flight from a leg
func NewFlight(l *reader.Leg, date time.Time) (*Flight, error) {
	departureAt, err := buildFlightDatetime(&date, l.DepartureScheduledTimeOfPassenger, l.DepartureUTCLocalTimeVariation)
	if err != nil {
		return nil, err
	}

	arrivalAt, err := buildFlightDatetime(&date, l.ArrivalScheduledTimeOfPassenger, l.ArrivalUTCLocalTimeVariation)
	if err != nil {
		return nil, err
	}

	arrivalAdditionalDuration, err := toArrivalDateVariationDuration(l.DateVariation)
	if err != nil {
		return nil, err
	}

	return &Flight{
		FlightID: fmt.Sprintf(
			"%s_%s_%d_%s_%s", // 20230101_AZ_1234_LHR_JFK
			departureAt.Format("20060102"),
			l.AirlineDesignator,
			l.FlightNumber,
			l.DepartureStation,
			l.ArrivalStation,
		),
		Airline:              l.AirlineDesignator,
		FlightNumber:         l.FlightNumber,
		Itinerary:            l.ItineraryVariationIdentifier,
		Sequence:             l.LegSequenceNumber,
		DepartureAirport:     l.DepartureStation,
		ArrivalAirport:       l.ArrivalStation,
		Aircraft:             l.AircraftType,
		ScheduledDepartureAt: departureAt,
		ScheduledArrivalAt:   arrivalAt.Add(arrivalAdditionalDuration),
		ExpectedDepartureAt:  departureAt,
		ExpectedArrivalAt:    arrivalAt.Add(arrivalAdditionalDuration),
	}, nil
}

// Codeshare represents a codeshare of a segment
type Codeshare struct {
	Airline      string `json:"airline"`
	FlightNumber int    `json:"flight_number"`
}

// ConnectingFlightPeriods represents the flight periods that are connected to each other
type ConnectingFlightPeriods struct {
	ID             int64              `json:"id"`
	Airline        string             `json:"airline"`
	BeginsAt       time.Time          `json:"begins_at"`
	EndsAt         time.Time          `json:"ends_at"`
	ArrivalAirport string             `json:"arrival_airport"`
	NumSegments    int8               `json:"num_segments"`
	Periods        []*PeriodOfFlights `json:"periods"`
}

// ToConnectingFlights converts the connecting flight periods to connecting flights
func (cf *ConnectingFlightPeriods) ToConnectingFlights() []ConnectingFlight {
	cfs := make([]ConnectingFlight, len(cf.Periods[0].Flights))
	for _, p := range cf.Periods {
		for i, f := range p.Flights {
			f.Codeshares = p.Codeshares
			cfs[i].Flights = append(cfs[i].Flights, f)
		}
	}

	// Connecting flight periods are converted to connecting flights
	// Set the connecting flight id, arrival airport, begins at and ends at fields
	for i, cf := range cfs {
		flightID := make([]string, len(cf.Flights))
		for j, f := range cf.Flights {
			flightID[j] = f.FlightID
		}
		cfs[i].ConnectingFlightID = strings.Join(flightID, "-")
		cfs[i].ArrivalAirport = cf.Flights[len(cf.Flights)-1].ArrivalAirport
		cfs[i].BeginsAt = cf.Flights[0].ScheduledDepartureAt
		cfs[i].EndsAt = cf.Flights[len(cf.Flights)-1].ScheduledArrivalAt
	}

	return cfs
}

// MonthlyConnectingFlights returns the monthly connecting flights
// of the single multi-month connecting flight
// Sometimes a connecting flights can extend to the next month
// because the first flight of the connecting flight is within the range of a month
// but the on of next flights of the connecting flight is in the next month
// in that case we still keep the connecting flight as a single connecting flight
// and we store it in the month of the first flight departure date's month
// Example:
// If the connecting flight is from 2019-01-01 to 2019-03-31
// first connecting flight will be from 2019-01-01 to 2019-01-31
// second connecting flight will be from 2019-02-01 to 2019-02-28
// third connecting flight will be from 2019-03-01 to 2019-03-31
func (cf *ConnectingFlightPeriods) MonthlyConnectingFlights() []*ConnectingFlightPeriods {
	monthlyConnectingFlights := make([]*ConnectingFlightPeriods, 0)

	beginningPeriod := cf.Periods[0]
	monthlyBeginningPeriods := beginningPeriod.MonthlyPeriods()

	for _, p := range monthlyBeginningPeriods {
		monthlyConnectingFlights = append(monthlyConnectingFlights, &ConnectingFlightPeriods{
			Airline:     cf.Airline,
			BeginsAt:    p.BeginsAt,
			EndsAt:      p.EndsAt,
			NumSegments: int8(len(cf.Periods)),
			Periods:     []*PeriodOfFlights{p},
		})
	}

	for _, next := range cf.Periods[1:] {
		offset := 0
		for i, monthly := range monthlyBeginningPeriods {
			nextMonthly := &PeriodOfFlights{
				Airline:          next.Airline,
				FlightNumber:     next.FlightNumber,
				Itinerary:        next.Itinerary,
				Sequence:         next.Sequence,
				DepartureAirport: next.DepartureAirport,
				ArrivalAirport:   next.ArrivalAirport,
				Aircraft:         next.Aircraft,
				Codeshares:       next.Codeshares,
				BeginsAt:         monthly.BeginsAt,
				EndsAt:           monthly.EndsAt,
				Flights:          make([]*Flight, len(monthly.Flights)),
			}

			for i := 0; i < len(monthly.Flights); i++ {
				nextMonthly.Flights[i] = next.Flights[i+offset]
			}

			offset += len(monthly.Flights)

			monthlyConnectingFlights[i].Periods = append(monthlyConnectingFlights[i].Periods, nextMonthly)
			monthlyConnectingFlights[i].ArrivalAirport = nextMonthly.ArrivalAirport
		}
	}

	return monthlyConnectingFlights
}

// NotCircularWith checks if the flight is circular with the other flight
func (cf *ConnectingFlightPeriods) NotCircularWith(p *PeriodOfFlights) bool {
	firstPeriod := cf.Periods[0]

	return firstPeriod.DepartureAirport != p.ArrivalAirport
}

// ConnectingFlight represents the flights that are connected to each other
type ConnectingFlight struct {
	ID                 int64     `json:"id"`
	ConnectingFlightID string    `json:"connecting_flight_id"`
	ArrivalAirport     string    `json:"arrival_airport"`
	Flights            []*Flight `json:"flights"`
	BeginsAt           time.Time `json:"begins_at"`
	EndsAt             time.Time `json:"ends_at"`
}

func (cf *ConnectingFlight) Equals(other *ConnectingFlight) bool {
	if !cf.BeginsAt.Equal(other.BeginsAt) || !cf.EndsAt.Equal(other.EndsAt) {
		return false
	}

	if len(cf.Flights) != len(other.Flights) {
		return false
	}

	for i := range cf.Flights {
		if !cf.Flights[i].Equals(other.Flights[i]) {
			return false
		}
	}

	return true
}

// PeriodOfFlights is a period of flights
type PeriodOfFlights struct {
	Airline          string       `json:"airline"`
	FlightNumber     int          `json:"flight_number"`
	Itinerary        int8         `json:"itinerary"`
	Sequence         int8         `json:"sequence"`
	DepartureAirport string       `json:"departure_airport"`
	ArrivalAirport   string       `json:"arrival_airport"`
	Aircraft         string       `json:"aircraft"`
	Codeshares       []*Codeshare `json:"codeshares,omitempty"`

	BeginsAt time.Time `json:"begins_at"`
	EndsAt   time.Time `json:"ends_at"`

	Flights []*Flight `json:"flights"`
}

// MonthlyPeriods returns the monthly periods of the period of flights
// The monthly periods are the periods of flights grouped by month
// Returns the monthly periods without breaking the original order of the flights
// Example:
// If the period of flights is from 2019-01-01 to 2019-03-31
// first monthly period will be from 2019-01-01 to 2019-01-31
// second monthly period will be from 2019-02-01 to 2019-02-28
// third monthly period will be from 2019-03-01 to 2019-03-31
func (p *PeriodOfFlights) MonthlyPeriods() []*PeriodOfFlights {
	periods := make([]*PeriodOfFlights, 0)
	periodsMap := make(map[string]*PeriodOfFlights)

	for _, flight := range p.Flights {
		key := FormatYYYYMM(flight.ScheduledDepartureAt)
		if _, ok := periodsMap[key]; !ok {
			beginsAt := time.Date(flight.ScheduledDepartureAt.Year(), flight.ScheduledDepartureAt.Month(), 1, 0, 0, 0, 0, time.UTC)
			endsAt := beginsAt.AddDate(0, 1, -1)

			newP := &PeriodOfFlights{
				Airline:          p.Airline,
				FlightNumber:     p.FlightNumber,
				Itinerary:        p.Itinerary,
				Sequence:         p.Sequence,
				DepartureAirport: p.DepartureAirport,
				ArrivalAirport:   p.ArrivalAirport,
				Aircraft:         p.Aircraft,
				Codeshares:       p.Codeshares,
				BeginsAt:         beginsAt,
				EndsAt:           endsAt,
			}
			periods = append(periods, newP)
			periodsMap[key] = newP
		}

		periodsMap[key].Flights = append(periodsMap[key].Flights, flight)
	}

	return periods
}

// SameItinerary checks if the flight is the same itinerary as the other flight
func (f *PeriodOfFlights) SameItinerary(other *reader.Leg) bool {
	return f.Airline == other.AirlineDesignator &&
		f.FlightNumber == other.FlightNumber &&
		f.Itinerary == other.ItineraryVariationIdentifier &&
		f.Sequence < other.LegSequenceNumber &&
		f.ArrivalAirport == other.DepartureStation
}

func (p *Parser) parseLeg(ctx context.Context, row string) error {
	leg, err := reader.ReadLeg(row)
	if err != nil {
		return errors.Wrap(err, "parser: failed to read leg")
	}

	// Update the leg's airline id using the mappers
	leg.AirlineDesignator = p.airlinesMapper.Get(ctx, leg.AirlineDesignator)

	if !leg.ContainsPassengers() {
		// If there are more than one period in the connecting flight we add it to the connecting flights
		// Otherwise we ignore it because it is not a connecting flight
		if p.ct.lastConnFlight != nil && len(p.ct.lastConnFlight.Periods) > 1 {
			p.ct.connectingFlightPeriods = append(p.ct.connectingFlightPeriods, p.ct.lastConnFlight)
		}

		p.ct.lastPeriod = nil
		p.ct.lastConnFlight = nil
		return nil
	}

	from, to, err := p.parseLegPeriod(leg)
	if err != nil {
		return err
	}

	//log.Debug(ctx, "Parsing leg period", zap.Time("from", from), zap.Time("to", to), zap.Any("leg", leg))

	operationDays := mapOperationDays(leg.DaysOfOperation)

	period := &PeriodOfFlights{
		Airline:          leg.AirlineDesignator,
		FlightNumber:     leg.FlightNumber,
		Itinerary:        leg.ItineraryVariationIdentifier,
		Sequence:         leg.LegSequenceNumber,
		DepartureAirport: leg.DepartureStation,
		ArrivalAirport:   leg.ArrivalStation,
		Aircraft:         leg.AircraftType,
		BeginsAt:         from,
		EndsAt:           to,
	}
	for date := from; date.Compare(to) != 1; date = date.AddDate(0, 0, 1) {
		if !operationDays[date.Weekday()] {
			continue
		}

		flight, err := NewFlight(leg, date)
		if err != nil {
			return errors.Wrap(err, "parser: failed to create flight")
		}

		period.Flights = append(period.Flights, flight)
	}

	if p.ct.lastConnFlight != nil && p.ct.lastPeriod.SameItinerary(leg) && p.ct.lastConnFlight.NotCircularWith(period) {
		p.ct.lastConnFlight.ArrivalAirport = leg.ArrivalStation
		p.ct.lastConnFlight.NumSegments++
		p.ct.lastConnFlight.Periods = append(p.ct.lastConnFlight.Periods, period)
	} else {
		// If there are more than one periods in the connecting flight we add it to the connecting flights
		// Otherwise we ignore it because it is not a connecting flight
		if p.ct.lastConnFlight != nil && len(p.ct.lastConnFlight.Periods) > 1 {
			p.ct.connectingFlightPeriods = append(p.ct.connectingFlightPeriods, p.ct.lastConnFlight)
		}

		// TODO: think about adding airline, beginsAt and endsAt fields to the connecting flight
		// or find a way to carry carrier/trailer information to the connecting flight split operation
		p.ct.lastConnFlight = &ConnectingFlightPeriods{
			ArrivalAirport: leg.ArrivalStation,
			Airline:        leg.AirlineDesignator,
			BeginsAt:       from,
			EndsAt:         to,
			NumSegments:    1,
			Periods:        []*PeriodOfFlights{period},
		}
	}

	p.ct.lastPeriod = period
	p.ct.periods = append(p.ct.periods, period)

	return nil
}

func (p *Parser) parseLegPeriod(l *reader.Leg) (time.Time, time.Time, error) {
	defaultFrom := p.ct.From
	defaultTo := p.ct.To
	if p.ct.lastPeriod != nil && p.ct.lastPeriod.SameItinerary(l) {
		departureVariationDuration, err := toDepartureDateVariationDuration(l.DateVariation)
		if err != nil {
			return time.Time{}, time.Time{}, errors.Wrap(err, "parser: failed to parse departure variation duration")
		}

		firstPeriod := p.ct.lastConnFlight.Periods[0]

		defaultFrom = firstPeriod.BeginsAt.Add(departureVariationDuration)
		defaultTo = firstPeriod.EndsAt.Add(departureVariationDuration)
	}

	from, err := ParseDateFormat00XXX00(l.PeriodOfOperation.From, defaultFrom)
	if err != nil {
		return time.Time{}, time.Time{}, errors.Wrap(err, "parser: failed to parse from date")
	}

	to, err := ParseDateFormat00XXX00(l.PeriodOfOperation.To, defaultTo)
	if err != nil {
		return time.Time{}, time.Time{}, errors.Wrap(err, "parser: failed to parse to date")
	}

	return from, to, nil
}

func (p *Parser) parseSegment(ctx context.Context, row string) error {
	// Ignore the segment if there is no period defined
	// Period can be nil because the leg can be ignored
	if p.ct.lastPeriod == nil {
		return nil
	}

	segment, err := reader.ReadSegment(row)
	if err != nil {
		return errors.Wrap(err, "parser: failed to read segment")
	}

	segment.AirlineDesignator = p.airlinesMapper.Get(ctx, segment.AirlineDesignator)

	// When the data element identifier is 50 it means that the flight is a codeshare flight
	// So the original leg is already defined. So we can ignore this record
	// Because we already have this record as a codeshare in the original flight
	if segment.DataElementIdentifier == "050" {
		p.ct.periods = p.ct.periods[:len(p.ct.periods)-1]

		// If connecting flight added for the leg which has data element identifier 50
		// We are going to remove the connecting flight as well from the connecting flights
		// This case never happened for now with a lot of files but it is possible since there are no certain rules
		if len(p.ct.connectingFlightPeriods) > 1 {
			cf := p.ct.connectingFlightPeriods[len(p.ct.connectingFlightPeriods)-1]
			lp := cf.Periods[len(cf.Periods)-1]
			if lp == p.ct.lastPeriod {
				p.ct.connectingFlightPeriods = p.ct.connectingFlightPeriods[:len(p.ct.connectingFlightPeriods)-1]
			}
		}

		p.ct.lastPeriod = nil
		p.ct.lastConnFlight = nil
		return nil
	}

	// We only care about segments with data element identifier 10
	// If necessary we can collect information from other data element identifiers
	// But for starters we only care about 10
	if segment.DataElementIdentifier != "010" {
		return nil
	}
	// 3.*107.*ZNZ.*DAR

	codeshares := make([]*Codeshare, 0)

	dataArr := cleanEmptyElements(strings.Split(segment.Data, "/"))
	for _, arr := range dataArr {
		codeshare := cleanEmptyElements(strings.Split(arr, " "))
		airline := strings.TrimSpace(codeshare[0])
		rawFlightNumber := strings.TrimSpace(codeshare[1])
		// If there is an Operational suffix `Z` it is included in the flight number
		// Check the last character and remove it if it is `Z`
		// TODO: completely remove the last element or just take the first 4 characters?
		if strings.ContainsRune("ZABS", rune(rawFlightNumber[len(rawFlightNumber)-1])) {
			rawFlightNumber = rawFlightNumber[:len(rawFlightNumber)-1]
		}

		flightNumber, err := strconv.Atoi(rawFlightNumber)
		if err != nil {
			return errors.Wrapf(err, "parser: failed to parse codeshare flight number %v", codeshare)
		}

		codeshares = append(codeshares, &Codeshare{
			Airline:      p.airlinesMapper.Get(ctx, airline),
			FlightNumber: flightNumber,
		})
	}

	p.ct.lastPeriod.Codeshares = codeshares

	return nil
}

package parser

import (
	"bufio"
	"context"
	"io"

	"go.impalastudios.com/aviation/schedules/pkg/ssim/reader"
	"go.impalastudios.com/log"
	"go.uber.org/zap"
)

type writer interface {
	WriteAll(cts []*CarrierTrailerBlock) ([]*CarrierTrailerBlockLink, error)
}

type mapper interface {
	Get(ctx context.Context, key string) string
}

// Parser parses SSIM file,
// since the SSIM files can be very large we don't want to store the whole file in memory
// so we parse the carrier trailer blocks one by one and write them to a file
type Parser struct {
	ct *CarrierTrailerBlock

	w writer

	airlinesMapper mapper

	ctlinks []*CarrierTrailerBlockLink
}

// NewParser creates a new parser
func NewParser(w writer, airlinesMapper mapper) *Parser {
	return &Parser{
		w:              w,
		ctlinks:        make([]*CarrierTrailerBlockLink, 0),
		airlinesMapper: airlinesMapper,
	}
}

// Parse parses an SSIM file
func (p *Parser) Parse(ctx context.Context, r io.Reader) (*SSIM, error) {
	scanner := bufio.NewScanner(r)
	scanner.Split(bufio.ScanLines)

	for scanner.Scan() {
		row := scanner.Text()

		if reader.IsHeader(row) || reader.IsZero(row) {
			// The header record is always the first record in the file
			// Usually the header record is useless, so we ignore it
			continue
		}

		if reader.IsCarrier(row) {
			// Carrier records are the starting record for a carrier trailer block
			// There can be multiple carrier trailer blocks in a file
			if err := p.startCarrierTrailerBlock(ctx, row); err != nil {
				return nil, err
			}
		} else if reader.IsLeg(row) {
			if err := p.parseLeg(ctx, row); err != nil {
				return nil, err
			}
		} else if reader.IsSegment(row) {
			// Segment row, will follow a leg or a segment row
			// so we store multiple segment information for a leg
			if err := p.parseSegment(ctx, row); err != nil {
				return nil, err
			}
		} else if reader.IsTrailer(row) {
			// The trailer record is the ending record for a carrier trailer block
			// There can be multiple carrier trailer blocks in a file
			if err := p.endCarrierTrailerBlock(ctx, row); err != nil {
				return nil, err
			}
		} else {
			log.Warn(ctx, "Unknown row type", zap.String("row", row))
		}
	}

	return &SSIM{Links: p.ctlinks}, nil
}

package parser

func cleanEmptyElements(arr []string) []string {
	newArr := make([]string, 0)
	for _, element := range arr {
		if len(element) > 0 {
			newArr = append(newArr, element)
		}
	}

	return newArr
}

package parser

import (
	"fmt"
	"strconv"
	"strings"
	"time"

	"github.com/cockroachdb/errors"
)

var months = map[string]time.Month{
	"JAN": time.January,
	"FEB": time.February,
	"MAR": time.March,
	"APR": time.April,
	"MAY": time.May,
	"JUN": time.June,
	"JUL": time.July,
	"AUG": time.August,
	"SEP": time.September,
	"OCT": time.October,
	"NOV": time.November,
	"DEC": time.December,
}

const (
	// ZeroTimeUTC used to represent zero time
	ZeroTimeUTC = "0000"
	// ZeroDate used to represent zero date
	ZeroDate = "00XXX00"
)

// ParseDateFormat00XXX00 parses a date in the format 00XXX00.
// The first two characters are the day, the next three are the month, and the last two are the year.
// Example. 11JAN17
func ParseDateFormat00XXX00(date string, default_ time.Time) (time.Time, error) {
	if date == ZeroDate {
		return default_, nil
	}

	rawDay := date[0:2]
	rawMonth := date[2:5]
	rawYear := date[5:7]

	day, err := strconv.Atoi(rawDay)
	if err != nil {
		return time.Time{}, errors.Wrap(err, "parser: could not parse day")
	}

	month, ok := months[rawMonth]
	if !ok {
		return time.Time{}, errors.Newf("parser: could not find month %s", rawMonth)
	}

	year, err := strconv.Atoi(rawYear)
	if err != nil {
		return time.Time{}, errors.Wrap(err, "parser: could not parse year")
	}
	year += 2000

	return time.Date(year, month, day, 0, 0, 0, 0, time.UTC), nil
}

// ParseHourMinute parses a time in the format 0000.
func ParseHourMinute(hourMinute string) (int, int, error) {
	hour, err := strconv.Atoi(hourMinute[0:2])
	if err != nil {
		return 0, 0, errors.Wrap(err, "parser: could not parse hour")
	}

	minute, err := strconv.Atoi(hourMinute[2:4])
	if err != nil {
		return 0, 0, errors.Wrap(err, "parser: could not parse minute")
	}

	return hour, minute, nil
}

// FixedTimezone is a placeholder for a function that will return a time.Location
// timezoneIdentifier is a string in the format +0300
func FixedTimezone(timezoneIdentifier string) (*time.Location, error) {
	hours := timezoneIdentifier[1:3]
	minutes := timezoneIdentifier[3:5]

	timezoneOffset, err := strconv.Atoi(hours)
	if err != nil {
		return nil, err
	}

	timezoneOffset *= 60

	minutesInt, err := strconv.Atoi(minutes)
	if err != nil {
		return nil, err
	}

	timezoneOffset += minutesInt

	if timezoneIdentifier[0:1] == "-" {
		timezoneOffset *= -1
	}

	timezoneOffset *= 60

	return time.FixedZone("UTC"+timezoneIdentifier, timezoneOffset), nil
}

func toArrivalDateVariationDuration(rawDateVariation string) (time.Duration, error) {
	dateVariation := strings.ReplaceAll(rawDateVariation, "J", "0")

	arrivalDateVariation, err := strconv.Atoi(dateVariation[1:])
	if err != nil {
		return 0, errors.Wrapf(err, "leg: failed to parse arrival date variation: %s", rawDateVariation)
	}

	departureDateVariation, err := strconv.Atoi(dateVariation[:1])
	if err != nil {
		return 0, errors.Wrapf(err, "leg: failed to parse departure date variation: %s", rawDateVariation)
	}

	if strings.ContainsAny(rawDateVariation[1:], "JA") {
		arrivalDateVariation = -1
	}

	if strings.ContainsAny(rawDateVariation[:1], "JA") {
		departureDateVariation = -1
	}

	return 24 * time.Hour * time.Duration(arrivalDateVariation-departureDateVariation), nil
}

func toDepartureDateVariationDuration(dateVariation string) (time.Duration, error) {
	departureDateVariation, err := strconv.Atoi(strings.ReplaceAll(dateVariation, "J", "0")[:1])
	if err != nil {
		return 0, errors.Wrapf(err, "leg: failed to parse departure date variation: %s", dateVariation)
	}

	if strings.ContainsAny(dateVariation[:1], "JA") {
		departureDateVariation = -1
	}

	return 24 * time.Hour * time.Duration(departureDateVariation), nil
}

// maxOperationDays returns a map for weekdays which the flight is operating at
// doo is a string of numbers from 1 to 7, each number represents a day of week
// 1 is Monday, 2 is Tuesday, 3 is Wednesday, 4 is Thursday, 5 is Friday, 6 is Saturday, 7 is Sunday
func mapOperationDays(doo string) map[time.Weekday]bool {
	// SSIM uses 1-7 for days of week, but Go uses 0-6
	// In Golang Monday startswith 1 and goes up to 6 and sunday is 0
	// In SSIM Monday startswith 1 and goes up to 7 and sunday is 7
	// We replace the 7s with 0s to make it compatible with Go
	daysOfOperation := strings.ReplaceAll(doo, "7", "0")

	operationDays := make(map[time.Weekday]bool)
	for i := 0; i <= 6; i++ {
		if strings.Contains(daysOfOperation, strconv.Itoa(i)) {
			operationDays[time.Weekday(i)] = true
		}
	}

	return operationDays
}

func buildFlightDatetime(date *time.Time, timeString, timezoneIdentifier string) (time.Time, error) {
	timezone, err := FixedTimezone(timezoneIdentifier)
	if err != nil {
		return time.Time{}, err
	}

	hour, minute, err := ParseHourMinute(timeString)
	if err != nil {
		return time.Time{}, err
	}

	return time.Date(date.Year(), date.Month(), date.Day(), hour, minute, 0, 0, timezone), nil
}

func FormatYYYYMM(t time.Time) string {
	return fmt.Sprintf("%d_%d", t.Year(), t.Month())
}

package parser

import (
	"context"
	"fmt"
	"time"

	"github.com/pkg/errors"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/reader"
	"go.impalastudios.com/log"
	"go.uber.org/zap"
)

// CarrierTrailerBlock is a block of carrier, leg and segment rows
type CarrierTrailerBlock struct {
	Carrier           *reader.Carrier     `json:"carrier"`
	Trailer           *reader.Trailer     `json:"trailer"`
	From              time.Time           `json:"from"`
	To                time.Time           `json:"to"`
	Flights           []*Flight           `json:"flights"`
	ConnectingFlights []*ConnectingFlight `json:"connecting_flights"`

	periods                 []*PeriodOfFlights
	connectingFlightPeriods []*ConnectingFlightPeriods
	lastPeriod              *PeriodOfFlights
	lastConnFlight          *ConnectingFlightPeriods
}

func (p *Parser) startCarrierTrailerBlock(ctx context.Context, row string) error {
	carrier := reader.ReadCarrier(row)
	carrier.AirlineDesignator = p.airlinesMapper.Get(ctx, carrier.AirlineDesignator)

	from, err := ParseDateFormat00XXX00(carrier.PeriodOfScheduleValidity.From, time.Time{})
	if err != nil {
		return errors.Wrap(err, "carrier: failed to parse from date")
	}

	to, err := ParseDateFormat00XXX00(carrier.PeriodOfScheduleValidity.To, time.Time{})
	if err != nil {
		return errors.Wrap(err, "carrier: failed to parse to date")
	}

	p.ct = &CarrierTrailerBlock{
		Carrier: carrier,
		From:    from,
		To:      to,
	}

	log.Debug(ctx, "Started parsing carrier/trailer block",
		zap.String("airline", p.ct.Carrier.AirlineDesignator),
		zap.Any("period", p.ct.Carrier.PeriodOfScheduleValidity),
		zap.String("season", p.ct.Carrier.Season),
	)

	return nil
}

// splitMultiMonthCarrierTrailerToMonthly splits a carrier trailer block into multiple blocks
// Some carrier trailer blocks can span multiple months, we split those blocks into multiple blocks per month
// Then for each carrier trailer block we convert periods to direct flights,
// and connecting flight periods to connecting flights
func splitMultiMonthCarrierTrailerToMonthly(mainBlock *CarrierTrailerBlock) []*CarrierTrailerBlock {
	carrierTrailerBlocks := make(map[string]*CarrierTrailerBlock)

	for _, period := range mainBlock.periods {
		monthlyPeriods := period.MonthlyPeriods()
		for _, monthlyPeriod := range monthlyPeriods {
			key := fmt.Sprintf("%s_%s", monthlyPeriod.Airline, FormatYYYYMM(monthlyPeriod.BeginsAt))
			if _, ok := carrierTrailerBlocks[key]; !ok {
				carrierTrailerBlocks[key] = &CarrierTrailerBlock{
					Carrier:           mainBlock.Carrier,
					Trailer:           mainBlock.Trailer,
					From:              monthlyPeriod.BeginsAt,
					To:                monthlyPeriod.EndsAt,
					Flights:           make([]*Flight, 0),
					ConnectingFlights: make([]*ConnectingFlight, 0),
				}
			}
			for _, f := range monthlyPeriod.Flights {
				f.Codeshares = monthlyPeriod.Codeshares
				carrierTrailerBlocks[key].Flights = append(carrierTrailerBlocks[key].Flights, f)
			}
		}
	}

	for _, cf := range mainBlock.connectingFlightPeriods {
		monthlyConnectingFlights := cf.MonthlyConnectingFlights()
		for _, mcf := range monthlyConnectingFlights {
			key := fmt.Sprintf("%s_%s", mcf.Airline, FormatYYYYMM(mcf.BeginsAt))
			cfs := mcf.ToConnectingFlights()
			for i := range cfs {
				carrierTrailerBlocks[key].ConnectingFlights = append(carrierTrailerBlocks[key].ConnectingFlights, &cfs[i])
			}
		}
	}

	blocks := make([]*CarrierTrailerBlock, 0)
	for _, block := range carrierTrailerBlocks {
		blocks = append(blocks, block)
	}
	return blocks
}

func (p *Parser) endCarrierTrailerBlock(ctx context.Context, row string) error {
	// Add the last connecting flight to the list of connecting flights
	// If we don't do this, the last connecting flight will be ignored
	if p.ct.lastConnFlight != nil && p.ct.lastConnFlight.NumSegments > 1 {
		p.ct.connectingFlightPeriods = append(p.ct.connectingFlightPeriods, p.ct.lastConnFlight)
	}

	p.ct.Trailer = reader.ReadTrailer(row)

	p.ct.Trailer.AirlineDesignator = p.airlinesMapper.Get(ctx, p.ct.Carrier.AirlineDesignator)

	carrierTrailerBlocks := splitMultiMonthCarrierTrailerToMonthly(p.ct)

	//log.Debug(ctx, "Finished parsing carrier/trailer block",
	//	zap.String("airline", p.ct.Trailer.AirlineDesignator),
	//	zap.Any("serial_number", p.ct.Trailer.RecordSerialNumber),
	//)

	p.ct = nil

	links, err := p.w.WriteAll(carrierTrailerBlocks)
	if err != nil {
		log.Error(ctx, "Failed to write block", zap.Error(err))
	}

	p.ctlinks = append(p.ctlinks, links...)

	for _, link := range links {
		log.Debug(ctx, "Wrote block", zap.String("filename", link.Filepath))
	}

	return nil
}

package writer

import (
	"encoding/json"
	"fmt"
	"os"
	"sync"

	"github.com/cockroachdb/errors"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
)

// DefaultDir is the default directory for carrier trailer blocks
const DefaultDir = "carrier_trailers"

// BlockWriter writes carrier trailer blocks to a file
type BlockWriter struct {
	hasDir bool
	dir    string

	mux *sync.Mutex
}

// NewBlockWriter creates a new block writer
func NewBlockWriter(dir string) *BlockWriter {
	return &BlockWriter{
		mux: &sync.Mutex{},

		dir: dir,
	}
}

func (w *BlockWriter) createDir() error {
	defer w.mux.Unlock()
	w.mux.Lock()

	if w.hasDir {
		return nil
	}

	if err := os.MkdirAll(w.dir, 0755); err != nil {
		return errors.Wrap(err, "writer: failed to create directory")
	}

	w.hasDir = true

	return nil
}

// Write writes a carrier trailer block
func (w *BlockWriter) Write(block *parser.CarrierTrailerBlock) (*parser.CarrierTrailerBlockLink, error) {
	if err := w.createDir(); err != nil {
		return nil, err
	}

	blockbytes, err := json.Marshal(block)
	if err != nil {
		return nil, errors.Wrap(err, "writer: failed to marshal block")
	}

	filepath := fmt.Sprintf("%s/%s_%s.json",
		w.dir,
		block.Carrier.AirlineDesignator,
		parser.FormatYYYYMM(block.From),
	)
	if err := os.WriteFile(filepath, blockbytes, 0644); err != nil {
		return nil, errors.Wrap(err, "writer: failed to write block to file")
	}

	return &parser.CarrierTrailerBlockLink{
		AirlineDesignator: block.Carrier.AirlineDesignator,
		PeriodBegin:       parser.FormatYYYYMM(block.From),
		PeriodEnd:         parser.FormatYYYYMM(block.To),
		Filepath:          filepath,
	}, nil
}

// WriteAll writes a carrier trailer blocks
func (w *BlockWriter) WriteAll(blocks []*parser.CarrierTrailerBlock) ([]*parser.CarrierTrailerBlockLink, error) {
	if err := w.createDir(); err != nil {
		return nil, err
	}

	links := make([]*parser.CarrierTrailerBlockLink, 0)

	for _, block := range blocks {
		blockbytes, err := json.Marshal(block)
		if err != nil {
			return nil, errors.Wrap(err, "writer: failed to marshal block")
		}

		filepath := fmt.Sprintf("%s/%s_%s.json",
			w.dir,
			block.Carrier.AirlineDesignator,
			parser.FormatYYYYMM(block.From),
		)
		if err := os.WriteFile(filepath, blockbytes, 0644); err != nil {
			return nil, errors.Wrap(err, "writer: failed to write block to file")
		}

		links = append(links, &parser.CarrierTrailerBlockLink{
			AirlineDesignator: block.Carrier.AirlineDesignator,
			PeriodBegin:       parser.FormatYYYYMM(block.From),
			PeriodEnd:         parser.FormatYYYYMM(block.To),
			Filepath:          filepath,
		})
	}

	return links, nil
}

package db

import (
	"context"
	"encoding/json"
	"fmt"
	"github.com/jackc/pgx/v4"
	"os"
	"sync"
	"time"

	"github.com/cockroachdb/errors"
	"go.impalastudios.com/aviation/schedules/pkg/concurrent"
	"go.impalastudios.com/aviation/schedules/pkg/pgsql"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
	"go.impalastudios.com/log"
	"go.uber.org/zap"
)

// Ingester ingests SSIM data into the database
type Ingester struct {
	db  *pgsql.DB
	cfg *pgsql.Config

	schemaCache *concurrent.Map[*schema]

	mux *sync.Mutex

	errors []error

	TotalFlightsIngested           *concurrent.AtomicInt64
	TotalConnectingFlightsIngested *concurrent.AtomicInt64
}

// NewIngester creates a new ingester
func NewIngester(
	db *pgsql.DB,
	dbcfg *pgsql.Config,
) *Ingester {
	return &Ingester{
		db:                             db,
		cfg:                            dbcfg,
		mux:                            &sync.Mutex{},
		schemaCache:                    concurrent.NewMap[*schema](),
		TotalFlightsIngested:           concurrent.NewAtomicInt64(0),
		TotalConnectingFlightsIngested: concurrent.NewAtomicInt64(0),
	}
}

// Ingest ingests SSIM data into the database
func (i *Ingester) Ingest(ctx context.Context, links []*parser.CarrierTrailerBlockLink) error {
	log.Debug(ctx, "Ingesting SSIM data", zap.Int("links", len(links)))

	i.errors = concurrent.ExecuteInPool(ctx, links, i.IngestLink)

	log.Debug(ctx, "Finished ingesting SSIM data", zap.Errors("errors", i.errors))

	return nil
}

func (i *Ingester) createOrLoadSchema(ctx context.Context, schema string) (*schema, error) {
	if s, ok := i.schemaCache.Get(schema); ok {
		return s, nil
	}

	defer i.mux.Unlock()
	i.mux.Lock()

	if s, ok := i.schemaCache.Get(schema); ok {
		return s, nil
	}

	s, err := i.loadSchema(ctx, schema)
	if err != nil {
		log.Warn(ctx, "Schema does not exist, creating a new one...", zap.String("schema", schema), zap.Error(err))

		s, err = i.createSchema(ctx, schema)
		if err != nil {
			return nil, errors.Wrap(err, "db: failed to create schema")
		}
	}

	if err := pgsql.MigrateUpWithSchema(i.cfg, schema); err != nil {
		return nil, errors.Wrap(err, "db: failed to migrate up")
	}

	i.schemaCache.Set(schema, s)
	return s, nil
}

// IngestCt ingests a carrier trailer block
func (i *Ingester) IngestCt(ctx context.Context, ct *parser.CarrierTrailerBlock) error {
	log.Debug(ctx, "Ingesting carrier trailer block")

	schema := "period_" + parser.FormatYYYYMM(ct.From)
	s, err := i.createOrLoadSchema(ctx, schema)
	if err != nil {
		return err
	}

	pgingest := &pgxCopyIngest{
		schema:                         schema,
		db:                             i.db,
		seq:                            s.seq,
		ct:                             ct,
		flightIndexMapper:              concurrent.NewMap[int](),
		codeshareMapper:                concurrent.NewMap[int64](),
		TotalFlightIngested:            i.TotalFlightsIngested,
		TotalConnectingFlightsIngested: i.TotalConnectingFlightsIngested,
	}
	pgingest.flattenCarrierTrailerBlock()
	return pgingest.insert(ctx)
}

func (i *Ingester) IngestLink(ctx context.Context, link *parser.CarrierTrailerBlockLink) error {
	fbytes, err := os.ReadFile(link.Filepath)
	if err != nil {
		return errors.Wrap(err, "db: failed to read file")
	}

	var ct parser.CarrierTrailerBlock
	if err := json.Unmarshal(fbytes, &ct); err != nil {
		return errors.Wrap(err, "db: failed to unmarshal carrier trailer block")
	}

	log.Debug(ctx, "Ingesting carrier trailer block", zap.String("filepath", link.Filepath))

	return i.IngestCt(ctx, &ct)
}

type pgxCopyIngest struct {
	db     *pgsql.DB
	schema string
	seq    *seq

	ct *parser.CarrierTrailerBlock

	flattenedFlights           [][]interface{}
	flattenedConnectingFlights [][]interface{}

	flightIndexMapper *concurrent.Map[int]
	codeshareMapper   *concurrent.Map[int64]

	TotalFlightIngested            *concurrent.AtomicInt64
	TotalConnectingFlightsIngested *concurrent.AtomicInt64
}

func dateOnly(t time.Time) time.Time {
	return time.Date(t.Year(), t.Month(), t.Day(), 0, 0, 0, 0, time.UTC)
}

func timeWithTZ(t time.Time) string {
	return t.Format("1504") + t.Format("-0700")
}

// flightID returns a unique identifier for a flight
func flightID(f *parser.Flight) string {
	return f.FlightID
}

func (pg *pgxCopyIngest) addFlight(connectingFlightID *int64, f *parser.Flight) {
	f.ID = pg.seq.FlightID.GetAndInc()
	codeshareAirlines := []string{}
	codeshareDesignators := []string{}
	codeshares := [][]string{}

	for _, c := range f.Codeshares {
		codeshareAirlines = append(codeshareAirlines, c.Airline)
		codeshareDesignators = append(codeshareDesignators, fmt.Sprintf("%s%d", c.Airline, c.FlightNumber))
		codeshares = append(codeshares, []string{c.Airline, fmt.Sprintf("%d", c.FlightNumber)})
	}

	pg.flattenedFlights = append(pg.flattenedFlights, []interface{}{
		connectingFlightID,
		f.ID,
		f.FlightID,
		f.Airline,
		f.FlightNumber,
		f.Itinerary,
		f.Sequence,
		f.DepartureAirport,
		f.ArrivalAirport,
		f.Aircraft,
		dateOnly(f.ScheduledDepartureAt),
		dateOnly(f.ExpectedDepartureAt),
		dateOnly(f.ActualDepartureAt),
		timeWithTZ(f.ScheduledDepartureAt),
		timeWithTZ(f.ExpectedDepartureAt),
		timeWithTZ(f.ActualDepartureAt),
		dateOnly(f.ScheduledArrivalAt),
		dateOnly(f.ExpectedArrivalAt),
		dateOnly(f.ActualArrivalAt),
		timeWithTZ(f.ScheduledArrivalAt),
		timeWithTZ(f.ExpectedArrivalAt),
		timeWithTZ(f.ActualArrivalAt),
		pgsql.Array(codeshareAirlines),
		pgsql.Array(codeshareDesignators),
		pgsql.Array(codeshares),
	})

	pg.flightIndexMapper.Set(flightID(f), len(pg.flattenedFlights)-1)
}

func (pg *pgxCopyIngest) flattenCarrierTrailerBlock() {
	for _, cf := range pg.ct.ConnectingFlights {
		cfi := pg.seq.ConnectingFlightID.GetAndInc()

		departureAirports := []string{}
		arrivalAirports := []string{}
		flightIDs := []string{}
		scheduledDepartures := []string{}
		for _, f := range cf.Flights {
			if _, ok := pg.flightIndexMapper.Get(flightID(f)); !ok {
				pg.addFlight(&cfi, f)
			}

			flightIDs = append(flightIDs, f.FlightID)
			scheduledDepartures = append(scheduledDepartures, dateOnly(f.ScheduledDepartureAt).Format(time.DateOnly))
			departureAirports = append(departureAirports, f.DepartureAirport)
			arrivalAirports = append(arrivalAirports, f.ArrivalAirport)
		}

		pg.flattenedConnectingFlights = append(pg.flattenedConnectingFlights, []interface{}{
			cfi,
			cf.ConnectingFlightID,
			len(cf.Flights),
			cf.ArrivalAirport,
			pgsql.Array(flightIDs),
			pgsql.Array(scheduledDepartures),
			pgsql.Array(departureAirports),
			pgsql.Array(arrivalAirports),
		})
	}

	for _, f := range pg.ct.Flights {
		if _, ok := pg.flightIndexMapper.Get(flightID(f)); !ok {
			pg.addFlight(nil, f)
		}
	}
}

package db

import (
	"context"
	"fmt"
	"strconv"
	"time"

	"github.com/cockroachdb/errors"
	"github.com/doug-martin/goqu/v9"
	"go.impalastudios.com/aviation/schedules/pkg/pgsql"
)

type FlightStatusUpdate struct {
	ExpectedDepartureAt   *time.Time
	ActualDepartureAt     *time.Time
	ExpectedArrivalAt     *time.Time
	ActualArrivalAt       *time.Time
	FlightStatus          *string
	DepartureFlightStatus *string
	ArrivalFlightStatus   *string
}

// UpdateFlightStatus updates the expected and actual times of a flight
// Checks the values and updates only if they are different in a single query
func UpdateFlightStatus(ctx context.Context, db *pgsql.DB, flightID string, update *FlightStatusUpdate) error {
	if update == nil {
		return nil
	}

	record := make(goqu.Record)
	if update.ExpectedDepartureAt != nil {
		record["expected_departure_at"] = dateOnly(*update.ExpectedDepartureAt)
		record["expected_departure_time_tz"] = timeWithTZ(*update.ExpectedDepartureAt)
	}

	if update.ExpectedArrivalAt != nil {
		record["expected_arrival_at"] = dateOnly(*update.ExpectedArrivalAt)
		record["expected_arrival_time_tz"] = timeWithTZ(*update.ExpectedArrivalAt)
	}

	if update.ActualDepartureAt != nil {
		record["actual_departure_at"] = dateOnly(*update.ActualDepartureAt)
		record["actual_departure_time_tz"] = timeWithTZ(*update.ActualDepartureAt)
	}

	if update.ActualArrivalAt != nil {
		record["actual_arrival_at"] = dateOnly(*update.ActualArrivalAt)
		record["actual_arrival_time_tz"] = timeWithTZ(*update.ActualArrivalAt)
	}

	if update.FlightStatus != nil {
		record["status"] = *update.FlightStatus
	}

	if update.DepartureFlightStatus != nil {
		record["departure_status"] = *update.DepartureFlightStatus
	}

	if update.ArrivalFlightStatus != nil {
		record["arrival_status"] = *update.ArrivalFlightStatus
	}

	// First part of the flight id is always the timestamp
	// And the format is YYYYMMDDHHmm, so we can just take the first 6 characters
	// We convert it to an int and then to a string to remove leading zeros
	year, err := strconv.Atoi(flightID[:4])
	if err != nil {
		return errors.Wrap(err, "failed to parse flight id year")
	}

	month, err := strconv.Atoi(flightID[4:6])
	if err != nil {
		return errors.Wrap(err, "failed to parse flight id month")
	}

	schema := fmt.Sprintf("period_%d_%d", year, month)

	q, args, _ := goqu.
		Update(fmt.Sprintf("%s.flights", schema)).
		Where(goqu.Ex{"flight_id": flightID}).
		Set(record).
		ToSQL()
	_, err = db.Exec(ctx, q, args...)
	return errors.Wrap(err, "failed to update flight times")
}

package db

import (
	"context"
	"fmt"
	"github.com/cockroachdb/errors"
	"go.impalastudios.com/aviation/schedules/pkg/pgsql"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/diff"
	"go.impalastudios.com/aviation/schedules/pkg/ssim/parser"
	"go.impalastudios.com/log"
	"go.uber.org/zap"
)

// IngestPartially ingests SSIM data into the database partially
func IngestPartially(ctx context.Context, db *pgsql.DB, i *Ingester, df *diff.Diff) error {
	if err := removeConnectingFlights(ctx, db, df.ConnectingFlights.Removed); err != nil {
		return err
	}

	if err := removeFlights(ctx, db, df.Flights.Removed); err != nil {
		return err
	}

	return i.IngestCt(ctx, df.MergedCT)
}

func removeConnectingFlights(ctx context.Context, db *pgsql.DB, cfs map[string]*parser.ConnectingFlight) error {
	if len(cfs) == 0 {
		return nil
	}

	log.Debug(ctx, "Removing connecting flights", zap.Int("connecting_flights", len(cfs)))

	cfsBySchema := groupConnectingFlightsBySchema(cfs)

	for schema, cfs := range cfsBySchema {
		ids := make([]int64, len(cfs))
		for i, cf := range cfs {
			ids[i] = cf.ID
		}

		log.Debug(ctx, "Removing connecting flights with IDs.", zap.String("schema", schema), zap.Int64s("ids", ids))

		if _, err := db.Exec(ctx, fmt.Sprintf(`DELETE FROM %s.connecting_flights WHERE id = ANY($1)`, schema), ids); err != nil {
			log.Error(ctx, "Cannot delete connecting flights.", zap.String("schema", schema), zap.Int64s("ids", ids), zap.Error(err))
			return errors.Wrapf(err, "failed to remove connecting flights from %s.connecting_flights", schema)
		}
	}

	return nil
}

func groupConnectingFlightsBySchema(cfs map[string]*parser.ConnectingFlight) map[string][]*parser.ConnectingFlight {
	cfsBySchema := make(map[string][]*parser.ConnectingFlight)
	for _, cf := range cfs {
		schema := "period_" + parser.FormatYYYYMM(cf.BeginsAt)
		cfsBySchema[schema] = append(cfsBySchema[schema], cf)
	}

	return cfsBySchema
}

func removeFlights(ctx context.Context, db *pgsql.DB, flights map[string]*parser.Flight) error {
	if len(flights) == 0 {
		return nil
	}

	log.Debug(ctx, "Removing flights", zap.Int("flights", len(flights)))

	flightsBySchema := groupFlightsBySchema(flights)

	for schema, flights := range flightsBySchema {
		ids := make([]int64, len(flights))
		for i, f := range flights {
			ids[i] = f.ID
		}

		log.Debug(ctx, "Removing flights with IDs.", zap.String("schema", schema), zap.Int64s("ids", ids))

		if _, err := db.Exec(ctx, fmt.Sprintf(`DELETE FROM %s.flights WHERE id = ANY($1)`, schema), ids); err != nil {
			log.Error(ctx, "Cannot delete flights.", zap.String("schema", schema), zap.Int64s("ids", ids), zap.Error(err))
			return errors.Wrapf(err, "failed to remove flights from %s.flights", schema)
		}
	}

	return nil
}

func groupFlightsBySchema(flights map[string]*parser.Flight) map[string][]*parser.Flight {
	flightsBySchema := make(map[string][]*parser.Flight)
	for _, f := range flights {
		schema := "period_" + parser.FormatYYYYMM(f.ScheduledDepartureAt)
		flightsBySchema[schema] = append(flightsBySchema[schema], f)
	}

	return flightsBySchema
}

func scan(rows pgx.Rows) (*flight, error) {
	var f flight
	err := rows.Scan(
		&f.CFI,
		&f.ID,
		&f.FlightID,
		&f.ConnectingFlightID,
		&f.Airline,
		&f.FlightNumber,
		&f.Itinerary,
		&f.Sequence,
		&f.DepartureAirport,
		&f.ArrivalAirport,
		&f.Aircraft,
		&f.Status,
		&f.DepartureStatus,
		&f.ArrivalStatus,
		&f.ScheduledDepartureAt,
		&f.ScheduledDepartureTimeTz,
		&f.ExpectedDepartureAt,
		&f.ExpectedDepartureTimeTz,
		&f.ActualDepartureAt,
		&f.ActualDepartureTimeTz,
		&f.ScheduledArrivalAt,
		&f.ScheduledArrivalTimeTz,
		&f.ExpectedArrivalAt,
		&f.ExpectedArrivalTimeTz,
		&f.ActualArrivalAt,
		&f.ActualArrivalTimeTz,
		&f.Codeshares,
	)
	return &f, err
}

func safePtr[T any](i *T) T {
	if i == nil {
		return *new(T)
	}
	return *i
}

func toDomain(f *flight) (*parser.Flight, error) {
	var err error

	flight := &parser.Flight{
		ID:                 f.ID,
		FlightID:           f.FlightID,
		ConnectingFlightID: safePtr(f.CFI),
		Airline:            f.Airline,
		FlightNumber:       f.FlightNumber,
		Itinerary:          f.Itinerary,
		Sequence:           f.Sequence,
		DepartureAirport:   f.DepartureAirport,
		ArrivalAirport:     f.ArrivalAirport,
		Aircraft:           f.Aircraft,
		Status:             f.Status,
		DepartureStatus:    f.DepartureStatus,
		ArrivalStatus:      f.ArrivalStatus,
	}

	if flight.ScheduledDepartureAt, err = fromDateAndTimeWithTZ(f.ScheduledDepartureAt, f.ScheduledDepartureTimeTz); err != nil {
		return nil, err
	}
	if flight.ExpectedDepartureAt, err = fromDateAndTimeWithTZ(f.ExpectedDepartureAt, f.ExpectedDepartureTimeTz); err != nil {
		return nil, err
	}
	if flight.ActualDepartureAt, err = fromDateAndTimeWithTZ(f.ActualDepartureAt, f.ActualDepartureTimeTz); err != nil {
		return nil, err
	}

	if flight.ScheduledArrivalAt, err = fromDateAndTimeWithTZ(f.ScheduledArrivalAt, f.ScheduledArrivalTimeTz); err != nil {
		return nil, err
	}
	if flight.ExpectedArrivalAt, err = fromDateAndTimeWithTZ(f.ExpectedArrivalAt, f.ExpectedArrivalTimeTz); err != nil {
		return nil, err
	}
	if flight.ActualArrivalAt, err = fromDateAndTimeWithTZ(f.ActualArrivalAt, f.ActualArrivalTimeTz); err != nil {
		return nil, err
	}

	flight.Codeshares = make([]*parser.Codeshare, 0, len(f.Codeshares))
	for i := range f.Codeshares {
		fn, _ := strconv.Atoi(f.Codeshares[i][1])
		flight.Codeshares = append(flight.Codeshares, &parser.Codeshare{
			Airline:      f.Codeshares[i][0],
			FlightNumber: fn,
		})
	}

	return flight, nil
}

