import (
	"context"
	"go.impalastudios.com/log"
	"go.uber.org/zap"

	"github.com/cockroachdb/errors"
	"github.com/doug-martin/goqu/v9"
	"go.impalastudios.com/aviation/schedules/pkg/concurrent"
	"go.impalastudios.com/aviation/schedules/pkg/pgsql"
)

// oagAirlinesMapper contains the OAG airlines mappings
type oagAirlinesMapper struct {
	m *concurrent.Map[string]
}

// Get returns the airline ID for the given OAG airline ID
// if not found, returns the original OAG airline ID
func (m *oagAirlinesMapper) Get(ctx context.Context, oagID string) string {
	if id, ok := m.m.Get(oagID); ok {
		return id
	}

	return oagID
}

// LoadOagAirlinesMapper fills the OAG airlines mapper with data from the database
func LoadOagAirlinesMapper(ctx context.Context, db *pgsql.DB) (*oagAirlinesMapper, error) {
	m := concurrent.NewMap[string]()

	mappings, err := fetchOagMappings(ctx, db, "SELECT oag_airline_id, airline_id FROM oag_airline_mappings")
	if err != nil {
		return nil, err
	}

	for _, mapping := range mappings {
		m.Set(mapping.OagID, mapping.ID)
	}

	log.Debug(ctx, "Airlines mappings loaded.", zap.Int("count", m.Size()))

	return &oagAirlinesMapper{m: m}, nil
}

type OagMapping struct {
	OagID string
	ID    string
}

func fetchOagMappings(ctx context.Context, db *pgsql.DB, q string) ([]OagMapping, error) {
	mappings := make([]OagMapping, 0)

	rows, err := db.Query(ctx, q)
	if err != nil {
		return nil, errors.Wrap(err, "db: failed to query oag_airport_mappings")
	}

	for rows.Next() {
		var mapping OagMapping

		if err := rows.Scan(&mapping.OagID, &mapping.ID); err != nil {
			return nil, errors.Wrap(err, "db: failed to scan oag_airport_mappings")
		}

		mappings = append(mappings, mapping)
	}

	return mappings, nil
}

func UpsertAirlineMappings(ctx context.Context, db *pgsql.DB, mappings []OagMapping) error {
	records := make([]goqu.Record, 0)
	for _, mapping := range mappings {
		records = append(records, goqu.Record{
			"oag_airline_id": mapping.OagID,
			"airline_id":     mapping.ID,
		})
	}

	q, args, err := goqu.Insert("oag_airline_mappings").
		Rows(records).
		OnConflict(goqu.DoUpdate("oag_airline_id", goqu.Record{
			"airline_id": goqu.L("EXCLUDED.airline_id"),
		})).
		ToSQL()
	if err != nil {
		return errors.Wrap(err, "repo: failed to upsert devices")
	}

	_, err = db.Exec(ctx, q, args...)
	return errors.Wrap(err, "repo: failed to upsert devices")
}

package db

import (
	"context"

	"github.com/pkg/errors"
	"go.impalastudios.com/aviation/schedules/pkg/concurrent"
)

type schema struct {
	seq *seq

	codeshareMapper *concurrent.Map[int64]
}

func (i *Ingester) loadSchema(ctx context.Context, schemaName string) (*schema, error) {
	var exists bool
	q := "SELECT EXISTS(SELECT 1 FROM information_schema.schemata WHERE schema_name = $1)"
	if err := i.db.QueryRow(ctx, q, schemaName).Scan(&exists); err != nil {
		return nil, errors.Wrap(err, "db: failed to check if schema exists")
	}

	if !exists {
		return nil, errors.New("db: schema does not exist")
	}

	seq, err := findLastSeq(ctx, schemaName, i.db)
	if err != nil {
		return nil, errors.Wrap(err, "db: failed to find last seq")
	}

	return &schema{
		seq:             seq,
		codeshareMapper: concurrent.NewMap[int64](),
	}, nil
}

func (i *Ingester) createSchema(ctx context.Context, schemaName string) (*schema, error) {
	if _, err := i.db.Exec(ctx, "CREATE SCHEMA IF NOT EXISTS "+schemaName); err != nil {
		return nil, errors.Wrap(err, "db: failed to create schema")
	}

	return &schema{
		seq: &seq{
			ConnectingFlightID: concurrent.NewAtomicInt64(1),
			FlightID:           concurrent.NewAtomicInt64(1),
		},
		codeshareMapper: concurrent.NewMap[int64](),
	}, nil
}

package db

import (
	"context"
	"database/sql"
	"fmt"

	"github.com/cockroachdb/errors"
	"go.impalastudios.com/aviation/schedules/pkg/concurrent"
	"go.impalastudios.com/aviation/schedules/pkg/pgsql"
)

type seq struct {
	ConnectingFlightID *concurrent.AtomicInt64
	FlightID           *concurrent.AtomicInt64
}

// findLastSeq finds the last sequences in the database
func findLastSeq(ctx context.Context, schema string, db *pgsql.DB) (*seq, error) {
	var (
		nullableConnectingFlightID sql.NullInt64
		nullableFlightID           sql.NullInt64
	)

	q := fmt.Sprintf(`SELECT 
 (SELECT MAX(id) FROM %[1]s.flights) AS flight_id,
 (SELECT MAX(id) FROM %[1]s.connecting_flights) AS connecting_flight_id`,
		schema,
	)
	err := db.QueryRow(ctx, q).Scan(
		&nullableFlightID,
		&nullableConnectingFlightID,
	)

	return &seq{
		ConnectingFlightID: concurrent.NewAtomicInt64(nullableConnectingFlightID.Int64 + 1),
		FlightID:           concurrent.NewAtomicInt64(nullableFlightID.Int64 + 1),
	}, errors.Wrap(err, "db: failed to scan last seq")
}

package pgsql

import (
	"context"
	"fmt"

	"github.com/cockroachdb/errors"
	"github.com/jackc/pgx/v4/pgxpool"
)

// DB wraps pgxpool.Pool
type DB struct {
	*pgxpool.Pool
}

// Connect connects to the database and returns the DB instance
func Connect(ctx context.Context, cfg *Config) (*DB, error) {
	datasource := cfg.datasource()

	poolCfg, err := pgxpool.ParseConfig(datasource)
	if err != nil {
		return nil, errors.Wrap(err, "connect: could not parse pgxpool config")
	}

	pool, err := pgxpool.ConnectConfig(ctx, poolCfg)
	return &DB{pool}, errors.Wrap(err, "could not connect with pgxpool")
}

// Config database configuration
type Config struct {
	Host     string
	Port     int
	User     string
	Password string
	DBName   string

	schemaName string
}

func (c *Config) datasource() string {
	return fmt.Sprintf("host=%s port=%d user=%s password=%s dbname=%s search_path=%s",
		c.Host, c.Port, c.User, c.Password, c.DBName, c.schemaName,
	)
}

package pgsql

import (
	"bytes"
	"database/sql"
	"database/sql/driver"
	"fmt"
	"github.com/lib/pq"
	"strings"
)

func Array(a interface{}) interface {
	driver.Valuer
	sql.Scanner
} {
	switch a := a.(type) {
	case []bool:
		return (*pq.BoolArray)(&a)
	case []float64:
		return (*pq.Float64Array)(&a)
	case []float32:
		return (*pq.Float32Array)(&a)
	case []int64:
		return (*pq.Int64Array)(&a)
	case []int32:
		return (*pq.Int32Array)(&a)
	case []string:
		return (*pq.StringArray)(&a)
	case [][]string:
		return (*ArrayStringArray)(&a)
	case [][]byte:
		return (*pq.ByteaArray)(&a)

	case *[]bool:
		return (*pq.BoolArray)(a)
	case *[]float64:
		return (*pq.Float64Array)(a)
	case *[]float32:
		return (*pq.Float32Array)(a)
	case *[]int64:
		return (*pq.Int64Array)(a)
	case *[]int32:
		return (*pq.Int32Array)(a)
	case *[]string:
		return (*pq.StringArray)(a)
	case *[][]string:
		return (*ArrayStringArray)(a)
	case *[][]byte:
		return (*pq.ByteaArray)(a)
	}

	return pq.GenericArray{a}
}

// ArrayStringArray represents a two-dimensional array of the PostgreSQL character types.
type ArrayStringArray [][]string

// Scan implements the sql.Scanner interface.
func (a *ArrayStringArray) Scan(src interface{}) error {
	switch src := src.(type) {
	case []byte:
		return a.scanBytes(src)
	case string:
		return a.scanBytes([]byte(src))
	case nil:
		*a = nil
		return nil
	}

	return fmt.Errorf("pq: cannot convert %T to StringArray", src)
}

func (a *ArrayStringArray) scanBytes(src []byte) error {
	elems, err := scanTwoDimensionalArray(src, []byte{','}, "ArrayStringArray")
	if err != nil {
		return err
	}
	if *a != nil && len(elems) == 0 {
		*a = (*a)[:0]
	} else {
		b := make(ArrayStringArray, len(elems))
		for i, v := range elems {
			b[i] = make([]string, len(v))
			for j, vv := range v {
				if b[i][j] = string(vv); v == nil {
					return fmt.Errorf("pq: parsing array element index (%d,%d): cannot convert nil to string", i, j)
				}
			}
		}
		*a = b
	}
	return nil
}

// Value implements the driver.Valuer interface.
func (a ArrayStringArray) Value() (driver.Value, error) {
	if a == nil {
		return nil, nil
	}

	if n := len(a); n > 0 {
		// There will be at least two curly brackets, 2*N bytes of quotes,
		// and N-1 bytes of delimiters.
		b := make([]byte, 1, 1+3*n)
		b[0] = '{'

		b = appendArrayQuotedBytes(b, nestedStringArray(a[0]))
		for i := 1; i < n; i++ {
			b = append(b, ',')
			b = appendArrayQuotedBytes(b, nestedStringArray(a[i]))
		}

		return string(append(b, '}')), nil
	}

	return "{}", nil
}

func nestedStringArray(a []string) []byte {
	if n := len(a); n > 0 {
		// There will be at least two curly brackets, 2*N bytes of quotes,
		// and N-1 bytes of delimiters.
		b := make([]byte, 1, 1+3*n)
		b[0] = '{'

		b = appendArrayQuotedBytes(b, []byte(a[0]))
		for i := 1; i < n; i++ {
			b = append(b, ',')
			b = appendArrayQuotedBytes(b, []byte(a[i]))
		}

		return append(b, '}')
	}

	return []byte("{}")
}

func appendArrayQuotedBytes(b, v []byte) []byte {
	b = append(b, '"')
	for {
		i := bytes.IndexAny(v, `"\`)
		if i < 0 {
			b = append(b, v...)
			break
		}
		if i > 0 {
			b = append(b, v[:i]...)
		}
		b = append(b, '\\', v[i])
		v = v[i+1:]
	}
	return append(b, '"')
}

func scanTwoDimensionalArray(src, del []byte, typ string) ([][][]byte, error) {
	dims, elems, err := parseArray(src, del)
	if err != nil {
		return nil, err
	}
	if len(dims) > 2 {
		return nil, fmt.Errorf("pq: cannot convert ARRAY%s to %s", strings.Replace(fmt.Sprint(dims), " ", "][", -1), typ)
	}

	r := make([][][]byte, dims[0])
	for i := 0; i < dims[0]; i++ {
		r[i] = make([][]byte, dims[1])
		for j := 0; j < dims[1]; j++ {
			r[i][j] = elems[dims[1]*i+j]
		}
	}

	return r, err
}

// parseArray extracts the dimensions and elements of an array represented in
// text format. Only representations emitted by the backend are supported.
// Notably, whitespace around brackets and delimiters is significant, and NULL
// is case-sensitive.
//
// See http://www.postgresql.org/docs/current/static/arrays.html#ARRAYS-IO
func parseArray(src, del []byte) (dims []int, elems [][]byte, err error) {
	var depth, i int

	if len(src) < 1 || src[0] != '{' {
		return nil, nil, fmt.Errorf("pq: unable to parse array; expected %q at offset %d", '{', 0)
	}

Open:
	for i < len(src) {
		switch src[i] {
		case '{':
			depth++
			i++
		case '}':
			elems = make([][]byte, 0)
			goto Close
		default:
			break Open
		}
	}
	dims = make([]int, i)

Element:
	for i < len(src) {
		switch src[i] {
		case '{':
			if depth == len(dims) {
				break Element
			}
			depth++
			dims[depth-1] = 0
			i++
		case '"':
			var elem = []byte{}
			var escape bool
			for i++; i < len(src); i++ {
				if escape {
					elem = append(elem, src[i])
					escape = false
				} else {
					switch src[i] {
					default:
						elem = append(elem, src[i])
					case '\\':
						escape = true
					case '"':
						elems = append(elems, elem)
						i++
						break Element
					}
				}
			}
		default:
			for start := i; i < len(src); i++ {
				if bytes.HasPrefix(src[i:], del) || src[i] == '}' {
					elem := src[start:i]
					if len(elem) == 0 {
						return nil, nil, fmt.Errorf("pq: unable to parse array; unexpected %q at offset %d", src[i], i)
					}
					if bytes.Equal(elem, []byte("NULL")) {
						elem = nil
					}
					elems = append(elems, elem)
					break Element
				}
			}
		}
	}

	for i < len(src) {
		if bytes.HasPrefix(src[i:], del) && depth > 0 {
			dims[depth-1]++
			i += len(del)
			goto Element
		} else if src[i] == '}' && depth > 0 {
			dims[depth-1]++
			depth--
			i++
		} else {
			return nil, nil, fmt.Errorf("pq: unable to parse array; unexpected %q at offset %d", src[i], i)
		}
	}

Close:
	for i < len(src) {
		if src[i] == '}' && depth > 0 {
			depth--
			i++
		} else {
			return nil, nil, fmt.Errorf("pq: unable to parse array; unexpected %q at offset %d", src[i], i)
		}
	}
	if depth > 0 {
		err = fmt.Errorf("pq: unable to parse array; expected %q at offset %d", '}', i)
	}
	if err == nil {
		for _, d := range dims {
			if (len(elems) % d) != 0 {
				err = fmt.Errorf("pq: multidimensional arrays must have elements with matching dimensions")
			}
		}
	}
	return
}

package pgsql

import (
	"database/sql"
	"embed"

	"github.com/cockroachdb/errors"
	"github.com/golang-migrate/migrate/v4"
	"github.com/golang-migrate/migrate/v4/database/pgx"
	_ "github.com/golang-migrate/migrate/v4/source/file"
	"github.com/golang-migrate/migrate/v4/source/iofs"
)

//go:embed migrations/*.sql
var migrationsfs embed.FS

//go:embed migrations/public/*.sql
var publicMigrations embed.FS

// MigrateUpWithSchema runs migrations for all sql files using pgx driver
// on the specified schema
func MigrateUpWithSchema(conf *Config, schema string) error {
	// Switch to any schema, if public is given the default public schema will be used
	conf.schemaName = schema

	var (
		fs   = migrationsfs
		path = "migrations"
	)
	if schema == "public" {
		fs = publicMigrations
		path = "migrations/public"
	}

	sourceDriver, err := iofs.New(fs, path)
	if err != nil {
		return errors.Wrap(err, "failed to create source driver")
	}

	datasource := conf.datasource()

	instance, err := sql.Open("pgx", datasource)
	if err != nil {
		return errors.Wrap(err, "failed to open database")
	}

	dbDriver, err := pgx.WithInstance(instance, &pgx.Config{SchemaName: schema})
	if err != nil {
		return errors.Wrap(err, "failed to create pgx driver")
	}

	migrator, err := migrate.NewWithInstance("iofs", sourceDriver, "pgx", dbDriver)
	if err != nil {
		return errors.Wrap(err, "failed to create migrator")
	}

	if err := migrator.Up(); err != nil && !errors.Is(err, migrate.ErrNoChange) {
		return errors.Wrap(err, "failed to migrate up")
	}

	return nil
}

// MigrateUp runs migrations for all sql files using pgx driver
func MigrateUp(conf *Config) error {
	return MigrateUpWithSchema(conf, "public")
}

package xhttp

// Pagination represents the pagination metadata
type Pagination struct {
	Limit uint    `json:"limit"`
	Next  *string `json:"next"`
}

// Metadata contains the information about the response
type Metadata struct {
	Code       string      `json:"code"`
	Message    string      `json:"message"`
	Pagination *Pagination `json:"pagination,omitempty"`
}

// Response is the http response format
type Response[T any] struct {
	Metadata Metadata `json:"meta"`
	Data     T        `json:"data"`
}

// NewResponse creates a new response
func NewResponse[T any](data T, code string, message string) *Response[T] {
	return &Response[T]{
		Metadata: Metadata{
			Code:    code,
			Message: message,
		},
		Data: data,
	}
}

// NewPaginatedResponse creates a new paginated response
func NewPaginatedResponse[T any](data T, code, message string, limit uint, nextPageToken *string) *Response[T] {
	return &Response[T]{
		Metadata: Metadata{
			Code:    code,
			Message: message,
			Pagination: &Pagination{
				Limit: limit,
				Next:  nextPageToken,
			},
		},
		Data: data,
	}
}

// NewErrorResponse creates a new error response
func NewErrorResponse[T any](code string, message string) *Response[T] {
	return &Response[T]{
		Metadata: Metadata{
			Code:    code,
			Message: message,
		},
		Data: getZero[T](),
	}
}

func getZero[T any]() T {
	var z T
	return z
}

