package i18n

import (
	pi18n "github.com/nicksnyder/go-i18n/v2/i18n"
	"golang.org/x/text/language"
)

var SupportedLanguages = []language.Tag{
	language.English,
	language.Dutch,
	language.French,
	language.German,
	language.Italian,
	language.Japanese,
	language.Korean,
	language.Portuguese,
	language.Russian,
	language.Spanish,
	language.Swedish,
	language.Chinese,
	language.Finnish,
	language.Norwegian,
	language.Danish,
	language.Thai,
	language.Arabic,
}

var matcher = language.NewMatcher(SupportedLanguages)

func GetLocaleFromHeader(header string) language.Tag {
	tags, _, _ := language.ParseAcceptLanguage(header)
	locale, _, _ := matcher.Match(tags...)
	return locale
}

func Localise(text map[string]string, locale language.Tag) string {
	bundle := pi18n.NewBundle(language.English)

	for l, t := range text {
		if loc, err := language.Parse(l); err == nil {
			_ = bundle.AddMessages(loc, &pi18n.Message{
				ID:    "Translation",
				Other: t,
			})
		}
	}

	localiser := pi18n.NewLocalizer(bundle, locale.String())
	if t, err := localiser.Localize(&pi18n.LocalizeConfig{MessageID: "Translation"}); err == nil {
		return t
	}

	return ""
}

func LocaliseNillable(text map[string]string, locale language.Tag) *string {
	bundle := pi18n.NewBundle(language.English)

	for l, t := range text {
		if loc, err := language.Parse(l); err == nil {
			_ = bundle.AddMessages(loc, &pi18n.Message{
				ID:    "Translation",
				Other: t,
			})
		}
	}

	localiser := pi18n.NewLocalizer(bundle, locale.String())
	if t, err := localiser.Localize(&pi18n.LocalizeConfig{MessageID: "Translation"}); err == nil {
		return &t
	}

	return nil
}

func IsSupported(l language.Tag) bool {
	for _, s := range SupportedLanguages {
		if s == l {
			return true
		}
	}

	return false
}

package i18n

import (
	"errors"
	"github.com/go-playground/locales/en"
	ut "github.com/go-playground/universal-translator"
	"github.com/go-playground/validator/v10"
	enTranslations "github.com/go-playground/validator/v10/translations/en"
)

var (
	Translator ut.Translator
)

func Register(v *validator.Validate) {
	enLocale := en.New()
	uni := ut.New(enLocale, enLocale)
	Translator, _ = uni.GetTranslator("en")
	_ = enTranslations.RegisterDefaultTranslations(v, Translator)
}

func GetFirstValidationErrorMessage(err error) string {
	var message string
	var validationErrors validator.ValidationErrors
	if errors.As(err, &validationErrors) {
		if len(validationErrors) > 0 {
			message = validationErrors[0].Translate(Translator)
		} else {
			message = "The input parameters are invalid."
		}
	}

	return message
}

package xaws

import (
	"context"

	"github.com/aws/aws-sdk-go-v2/aws"
	"github.com/aws/aws-sdk-go-v2/config"
	"github.com/aws/aws-sdk-go-v2/credentials"
)

type Config struct {
	AccessKeyID     string `mapstructure:"access-key-id"`
	SecretAccessKey string `mapstructure:"secret-access-key"`
	SessionToken    string `mapstructure:"session-token"`
	EndpointURL     string `mapstructure:"endpoint-url"`
	Region          string `mapstructure:"region"`
	S3              struct {
		UsePathStyle bool `mapstructure:"use-path-style"`
	} `mapstructure:"s3"`
}

func GetConfig(ctx context.Context, cfg *Config) (aws.Config, error) {
	var optFns []func(*config.LoadOptions) error

	if cfg.AccessKeyID != "" || cfg.SecretAccessKey != "" || cfg.SessionToken != "" {
		optFns = append(optFns, config.WithCredentialsProvider(
			credentials.NewStaticCredentialsProvider(
				cfg.AccessKeyID, cfg.SecretAccessKey, cfg.SessionToken,
			),
		))
	}

	if cfg.Region != "" {
		optFns = append(optFns, config.WithRegion(cfg.Region))
	}

	if cfg.EndpointURL != "" {
		optFns = append(optFns, config.WithBaseEndpoint(cfg.EndpointURL))
	}

	awsCfg, err := config.LoadDefaultConfig(ctx, optFns...)

	if err != nil {
		return aws.Config{}, err
	}

	return awsCfg, nil
}
