package main

import (
	"go.impalastudios.com/weather/forecasts/cmd"
)

func main() {
	if err := cmd.RunRootCmd(); err != nil {
		panic(err)
	}
}

package cmd

import (
	"go.impalastudios.com/weather/forecasts/cmd/serve"
	"strings"

	"github.com/spf13/cobra"
	"github.com/spf13/viper"
)

// RunRootCmd entrypoint of the root command
func RunRootCmd() error {
	viper.AutomaticEnv()
	viper.SetEnvKeyReplacer(strings.NewReplacer(".", "_", "-", "_"))

	cmd := &cobra.Command{
		Use:     "weather-forecasts",
		Short:   "[Weather] Forecast microservice",
		Version: "0.0.1",
	}

	cmd.AddCommand(serve.New())

	cmd.PersistentFlags().Bool("logging.debug", false, "Whether to emit debug level logs or not.")
	cmd.PersistentFlags().String("logging.encoding", "json", "The log encoding strategy.")
	cmd.PersistentFlags().StringP("app.name", "n", "weather-forecasts", "The application name.")

	cmd.PersistentFlags().String("services.foreca-proxy-endpoint", "", "The endpoint of the Foreca Proxy microservice.")
	cmd.PersistentFlags().String("services.locations-endpoint", "", "The endpoint of the Locations microservice.")

	_ = viper.BindPFlags(cmd.PersistentFlags())

	return cmd.Execute()
}

func runServeHttp(cmd *cobra.Command, _ []string) error {
	quit := make(chan os.Signal, 1)
	defer close(quit)

	signal.Notify(quit, os.Interrupt)

	logger := log.New(
		log.WithService(viper.GetString("app.name")),
		log.WithEncoding(viper.GetString("logging.encoding")),
		log.WithDebugEnabled(viper.GetBool("logging.debug")),
	)
	log.SetLogger(logger)

	tp, err := xotel.NewTracerProvider(cmd.Context(), xotel.TracerProviderConfig{
		Endpoint: viper.GetString("otel.tracer.endpoint"),
	})
	if err != nil {
		return errors.Wrap(err, "failed to create tracer provider")
	}

	otel.SetTracerProvider(tp)
	otel.SetTextMapPropagator(propagation.NewCompositeTextMapPropagator(propagation.TraceContext{}, propagation.Baggage{}))
	xotel.SetGlobalTracer(tp.Tracer(viper.GetString("app.name")))

	resolver.Register(dnssrvresolver.New())

	forecaGrpcClient := forecaPbv0.NewForecaProxyClient(getGrpcConnection(viper.GetString("services.foreca-proxy-endpoint")))
	locationsGrpcClient := locationsPbv0.NewLocationsServiceClient(getGrpcConnection(viper.GetString("services.locations-endpoint")))

	forecaService := adapters.NewForecaService(forecaGrpcClient)
	locationsService := adapters.NewLocationsService(locationsGrpcClient)

	forecastService := forecasts.NewService(forecaService, locationsService)

	router := gin.New()
	router.Use(requestid.New()...)
	router.Use(
		ginzap.GinzapWithConfig(logger, &ginzap.Config{
			TimeFormat: time.RFC3339,
			UTC:        true,
			SkipPaths:  []string{"/__gw/forecasts/health", "/__gw/health"},
			Context: func(c *gin.Context) []zapcore.Field {
				return []zapcore.Field{
					log.RequestIDExtractor(c),
					zap.Bool("is_legacy_request", c.GetHeader("X-Legacy-Endpoint") == "true"),
				}
			},
		}),
		ginzap.RecoveryWithZap(logger, true),
	)
	router.Use(otelgin.Middleware(
		viper.GetString("app.name"),
		otelgin.WithTracerProvider(tp),
		otelgin.WithFilter(func(req *http.Request) bool {
			if req.URL.Path == "/__gw/health" {
				return false
			}

			return true
		}),
	))
	legacyGw := router.Group("/__gw/forecasts")
	gw := router.Group("/__gw")

	handlerV0 := v0.New(forecastService)
	handlerV0.Register(gw)
	handlerV0.Register(legacyGw)

	healthHandler := httphandlers.NewHealthHandler(logger)
	healthHandler.Register(gw)
	healthHandler.Register(legacyGw)

	if v, ok := binding.Validator.Engine().(*validator.Validate); ok {
		customrules.Register(v)
	}

	srv := http.Server{
		Addr:    fmt.Sprintf(":%d", viper.GetInt("app.http.port")),
		Handler: router,
	}

	if err := srv.ListenAndServe(); err != nil && err != http.ErrServerClosed {
		logger.Error("Error occurred while listening.", zap.Error(err))
	}

	<-quit

	logger.Info("Shutting down the REST server...")

	ctx, cancel := context.WithTimeout(context.Background(), 5*time.Second)
	defer cancel()

	if err := srv.Shutdown(ctx); err != nil {
		logger.Error("Error occurred while shutting down.", zap.Error(err))
	}

	if err := tp.Shutdown(ctx); err != nil {
		log.Error(context.TODO(), "Error occurred while shutting down the tracer.", zap.Error(err))
	}

	select {
	case <-ctx.Done():
		logger.Info("REST server shutdown timed out.")
	default:
		logger.Info("REST server shutdown gracefully.")
	}

	return nil
}

package serve

import (
	"github.com/spf13/cobra"
	"go.opentelemetry.io/contrib/instrumentation/google.golang.org/grpc/otelgrpc"
	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials/insecure"
	"time"
)

func New() *cobra.Command {
	cmd := &cobra.Command{
		Use:   "serve",
		Short: "Serve",
	}

	cmd.AddCommand(newServeHttpCommand())

	cmd.PersistentFlags().String("otel.tracer.endpoint", "localhost:4317", "The URL of the tracing collector.")
	cmd.PersistentFlags().String("otel.meter.endpoint", "localhost:4317", "The URL of the tracing collector.")
	cmd.PersistentFlags().Duration("otel.meter.interval", 60*time.Second, "The interval which the metrics will be reported to the collector.")

	return cmd
}

func getGrpcConnection(target string) *grpc.ClientConn {
	conn, err := grpc.NewClient(
		target,
		grpc.WithDefaultServiceConfig(`{"loadBalancingConfig": [{"round_robin":{}}]}`),
		grpc.WithTransportCredentials(insecure.NewCredentials()),
		grpc.WithStatsHandler(otelgrpc.NewClientHandler()),
	)
	if err != nil {
		panic(err)
	}

	return conn
}

package forecasts

import (
	"context"
	"go.impalastudios.com/weather/forecasts/pkg/xotel"
	"go.opentelemetry.io/otel/trace"
	"golang.org/x/text/language"
)

type forecaService interface {
	GetForecastForLocation(ctx context.Context, id int) (*Forecast, error)
	GetForecastForLocations(ctx context.Context, ids []int) ([]*Forecast, error)
}

type locationsService interface {
	Get(ctx context.Context, id int, locale language.Tag) (*Location, error)
	GetBatch(ctx context.Context, ids []int, locale language.Tag) ([]*Location, error)
	Nearest(ctx context.Context, coordinate Coordinate, locale language.Tag) (*Location, error)
	BatchNearest(ctx context.Context, coordinates []Coordinate, locale language.Tag) ([]*Location, error)
}

type Service struct {
	foreca    forecaService
	locations locationsService
}

func NewService(foreca forecaService, locations locationsService) *Service {
	return &Service{
		foreca:    foreca,
		locations: locations,
	}
}

func (s *Service) GetForecastsForLocation(ctx context.Context, id int, locale language.Tag) (*Forecast, *Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "service:forecasts-location", trace.WithSpanKind(trace.SpanKindInternal))
	defer span.End()

	location, err := s.locations.Get(ctx, id, locale)
	if err != nil {
		return nil, nil, err
	}

	forecast, err := s.foreca.GetForecastForLocation(ctx, id)
	if err != nil {
		return nil, nil, err
	}

	return forecast, location, nil
}

func (s *Service) GetForecastsForLocations(ctx context.Context, ids []int, locale language.Tag) ([]*Forecast, []*Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "service:forecasts-locations", trace.WithSpanKind(trace.SpanKindInternal))
	defer span.End()

	locations, err := s.locations.GetBatch(ctx, ids, locale)
	if err != nil {
		return nil, nil, err
	}

	fcs, err := s.foreca.GetForecastForLocations(ctx, ids)
	if err != nil {
		return nil, nil, err
	}

	return fcs, locations, nil
}

func (s *Service) GetForecastForClosestLocation(ctx context.Context, coordinate Coordinate, locale language.Tag) (*Forecast, *Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "service:forecasts-nearest-location", trace.WithSpanKind(trace.SpanKindInternal))
	defer span.End()

	location, err := s.locations.Nearest(ctx, coordinate, locale)
	if err != nil {
		return nil, nil, err
	}

	forecast, err := s.foreca.GetForecastForLocation(ctx, int(location.ID))
	if err != nil {
		return nil, nil, err
	}

	return forecast, location, nil
}

func (s *Service) GetForecastForClosestLocations(ctx context.Context, coordinates []Coordinate, locale language.Tag) ([]*Forecast, []*Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "service:forecasts-nearest-locations", trace.WithSpanKind(trace.SpanKindInternal))
	defer span.End()

	locations, err := s.locations.BatchNearest(ctx, coordinates, locale)
	if err != nil {
		return nil, nil, err
	}

	ids := make([]int, len(locations))
	for i, l := range locations {
		ids[i] = int(l.ID)
	}

	fcs, err := s.foreca.GetForecastForLocations(ctx, ids)
	if err != nil {
		return nil, nil, err
	}

	return fcs, locations, nil
}

package forecasts

import "errors"

var (
	ErrGeneric          = errors.New("forecast service: cannot get forecast for location")
	ErrLocationNotFound = errors.New("forecast service: location not found")
)

package adapters

import (
	"context"
	"github.com/tidwall/limiter"
	"go.impalastudios.com/log"
	"go.impalastudios.com/requestid"
	"go.impalastudios.com/weather/forecasts/internal/forecasts"
	pbv0 "go.impalastudios.com/weather/forecasts/internal/forecasts/adapters/clients/foreca/v0"
	"go.impalastudios.com/weather/forecasts/pkg/xotel"
	"go.opentelemetry.io/otel/attribute"
	otelcodes "go.opentelemetry.io/otel/codes"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"
	"google.golang.org/grpc/metadata"
	"sync"
	"time"
)

type ForecaService struct {
	client pbv0.ForecaProxyClient
}

func NewForecaService(client pbv0.ForecaProxyClient) *ForecaService {
	return &ForecaService{client}
}

func (s *ForecaService) GetForecastForLocation(ctx context.Context, id int) (*forecasts.Forecast, error) {
	ctx, span := xotel.Tracer.Start(ctx, "foreca-service:get", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	span.SetAttributes(attribute.Int("id", id))

	ctx = metadata.AppendToOutgoingContext(ctx, "request-id", requestid.GetRequestID(ctx))
	resp, err := s.client.GetForecast(ctx, &pbv0.ForecastRequest{LocationId: int64(id)})

	if err != nil {
		span.SetStatus(otelcodes.Error, "Cannot get forecasts.")
		span.RecordError(err)

		log.Error(ctx, "Error retrieving forecast for location.", zap.Error(err))
		return nil, err
	}

	return s.toDomain(resp), nil
}

func (s *ForecaService) GetForecastForLocations(ctx context.Context, ids []int) ([]*forecasts.Forecast, error) {
	ctx, span := xotel.Tracer.Start(ctx, "foreca-service:batch-get", trace.WithSpanKind(trace.SpanKindInternal))
	defer span.End()

	l := limiter.New(5)

	var wg sync.WaitGroup
	wg.Add(len(ids))

	fcs := make([]*forecasts.Forecast, len(ids))
	for i, id := range ids {
		go func(index int, locationId int) {
			l.Begin()
			defer func() {
				wg.Done()
				l.End()
			}()

			ctx, span := xotel.Tracer.Start(ctx, "foreca-service:get", trace.WithSpanKind(trace.SpanKindClient))
			defer span.End()

			span.SetAttributes(attribute.Int("id", locationId))

			ctx = metadata.AppendToOutgoingContext(ctx, "request-id", requestid.GetRequestID(ctx))
			resp, err := s.client.GetForecast(ctx, &pbv0.ForecastRequest{LocationId: int64(locationId)})

			if err != nil {
				log.Error(ctx, "Error retrieving forecast for location.", zap.Int("location_id", locationId), zap.Error(err))
				span.RecordError(err)
				span.SetStatus(otelcodes.Error, "Error retrieving forecast for location.")
				return
			}

			fcs[index] = s.toDomain(resp)
		}(i, id)
	}

	wg.Wait()

	return fcs, nil
}

func (s *ForecaService) toDomain(response *pbv0.ForecastResponse) *forecasts.Forecast {

	var dcs []*forecasts.DailyCondition
	for _, condition := range response.GetDailyForecasts() {
		dcs = append(dcs, s.toDailyConditionDomain(condition))
	}

	var hcs []*forecasts.HourlyCondition
	for _, condition := range response.GetHourlyForecasts() {
		hcs = append(hcs, s.toHourlyConditionDomain(condition))
	}

	cc := s.toCurrentConditionDomain(response.GetCurrentCondition())

	if len(dcs) > 0 {
		day := dcs[0]

		if cc.Epoch != nil && day.Sunrise != nil && day.Sunset != nil {
			cc.IsNight = cc.Epoch.Before(*day.Sunrise) || cc.Epoch.After(*day.Sunset)
		}
	}

	return &forecasts.Forecast{
		CurrentCondition: cc,
		DailyConditions:  dcs,
		HourlyConditions: hcs,
	}
}

func (s *ForecaService) toCurrentConditionDomain(current *pbv0.ForecastResponse_CurrentCondition) *forecasts.CurrentCondition {
	cc := &forecasts.CurrentCondition{
		WeatherCondition: &forecasts.WeatherCondition{},

		NextHour: struct {
			WeatherCondition *forecasts.WeatherCondition
			Precipitation    float32
		}{WeatherCondition: &forecasts.WeatherCondition{}},
		WindGust: &forecasts.WindGust{Wind: &forecasts.Wind{Direction: &forecasts.WindDirection{}}},
		Pressure: &forecasts.Pressure{},
	}

	if current != nil {
		t := time.Unix(current.Epoch, 0)
		cc.Epoch = &t
		cc.Visibility = current.GetVisibility()
		cc.WeatherCondition.Code = current.GetWeatherCondition().GetCode()
		cc.Precipitation = current.GetPrecipitation()

		if current.GetNextHour() != nil {
			if current.GetNextHour().GetWeatherCondition() != nil {
				cc.NextHour.WeatherCondition.Code = current.GetNextHour().GetWeatherCondition().GetCode()
			}
			cc.NextHour.Precipitation = current.GetNextHour().GetPrecipitation()
		}
		cc.Temperature = current.GetTemperature()
		cc.TemperatureFeelsLike = current.GetTemperatureFeelsLike()
		if current.GetWindGust() != nil {
			cc.WindGust.Gust = current.GetWindGust().GetGust()
			if current.GetWindGust().GetWind() != nil {
				cc.WindGust.Wind = s.toWindDomain(current.GetWindGust().GetWind())
			}
		}
		cc.DewPoint = current.GetDewPoint()
		cc.HeatIndex = current.GetHeatIndex()

		if current.GetPressure() != nil {
			cc.Pressure.Millibar = current.GetPressure().GetMillibar()
			cc.Pressure.Trend = int(current.GetPressure().GetTrend())
		}

		cc.UvIndex = current.GetUvIndex()
		cc.HumidityPercentage = int(current.GetHumidityPercentage())
	}

	return cc
}

func (s *ForecaService) toDailyConditionDomain(daily *pbv0.ForecastResponse_DailyForecast) *forecasts.DailyCondition {
	d := &forecasts.DailyCondition{
		Wind:                  &forecasts.Wind{Direction: &forecasts.WindDirection{}},
		WeatherCondition:      &forecasts.WeatherCondition{},
		WeatherConditionNight: &forecasts.WeatherCondition{},
	}

	if daily != nil {
		t := time.Unix(daily.Epoch, 0)
		d.Epoch = &t

		d.TemperatureMin = daily.GetTemperatureMin()
		d.TemperatureMax = daily.GetTemperatureMax()
		d.Precipitation = daily.GetPrecipitation()
		d.PrecipitationDay = daily.GetPrecipitationDay()
		d.PrecipitationNight = daily.GetPrecipitationNight()
		d.Snow = daily.GetSnow()
		d.SnowDay = daily.GetSnowDay()
		d.SnowNight = daily.GetSnowNight()
		d.PrecipitationProbability = int(daily.GetPrecipitationProbability())

		if daily.GetWind() != nil {
			d.Wind = s.toWindDomain(daily.GetWind())
		}

		d.AverageHumidityPercentage = int(daily.GetAverageHumidityPercentage())
		if daily.GetWeatherCondition() != nil {
			d.WeatherCondition.Code = daily.GetWeatherCondition().GetCode()
		}
		if daily.GetWeatherConditionNight() != nil {
			d.WeatherConditionNight.Code = daily.GetWeatherConditionNight().GetCode()
		}
		d.UvIndex = daily.GetUvIndex()

		rise := time.Unix(daily.GetSunriseEpoch(), 0)
		d.Sunrise = &rise

		set := time.Unix(daily.GetSunsetEpoch(), 0)
		d.Sunset = &set
	}

	return d
}

func (s *ForecaService) toHourlyConditionDomain(hourly *pbv0.ForecastResponse_HourlyForecast) *forecasts.HourlyCondition {
	h := &forecasts.HourlyCondition{
		WeatherCondition: &forecasts.WeatherCondition{},
		Wind:             &forecasts.Wind{Direction: &forecasts.WindDirection{}},
		Pressure:         &forecasts.Pressure{},
	}

	if hourly != nil {
		t := time.Unix(hourly.Epoch, 0)
		h.Epoch = &t

		h.Temperature = hourly.GetTemperature()
		h.TemperatureFeelsLike = hourly.GetTemperatureFeelsLike()
		if hourly.GetWeatherCondition() != nil {
			h.WeatherCondition.Code = hourly.GetWeatherCondition().GetCode()
		}

		h.Precipitation = hourly.GetPrecipitation()
		h.Snow = hourly.GetSnow()
		h.PrecipitationProbability = int(hourly.GetPrecipitationProbability())
		if hourly.GetWind() != nil {
			h.Wind = s.toWindDomain(hourly.GetWind())
		}

		if hourly.GetPressure() != nil {
			h.Pressure.Millibar = hourly.GetPressure().GetMillibar()
			h.Pressure.Trend = int(hourly.GetPressure().GetTrend())
		}

		h.HumidityPercentage = int(hourly.GetHumidityPercentage())
		h.UvIndex = hourly.GetUvIndex()
	}

	return h
}

func (s *ForecaService) toWindDomain(wind *pbv0.ForecastResponse_Wind) *forecasts.Wind {
	w := &forecasts.Wind{Direction: &forecasts.WindDirection{}}

	if wind != nil {
		w.Direction.Abbreviation = wind.GetDirection().GetAbbreviation()
		w.Direction.Degrees = wind.GetDirection().GetDegrees()
		w.Speed = wind.GetSpeed()
	}

	return w
}

package adapters

import (
	"context"
	"fmt"
	"go.impalastudios.com/log"
	"go.impalastudios.com/requestid"
	"go.impalastudios.com/weather/forecasts/internal/forecasts"
	pbv0 "go.impalastudios.com/weather/forecasts/internal/forecasts/adapters/clients/locations/v0"
	"go.impalastudios.com/weather/forecasts/pkg/xotel"
	"go.opentelemetry.io/otel/attribute"
	otelcodes "go.opentelemetry.io/otel/codes"
	"go.opentelemetry.io/otel/trace"
	"go.uber.org/zap"
	"golang.org/x/text/language"
	"google.golang.org/grpc/codes"
	"google.golang.org/grpc/metadata"
	"google.golang.org/grpc/status"
)

type LocationsService struct {
	client pbv0.LocationsServiceClient
}

func NewLocationsService(client pbv0.LocationsServiceClient) *LocationsService {
	return &LocationsService{client}
}

func (s *LocationsService) Get(ctx context.Context, id int, locale language.Tag) (*forecasts.Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "locations-service:get", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	span.SetAttributes(attribute.Int("id", id), attribute.String("locale", locale.String()))

	md := metadata.Pairs("accept-language", locale.String(), "request-id", requestid.GetRequestID(ctx))
	ctx = metadata.NewOutgoingContext(ctx, md)
	resp, err := s.client.Get(ctx, &pbv0.GetRequest{Id: int32(id)})

	if err != nil {
		span.SetStatus(otelcodes.Error, "Cannot get location.")
		span.RecordError(err)

		if statusErr, ok := status.FromError(err); ok && statusErr.Code() == codes.NotFound {
			return nil, forecasts.ErrLocationNotFound
		}

		log.Error(ctx, "Error retrieving nearest location.", zap.String("locale", locale.String()), zap.Int("id", id), zap.Error(err))

		return nil, err
	}

	return s.toDomain(resp.GetLocation()), nil
}

func (s *LocationsService) GetBatch(ctx context.Context, ids []int, locale language.Tag) ([]*forecasts.Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "locations-service:batch-get", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	span.SetAttributes(attribute.IntSlice("ids", ids), attribute.String("locale", locale.String()))

	req := &pbv0.GetBatchRequest{Ids: []int32{}}
	for _, id := range ids {
		req.Ids = append(req.Ids, int32(id))
	}

	ctx = metadata.AppendToOutgoingContext(ctx, "accept-language", locale.String(), "request-id", requestid.GetRequestID(ctx))
	resp, err := s.client.GetBatch(ctx, req)

	if err != nil {
		span.SetStatus(otelcodes.Error, "Cannot get locations batch.")
		span.RecordError(err)

		log.Error(ctx, "Error retrieving locations.", zap.Ints("ids", ids), zap.String("locale", locale.String()), zap.Error(err))
		return nil, err
	}

	locs := make([]*forecasts.Location, len(resp.GetLocations()))
	for i, l := range resp.GetLocations() {
		locs[i] = s.toDomain(l)
	}

	return locs, nil
}

func (s *LocationsService) Nearest(ctx context.Context, coordinate forecasts.Coordinate, locale language.Tag) (*forecasts.Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "locations-service:nearest", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	span.SetAttributes(attribute.String("coordinate", fmt.Sprintf("%.6f,%.6f", coordinate.Lat, coordinate.Lon)), attribute.String("locale", locale.String()))

	ctx = metadata.AppendToOutgoingContext(ctx, "accept-language", locale.String(), "request-id", requestid.GetRequestID(ctx))
	resp, err := s.client.Nearest(ctx,
		&pbv0.NearestRequest{
			Latitude:  float32(coordinate.Lat),
			Longitude: float32(coordinate.Lon),
			Types:     []pbv0.Type{pbv0.Type_P, pbv0.Type_T},
		},
	)

	if err != nil {
		span.SetStatus(otelcodes.Error, "Cannot get nearest location.")
		span.RecordError(err)

		if statusErr, ok := status.FromError(err); ok && statusErr.Code() == codes.NotFound {
			return nil, forecasts.ErrLocationNotFound
		}

		log.Error(ctx, "Error retrieving nearest location.", zap.Any("coordinate", coordinate), zap.String("locale", locale.String()), zap.Error(err))
		return nil, err
	}

	return s.toDomain(resp.GetLocation()), nil
}

func (s *LocationsService) BatchNearest(ctx context.Context, coordinates []forecasts.Coordinate, locale language.Tag) ([]*forecasts.Location, error) {
	ctx, span := xotel.Tracer.Start(ctx, "locations-service:batch-nearest", trace.WithSpanKind(trace.SpanKindClient))
	defer span.End()

	var points []*pbv0.BatchNearestRequest_Point
	var pointsTrace []string
	for i := range coordinates {
		pointsTrace = append(pointsTrace, fmt.Sprintf("%.6f,%.6f", coordinates[i].Lat, coordinates[i].Lon))
		points = append(points, &pbv0.BatchNearestRequest_Point{
			Latitude:  float32(coordinates[i].Lat),
			Longitude: float32(coordinates[i].Lon),
		})
	}
	span.SetAttributes(attribute.StringSlice("coordinates", pointsTrace), attribute.String("locale", locale.String()))

	ctx = metadata.AppendToOutgoingContext(ctx, "accept-language", locale.String(), "request-id", requestid.GetRequestID(ctx))
	resp, err := s.client.BatchNearest(ctx,
