	"strings"
)

type Coordinate string

func (c Coordinate) Parse() (forecasts.Coordinate, error) {
	pieces := strings.Split(string(c), ",")
	if len(pieces) != 2 {
		return forecasts.Coordinate{}, errors.New("invalid value")
	}

	lat, err := strconv.ParseFloat(pieces[0], 64)
	if err != nil {
		return forecasts.Coordinate{}, errors.Wrap(err, "invalid latitude value")
	}

	lon, err := strconv.ParseFloat(pieces[1], 64)
	if err != nil {
		return forecasts.Coordinate{}, errors.Wrap(err, "invalid longitude value")
	}

	return forecasts.Coordinate{
		Lat: lat,
		Lon: lon,
	}, nil
}

type Handler struct {
	forecasts forecastsService
}

type forecastsService interface {
	GetForecastsForLocation(context.Context, int, language.Tag) (*forecasts.Forecast, *forecasts.Location, error)
	GetForecastsForLocations(ctx context.Context, ids []int, locale language.Tag) ([]*forecasts.Forecast, []*forecasts.Location, error)
	GetForecastForClosestLocation(ctx context.Context, coordinate forecasts.Coordinate, locale language.Tag) (*forecasts.Forecast, *forecasts.Location, error)
	GetForecastForClosestLocations(ctx context.Context, coordinates []forecasts.Coordinate, locale language.Tag) ([]*forecasts.Forecast, []*forecasts.Location, error)
}

func New(forecasts forecastsService) *Handler {
	return &Handler{forecasts}
}

// Register gin routes
func (h *Handler) Register(g *gin.RouterGroup) {
	g.GET("/v0/single/:id", h.single)
	g.GET("/v0/batch", h.batch)
	g.GET("/v0/nearest/:coordinate", h.nearest)
	g.GET("/v0/nearest/batch", h.nearestBatch)
}

func (h *Handler) toLocationResource(l *forecasts.Location) *Location {
	loc := &Location{
		ID:        l.ID,
		Latitude:  l.Lat,
		Longitude: l.Lon,
		Timezone:  &l.Timezone,
		Name:      l.Name,
	}

	if l.DivisionCode != nil {
		loc.Division = &Division{
			Code: l.DivisionCode,
			Name: l.DivisionName,
		}
	}

	if l.SubdivisionCode != nil {
		loc.Subdivision = &Subdivision{
			Code: l.SubdivisionCode,
			Name: l.SubdivisionName,
		}
	}

	if l.CountryCode != nil {
		loc.Country = Country{
			Code: *l.CountryCode,
			Name: *l.CountryName,
		}
	}

	return loc
}

