func (h *Handler) batch(c *gin.Context) {
	locale := i18n.GetLocaleFromHeader(c.GetHeader("Accept-Language"))

	log.Debug(c, "Request detected locale.", zap.String("locale", locale.String()))

	request := batchLocationsForecastRequest{
		HourlyHours: []string{"*"},
	}

	if err := c.ShouldBind(&request); err != nil {
		log.Debug(c, "Validation errors.", zap.Any("request", request), zap.Error(err))

		message := i18n.GetFirstValidationErrorMessage(err)

		c.PureJSON(
			http.StatusBadRequest,
			httpresponses.NewErrorResponse[*Response]("invalid_parameters", message),
		)
		return
	}

	if len(request.CurrentFields) == 0 && len(request.DailyFields) == 0 && len(request.HourlyFields) == 0 {
		log.Debug(c, "current[fields], daily[fields], and hourly[fields] cannot be null at the same time.", zap.Any("request", request))
		c.JSON(
			http.StatusBadRequest,
			httpresponses.NewErrorResponse[[]*Response]("invalid_parameters", "At least one of current[fields], daily[fields], or hourly[fields] must be present."),
		)

		return
	}

	fcs, locations, err := h.forecasts.GetForecastsForLocations(c.Request.Context(), request.IDs, locale)

	if err != nil {
		log.Error(c, "Something went wrong while retrieving the forecast for locations.", zap.Any("request", request), zap.Error(err))
