import (
	"github.com/spf13/cobra"
	"time"
)
func New() *cobra.Command {
	cmd := &cobra.Command{
		Use:   "serve",
		Short: "Serve",
	}

	cmd.AddCommand(newServeGrpcCommand())

	cmd.PersistentFlags().String("otel.tracer.endpoint", "localhost:4317", "The URL of the tracing collector.")
	cmd.PersistentFlags().String("otel.meter.endpoint", "localhost:4317", "The URL of the tracing collector.")
	cmd.PersistentFlags().Duration("otel.meter.interval", 60*time.Second, "The interval which the metrics will be reported to the collector.")

	return cmd
}

